import React, { useState, useEffect } from 'react';
import { Link, useSearchParams } from 'react-router-dom';
import { 
  Box, Typography, Button, Grid, Paper, Tabs, Tab, Snackbar, Alert, 
  List, ListItem, ListItemIcon, ListItemText, LinearProgress,
  FormControl, InputLabel, Select, MenuItem, Chip, Divider
} from '@mui/material';
import { 
  PlayArrow, Save, ArrowBack, LightbulbOutlined, BarChart, Check, 
  MenuBook, Assignment, Code, Terminal, Assessment, Info,
  BugReport, Download, Upload, ContentCopy, ContentPaste
} from '@mui/icons-material';

const CodeEditor = () => {
  const [searchParams, setSearchParams] = useSearchParams();
  const [selectedModule, setSelectedModule] = useState(searchParams.get('module') || 'full_adder');
  const [code, setCode] = useState('');
  const [output, setOutput] = useState('');
  const [consoleOutput, setConsoleOutput] = useState('');
  const [isCompiling, setIsCompiling] = useState(false);
  const [tabValue, setTabValue] = useState(0);
  const [notification, setNotification] = useState({ open: false, message: '', severity: 'info' });
  const [exerciseData, setExerciseData] = useState(null);

  // Available practice modules
  const practiceModules = [
    { id: 'full_adder', name: 'Full Adder', level: 'Beginner', category: 'Combinational' },
    { id: 'ripple_carry_adder', name: '4-bit Ripple Carry Adder', level: 'Beginner', category: 'Combinational' },
    { id: 'alu', name: '4-bit ALU', level: 'Intermediate', category: 'Combinational' },
    { id: 'counter', name: '4-bit Counter', level: 'Beginner', category: 'Sequential' },
    { id: 'shift_register', name: '8-bit Shift Register', level: 'Intermediate', category: 'Sequential' },
    { id: 'fsm_traffic', name: 'Traffic Light Controller FSM', level: 'Intermediate', category: 'Sequential' },
    { id: 'uart_tx', name: 'UART Transmitter', level: 'Advanced', category: 'Communication' },
    { id: 'memory_controller', name: 'Simple Memory Controller', level: 'Advanced', category: 'Memory' },
    { id: 'fifo', name: 'Synchronous FIFO', level: 'Advanced', category: 'Memory' },
  ];

  // Handle module selection change
  const handleModuleChange = (event) => {
    const moduleId = event.target.value;
    setSelectedModule(moduleId);
    setSearchParams({ module: moduleId });
    // Reset output
    setOutput('');
    setConsoleOutput('');
    setTabValue(0);
  };

  // Fetch module data when selection changes
  useEffect(() => {
    // In a real application, this would be an API call
    // For now, we'll simulate fetching data for different modules
    const fetchModuleData = async () => {
      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 300));
      
      let moduleTemplate;
      
      switch(selectedModule) {
        case 'full_adder':
          moduleTemplate = `module full_adder(
  input wire a, b, cin,
  output wire sum, cout
);
  // TODO: Implement full adder logic
  // sum = a XOR b XOR cin
  // cout = (a AND b) OR (cin AND (a XOR b))
  
  // Your code here:
  
endmodule

// Testbench (uncomment to simulate)
/*
module full_adder_tb;
  // Testbench signals
  reg a, b, cin;
  wire sum, cout;
  
  // Instantiate the full_adder
  full_adder dut(
    .a(a),
    .b(b),
    .cin(cin),
    .sum(sum),
    .cout(cout)
  );
  
  // Test stimulus
  initial begin
    // Display header
    $display("a b cin | sum cout");
    $display("--------+---------");
    
    // Test all input combinations
    {a, b, cin} = 3'b000; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    {a, b, cin} = 3'b001; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    {a, b, cin} = 3'b010; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    {a, b, cin} = 3'b011; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    {a, b, cin} = 3'b100; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    {a, b, cin} = 3'b101; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    {a, b, cin} = 3'b110; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    {a, b, cin} = 3'b111; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    $finish;
  end
endmodule
*/`;
          setExerciseData({
            id: 'full_adder',
            title: 'Implementing a Full Adder',
            description: 'Create a Verilog module for a full adder with inputs a, b, cin and outputs sum and cout. A full adder adds three bits and produces a sum bit and a carry bit.',
            objectives: [
              'Understand the operation of a full adder circuit',
              'Implement combinational logic using Verilog assign statements',
              'Utilize internal wires for intermediate signals',
              'Apply proper port declarations with the wire data type'
            ],
            background: 'A full adder is a fundamental building block in digital circuit design, used to add two binary digits along with a carry input. It produces a sum output and a carry output, which can be cascaded to create multi-bit adders.',
            testCases: [
              { inputs: 'a=0, b=0, cin=0', expectedOutputs: 'sum=0, cout=0', explanation: 'With all inputs 0, both sum and carry are 0' },
              { inputs: 'a=1, b=0, cin=0', expectedOutputs: 'sum=1, cout=0', explanation: 'Adding just a single 1 gives sum=1, with no carry' },
              { inputs: 'a=0, b=1, cin=0', expectedOutputs: 'sum=1, cout=0', explanation: 'Adding just a single 1 gives sum=1, with no carry' },
              { inputs: 'a=0, b=0, cin=1', expectedOutputs: 'sum=1, cout=0', explanation: 'Adding just a single 1 gives sum=1, with no carry' },
              { inputs: 'a=1, b=1, cin=0', expectedOutputs: 'sum=0, cout=1', explanation: 'Adding two 1s results in 0 with a carry of 1 (binary 10)' },
              { inputs: 'a=1, b=0, cin=1', expectedOutputs: 'sum=0, cout=1', explanation: 'Adding two 1s results in 0 with a carry of 1 (binary 10)' },
              { inputs: 'a=0, b=1, cin=1', expectedOutputs: 'sum=0, cout=1', explanation: 'Adding two 1s results in 0 with a carry of 1 (binary 10)' },
              { inputs: 'a=1, b=1, cin=1', expectedOutputs: 'sum=1, cout=1', explanation: 'Adding three 1s results in 1 with a carry of 1 (binary 11)' }
            ],
            hints: [
              'Remember the boolean equations for a full adder: sum = a âŠ• b âŠ• cin and cout = (aÂ·b) + (cinÂ·(aâŠ•b))',
              'Use intermediate wires to make your code more readable',
              'The XOR operator in Verilog is represented by the caret symbol (^)',
              'Use the & operator for AND and | operator for OR operations'
            ],
            truthTable: [
              { a: 0, b: 0, cin: 0, sum: 0, cout: 0 },
              { a: 0, b: 0, cin: 1, sum: 1, cout: 0 },
              { a: 0, b: 1, cin: 0, sum: 1, cout: 0 },
              { a: 0, b: 1, cin: 1, sum: 0, cout: 1 },
              { a: 1, b: 0, cin: 0, sum: 1, cout: 0 },
              { a: 1, b: 0, cin: 1, sum: 0, cout: 1 },
              { a: 1, b: 1, cin: 0, sum: 0, cout: 1 },
              { a: 1, b: 1, cin: 1, sum: 1, cout: 1 }
            ],
            imageUrl: 'https://www.researchgate.net/publication/329466923/figure/fig12/AS:701497183633415@1544299999838/A-full-adder-circuit-and-its-truth-table.png'
          });
          break;
          
        case 'ripple_carry_adder':
          moduleTemplate = `module ripple_carry_adder(
  input [3:0] a, b,
  input cin,
  output [3:0] sum,
  output cout
);
  // TODO: Implement a 4-bit ripple carry adder
  // Hint: You can use full adders connected in series
  
  // Your code here:
  
endmodule

// Optional: Define a full adder module below
// module full_adder(...);
// ...
// endmodule`;
          
          setExerciseData({
            id: 'ripple_carry_adder',
            title: 'Implementing a 4-bit Ripple Carry Adder',
            description: 'Create a Verilog module for a 4-bit ripple carry adder that adds two 4-bit numbers and produces a 4-bit sum and a carry out.',
            objectives: [
              'Understand multi-bit adder design',
              'Learn to reuse components in digital design',
              'Practice connecting modules together',
              'Implement a cascaded design pattern'
            ],
            background: 'A ripple carry adder combines multiple full adders to add multi-bit numbers. The carry bit "ripples" from one adder to the next, which is simple to implement but can introduce propagation delays.',
            testCases: [
              { inputs: 'a=0000, b=0000, cin=0', expectedOutputs: 'sum=0000, cout=0', explanation: 'Adding zeros gives zero' },
              { inputs: 'a=1010, b=0101, cin=0', expectedOutputs: 'sum=1111, cout=0', explanation: '10 + 5 = 15 (no carry out)' },
              { inputs: 'a=1111, b=0001, cin=0', expectedOutputs: 'sum=0000, cout=1', explanation: '15 + 1 = 16 (rolls over with carry)' },
              { inputs: 'a=1111, b=1111, cin=0', expectedOutputs: 'sum=1110, cout=1', explanation: '15 + 15 = 30 (exceeds 4 bits)' }
            ],
            hints: [
              'Start by designing or reusing a full adder module',
              'Connect four full adders, with the carry output of each going to the carry input of the next',
              'The first full adder takes the cin input, and the last full adder produces the cout output',
              'Remember to use proper bit indices when connecting multi-bit signals'
            ],
            imageUrl: 'https://www.researchgate.net/publication/265755789/figure/fig1/AS:668550417743875@1536402642245/4-bit-Ripple-Carry-Adder.png'
          });
          break;
          
        case 'alu':
          moduleTemplate = `module alu_4bit(
  input [3:0] a, b,
  input [1:0] op,
  output reg [3:0] result,
  output zero
);
  // TODO: Implement a 4-bit ALU with the following operations:
  // op = 00: ADD (a + b)
  // op = 01: SUB (a - b)
  // op = 10: AND (a & b)
  // op = 11: OR (a | b)
  // zero flag should be set when result is 0
  
  // Your code here:
  
endmodule`;
          
          setExerciseData({
            id: 'alu',
            title: 'Designing a 4-bit ALU',
            description: 'Create a 4-bit Arithmetic Logic Unit (ALU) that can perform addition, subtraction, AND, and OR operations.',
            objectives: [
              'Design a multi-functional digital component',
              'Implement operation selection using control signals',
              'Practice using conditional (case) statements in Verilog',
              'Understand zero flag generation'
            ],
            background: 'An ALU is a fundamental digital circuit that performs arithmetic and logical operations. ALUs are a critical component in CPU design, forming the heart of the execution unit.',
            testCases: [
              { inputs: 'a=0101, b=0011, op=00', expectedOutputs: 'result=1000, zero=0', explanation: 'ADD: 5 + 3 = 8' },
              { inputs: 'a=1000, b=0011, op=01', expectedOutputs: 'result=0101, zero=0', explanation: 'SUB: 8 - 3 = 5' },
              { inputs: 'a=1001, b=0011, op=10', expectedOutputs: 'result=0001, zero=0', explanation: 'AND: 1001 & 0011 = 0001' },
              { inputs: 'a=1000, b=0011, op=11', expectedOutputs: 'result=1011, zero=0', explanation: 'OR: 1000 | 0011 = 1011' },
              { inputs: 'a=0011, b=0011, op=01', expectedOutputs: 'result=0000, zero=1', explanation: 'SUB: 3 - 3 = 0 (zero flag set)' }
            ],
            hints: [
              'Use a case statement to implement different operations based on the op input',
              'For subtraction, remember that a - b can be implemented as a + (complement of b) + 1',
              'The zero flag is set when all bits of the result are 0',
              'Use the always block with blocking assignments for combinational logic'
            ],
            imageUrl: 'https://www.allaboutcircuits.com/uploads/articles/an-introduction-to-arithmetic-logic-units-alus-fig2.jpg'
          });
          break;
          
        case 'fsm_traffic':
          moduleTemplate = `module traffic_light_controller(
  input clk,
  input reset,
  input enable,
  output reg [2:0] highway_lights, // R, Y, G
  output reg [2:0] farm_road_lights // R, Y, G
);
  // TODO: Implement a traffic light controller for a highway and farm road intersection
  // The highway normally stays green, while the farm road stays red
  // When enable is high, start the sequence to allow farm road traffic
  // Proper yellow light timing should be included
  // Hint: Define state parameters, create a state register, and use a state machine approach
  
  // Your code here:
  
endmodule`;
          
          setExerciseData({
            id: 'fsm_traffic',
            title: 'Traffic Light Controller FSM',
            description: 'Design a traffic light controller for an intersection between a highway and a farm road using a finite state machine approach.',
            objectives: [
              'Implement a finite state machine (FSM) in Verilog',
              'Practice sequential circuit design',
              'Learn state encoding and transitions',
              'Understand timing sequences in digital control systems'
            ],
            background: 'Traffic light controllers are classical examples of finite state machines. They need to maintain the current state, handle transitions based on inputs, and control outputs according to the current state.',
            testCases: [
              { inputs: 'enable=0 (steady state)', expectedOutputs: 'highway=GREEN, farm=RED', explanation: 'Normal operation with highway traffic flowing' },
              { inputs: 'enable transitions to 1', expectedOutputs: 'Sequence starts: highwayâ†’YELLOWâ†’RED, farmâ†’REDâ†’GREEN', explanation: 'Transition sequence initiated' },
              { inputs: 'After timeout with enable=0', expectedOutputs: 'Sequence ends: highwayâ†’GREEN, farmâ†’RED', explanation: 'Return to normal operation' }
            ],
            hints: [
              'First, define your states using parameters (e.g., HWY_GREEN, HWY_YELLOW, etc.)',
              'Use a state register to keep track of the current state',
              'Implement two always blocks: one for state transitions and one for output logic',
              'Don\'t forget to handle the reset condition to initialize the FSM',
              'Consider adding counters for timing the duration of each state'
            ],
            imageUrl: 'https://www.researchgate.net/publication/261209431/figure/fig2/AS:296969038467074@1447808916417/Basic-traffic-light-controller-state-diagram.png'
          });
          break;
          
        default:
          moduleTemplate = '// Select a module from the dropdown to begin';
          setExerciseData(null);
      }
      
      setCode(moduleTemplate);
    };

    fetchModuleData();
  }, [selectedModule]);

  const handleTabChange = (_, newValue) => setTabValue(newValue);

  const handleRunCode = () => {
    setIsCompiling(true);
    setOutput('');
    setConsoleOutput('');
    
    // Validate code has the basic structure before attempting to "compile"
    const errors = verifySyntax(code);
    if (errors.length > 0) {
      setTimeout(() => {
        setConsoleOutput(`Syntax Verification Failed:\n\n${errors.map(err => `- ${err}`).join('\n')}\n\nPlease fix these issues before running the code.`);
        setOutput(`\x1b[1;31mCompilation Error:\x1b[0m\nYour code has syntax issues that need to be fixed.\nCheck the Console tab for details.`);
        setNotification({
          open: true,
          message: 'Compilation failed. Please check your code for syntax issues.',
          severity: 'error'
        });
        setIsCompiling(false);
      }, 800);
      return;
    }
    
    // Simulate compilation and running with module-specific validation
    setTimeout(() => {
      let simulationPassed = false;
      
      // Module-specific validation
      if (selectedModule === 'full_adder') {
        if (code.includes('assign sum') && code.includes('assign cout')) {
          // Check for common mistakes in full adder logic
          if (
            (code.includes('a ^ b ^ cin') || code.includes('a_xor_b ^ cin')) && 
            (
              (code.includes('a & b') && code.includes('cin &') && code.includes('|')) ||
              (code.includes('(a & b)') && code.includes('(cin &'))
            )
          ) {
            simulationPassed = true;
          } else {
            setConsoleOutput(`Warning: Your full adder implementation may not be correct.\n\nExpected logic:\n- sum = a ^ b ^ cin\n- cout = (a & b) | (cin & (a ^ b))\n\nCheck your logic equations and try again.`);
          }
        }
      } else if (selectedModule === 'ripple_carry_adder') {
        if (code.includes('full_adder') && code.includes('[3:0] sum') && code.includes('cout')) {
          // Check if the connections between adders are properly done
          if (code.includes('wire') && (code.includes('carry') || code.includes('c1') || code.includes('c2'))) {
            simulationPassed = true;
          } else {
            setConsoleOutput(`Warning: Your ripple carry adder implementation may not be correct.\n\nMake sure you've:\n- Created internal wires for carries between adders\n- Connected the full adders correctly in a chain\n- Properly handled the 4-bit inputs and outputs`);
          }
        }
      } else if (selectedModule === 'alu') {
        if (code.includes('case') && code.includes('2\'b00') && code.includes('assign zero')) {
          simulationPassed = true;
        }
