          severity: 'warning'
        });
      }
      
      setIsCompiling(false);
    }, 1500);
  };

  const handleSaveCode = () => {
    setNotification({
      open: true,
      message: 'Code saved successfully!',
      severity: 'success'
    });
  };

  const handleCloseNotification = () => {
    setNotification({ ...notification, open: false });
  };

  const handleShowHint = () => {
    setTabValue(2); // Switch to the Hints tab
  };
  
  const handleCopyCode = () => {
    navigator.clipboard.writeText(code);
    setNotification({
      open: true,
      message: 'Code copied to clipboard!',
      severity: 'info'
    });
  };
  
  const handleDownloadCode = () => {
    const blob = new Blob([code], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${selectedModule}.v`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };
  
  // More robust syntax verification
  const verifySyntax = (code) => {
    const errors = [];
    
    // Check for basic syntax elements
    if (!code.includes('module')) errors.push('Missing module declaration');
    if (!code.includes('endmodule')) errors.push('Missing endmodule statement');
    
    // Module-specific checks
    if (selectedModule === 'full_adder') {
      if (!code.includes('input') || !code.includes('a') || !code.includes('b') || !code.includes('cin')) 
        errors.push('Missing input port declarations (a, b, cin)');
      if (!code.includes('output') || !code.includes('sum') || !code.includes('cout'))
        errors.push('Missing output port declarations (sum, cout)');
      if (!code.includes('assign sum') && !code.includes('assign sum ='))
        errors.push('Missing sum output assignment');
      if (!code.includes('assign cout') && !code.includes('assign cout ='))
        errors.push('Missing cout output assignment');
    } else if (selectedModule === 'ripple_carry_adder') {
      if (!code.includes('input') || !code.includes('[3:0] a') || !code.includes('[3:0] b'))
        errors.push('Missing 4-bit input port declarations');
      if (!code.includes('output') || !code.includes('[3:0] sum') || !code.includes('cout'))
        errors.push('Missing output port declarations');
    }
    
    return errors;
  };

  return (
    <Box className="container page-container">
      {/* Page Header */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 4, flexWrap: 'wrap', gap: 2 }}>
        <Box>
          <Button 
            component={Link} 
            to="/modules" 
            startIcon={<ArrowBack />} 
            sx={{ mb: 2 }}
          >
            Back to Modules
          </Button>
          <Typography variant="h4" component="h1" sx={{ fontWeight: 'bold', color: 'primary.main' }}>
            VeriGeek Code Editor
          </Typography>
          <Typography variant="body1" color="text.secondary">
            Write, test, and simulate Verilog HDL code in your browser
          </Typography>
        </Box>
        
        {/* Module Selector */}
        <Box sx={{ minWidth: 300 }}>
          <FormControl fullWidth variant="outlined" size="small">
            <InputLabel id="module-selector-label">Practice Module</InputLabel>
            <Select
              labelId="module-selector-label"
              id="module-selector"
              value={selectedModule}
              onChange={handleModuleChange}
              label="Practice Module"
            >
              {practiceModules.map(module => (
                <MenuItem key={module.id} value={module.id}>
                  <Box sx={{ display: 'flex', alignItems: 'center', width: '100%', justifyContent: 'space-between' }}>
                    <Typography>{module.name}</Typography>
                    <Box>
                      <Chip
                        label={module.level}
                        size="small"
                        sx={{ 
                          ml: 1, 
                          bgcolor: module.level === 'Beginner' ? '#e3f2fd' : 
                                  module.level === 'Intermediate' ? '#fff8e1' : '#fbe9e7',
                          color: module.level === 'Beginner' ? '#0277bd' : 
                                module.level === 'Intermediate' ? '#ff8f00' : '#e64a19',
                          fontSize: '0.75rem'
                        }}
                      />
                    </Box>
                  </Box>
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </Box>
      </Box>
      
      {/* Exercise Info */}
      {exerciseData && (
        <Paper elevation={2} sx={{ p: 3, mb: 4, borderRadius: 2 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 2 }}>
            <Box>
              <Typography variant="h5" gutterBottom sx={{ fontWeight: 'bold', color: 'primary.main' }}>
                {exerciseData.title}
              </Typography>
              <Typography paragraph sx={{ maxWidth: '800px' }}>
                {exerciseData.description}
              </Typography>
            </Box>
            <Box sx={{ display: 'flex', gap: 1 }}>
              <Chip 
                icon={<Info fontSize="small" />} 
                label={`ID: ${exerciseData.id}`} 
                variant="outlined" 
                size="small" 
              />
            </Box>
          </Box>
          
          {/* Learning Objectives */}
          <Typography variant="subtitle1" gutterBottom sx={{ fontWeight: 'bold', mt: 3 }}>
            Learning Objectives:
          </Typography>
          <List dense disablePadding sx={{ mb: 2 }}>
            {exerciseData.objectives.map((objective, index) => (
              <ListItem key={index} sx={{ py: 0.5 }}>
                <ListItemIcon sx={{ minWidth: 36 }}>
                  <Check color="primary" />
                </ListItemIcon>
                <ListItemText primary={objective} />
              </ListItem>
            ))}
          </List>
        </Paper>
      )}
      
      {/* Exercise Background */}
      {exerciseData && (
        <Grid container spacing={3} sx={{ mb: 4 }}>
          <Grid item xs={12} md={6}>
            <Paper elevation={0} variant="outlined" sx={{ p: 3, height: '100%', borderRadius: 2 }}>
              <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center' }}>
                <MenuBook color="primary" sx={{ mr: 1 }} /> Background
              </Typography>
              <Typography paragraph>{exerciseData.background}</Typography>
              
              {/* Truth Table if available */}
              {exerciseData.truthTable && (
                <>
                  <Typography variant="subtitle1" sx={{ mt: 2, mb: 1, fontWeight: 'bold' }}>
                    Truth Table:
                  </Typography>
                  <Box sx={{ overflowX: 'auto' }}>
                    <table style={{ 
                      width: '100%', 
                      borderCollapse: 'collapse', 
                      textAlign: 'center',
                      fontSize: '0.9rem',
                      border: '1px solid #e0e0e0'
                    }}>
                      <thead>
                        <tr style={{ backgroundColor: '#f5f5f5' }}>
                          {Object.keys(exerciseData.truthTable[0]).map(key => (
                            <th key={key} style={{ padding: '8px', border: '1px solid #e0e0e0' }}>
                              {key.toUpperCase()}
                            </th>
                          ))}
                        </tr>
                      </thead>
                      <tbody>
                        {exerciseData.truthTable.map((row, index) => (
                          <tr key={index} style={{ backgroundColor: index % 2 === 0 ? 'white' : '#fafafa' }}>
                            {Object.values(row).map((value, idx) => (
                              <td key={idx} style={{ padding: '8px', border: '1px solid #e0e0e0' }}>
                                {value}
                              </td>
                            ))}
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </Box>
                </>
              )}
            </Paper>
          </Grid>
          <Grid item xs={12} md={6}>
            <Paper elevation={0} variant="outlined" sx={{ p: 3, height: '100%', borderRadius: 2 }}>
              <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center' }}>
                <Assignment color="primary" sx={{ mr: 1 }} /> Implementation Guide
              </Typography>
              
              {exerciseData.imageUrl && (
                <Box
                  component="img"
                  src={exerciseData.imageUrl}
                  alt={`${exerciseData.title} diagram`}
                  sx={{
                    width: '100%',
                    maxHeight: '200px',
                    objectFit: 'contain',
                    mb: 2,
                    borderRadius: 1,
                    border: '1px solid #e0e0e0'
                  }}
                />
              )}
              
              <Typography variant="subtitle1" gutterBottom sx={{ fontWeight: 'bold' }}>
                Implementation Steps:
              </Typography>
              
              <List dense>
                {selectedModule === 'full_adder' && (
                  <>
                    <ListItem>
                      <ListItemIcon><Check color="primary" fontSize="small" /></ListItemIcon>
                      <ListItemText primary="1. Define an intermediate wire for a XOR b" />
                    </ListItem>
                    <ListItem>
                      <ListItemIcon><Check color="primary" fontSize="small" /></ListItemIcon>
                      <ListItemText primary="2. Calculate the sum as (a XOR b) XOR cin" />
                    </ListItem>
                    <ListItem>
                      <ListItemIcon><Check color="primary" fontSize="small" /></ListItemIcon>
                      <ListItemText primary="3. Calculate the carry out as (a AND b) OR (cin AND (a XOR b))" />
                    </ListItem>
                  </>
                )}
                
                {selectedModule === 'ripple_carry_adder' && (
                  <>
                    <ListItem>
                      <ListItemIcon><Check color="primary" fontSize="small" /></ListItemIcon>
                      <ListItemText primary="1. Create a full adder module or instantiate it directly" />
                    </ListItem>
                    <ListItem>
                      <ListItemIcon><Check color="primary" fontSize="small" /></ListItemIcon>
                      <ListItemText primary="2. Create internal wires for the carry signals between adders" />
                    </ListItem>
                    <ListItem>
                      <ListItemIcon><Check color="primary" fontSize="small" /></ListItemIcon>
                      <ListItemText primary="3. Connect the carry out of each adder to the carry in of the next" />
                    </ListItem>
                    <ListItem>
                      <ListItemIcon><Check color="primary" fontSize="small" /></ListItemIcon>
                      <ListItemText primary="4. Properly connect the input bits to each full adder" />
                    </ListItem>
                  </>
                )}
                
                {selectedModule === 'alu' && (
                  <>
                    <ListItem>
                      <ListItemIcon><Check color="primary" fontSize="small" /></ListItemIcon>
                      <ListItemText primary="1. Create an always block for combinational logic" />
                    </ListItem>
                    <ListItem>
                      <ListItemIcon><Check color="primary" fontSize="small" /></ListItemIcon>
                      <ListItemText primary="2. Use a case statement to handle the different operations" />
                    </ListItem>
                    <ListItem>
                      <ListItemIcon><Check color="primary" fontSize="small" /></ListItemIcon>
                      <ListItemText primary="3. Implement each operation (ADD, SUB, AND, OR)" />
                    </ListItem>
                    <ListItem>
                      <ListItemIcon><Check color="primary" fontSize="small" /></ListItemIcon>
                      <ListItemText primary="4. Create a separate assignment for the zero flag" />
                    </ListItem>
                  </>
                )}
                
                {selectedModule === 'fsm_traffic' && (
                  <>
                    <ListItem>
                      <ListItemIcon><Check color="primary" fontSize="small" /></ListItemIcon>
                      <ListItemText primary="1. Define state parameters and a state register" />
                    </ListItem>
                    <ListItem>
                      <ListItemIcon><Check color="primary" fontSize="small" /></ListItemIcon>
                      <ListItemText primary="2. Create an always block for state transitions" />
                    </ListItem>
                    <ListItem>
                      <ListItemIcon><Check color="primary" fontSize="small" /></ListItemIcon>
                      <ListItemText primary="3. Create another always block for output logic" />
                    </ListItem>
                    <ListItem>
                      <ListItemIcon><Check color="primary" fontSize="small" /></ListItemIcon>
                      <ListItemText primary="4. Handle the reset condition to initialize the FSM" />
                    </ListItem>
                  </>
                )}
              </List>
            </Paper>
          </Grid>
        </Grid>
      )}
      
      {/* Test Cases */}
      {exerciseData && exerciseData.testCases && (
        <Paper elevation={2} sx={{ p: 3, mb: 4, borderRadius: 2 }}>
          <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center' }}>
            <Assignment color="primary" sx={{ mr: 1 }} /> Test Cases
          </Typography>
          <Typography variant="body2" sx={{ mb: 2 }}>
            Your implementation will be validated against these test cases. Make sure your code handles all these scenarios correctly.
          </Typography>
          <Box sx={{ overflowX: 'auto' }}>
            <table style={{ 
              width: '100%', 
              borderCollapse: 'collapse', 
              textAlign: 'left',
              fontSize: '0.9rem',
              border: '1px solid #e0e0e0'
            }}>
              <thead>
                <tr style={{ backgroundColor: '#f5f5f5' }}>
                  <th style={{ padding: '10px', border: '1px solid #e0e0e0' }}>Test Case</th>
                  <th style={{ padding: '10px', border: '1px solid #e0e0e0' }}>Inputs</th>
                  <th style={{ padding: '10px', border: '1px solid #e0e0e0' }}>Expected Outputs</th>
                  <th style={{ padding: '10px', border: '1px solid #e0e0e0' }}>Explanation</th>
                </tr>
              </thead>
              <tbody>
                {exerciseData.testCases.map((testCase, index) => (
                  <tr key={index} style={{ backgroundColor: index % 2 === 0 ? 'white' : '#fafafa' }}>
                    <td style={{ padding: '10px', border: '1px solid #e0e0e0', fontWeight: 'bold' }}>Case {index + 1}</td>
                    <td style={{ padding: '10px', border: '1px solid #e0e0e0', fontFamily: 'monospace' }}>{testCase.inputs}</td>
                    <td style={{ padding: '10px', border: '1px solid #e0e0e0', fontFamily: 'monospace' }}>{testCase.expectedOutputs}</td>
                    <td style={{ padding: '10px', border: '1px solid #e0e0e0' }}>{testCase.explanation}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </Box>
        </Paper>
      )}
      
      {/* Editor and Output */}
      <Grid container spacing={3}>
        {/* Editor Section */}
        <Grid item xs={12} md={7}>
          <Paper elevation={3} sx={{ height: '100%', display: 'flex', flexDirection: 'column', borderRadius: 2 }}>
            <Box sx={{ p: 2, borderBottom: 1, borderColor: 'divider', bgcolor: '#f5f5f5', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Typography variant="h6" sx={{ display: 'flex', alignItems: 'center' }}>
                <Code sx={{ mr: 1 }} />
                Verilog Editor
              </Typography>
              <Box sx={{ display: 'flex', gap: 1 }}>
                <Button 
                  size="small" 
                  startIcon={<ContentCopy />} 
                  onClick={handleCopyCode}
                  sx={{ textTransform: 'none' }}
                >
                  Copy
                </Button>
                <Button 
                  size="small" 
                  startIcon={<Download />} 
                  onClick={handleDownloadCode}
                  sx={{ textTransform: 'none' }}
                >
                  Download
                </Button>
              </Box>
            </Box>
            <Box sx={{ flexGrow: 1, p: 0, bgcolor: '#272822', minHeight: '500px' }}>
              <textarea
                value={code}
                onChange={(e) => setCode(e.target.value)}
                style={{
                  width: '100%',
                  height: '100%',
                  minHeight: '500px',
                  fontFamily: 'Consolas, Monaco, "Andale Mono", "Ubuntu Mono", monospace',
                  fontSize: '14px',
                  padding: '16px',
                  border: 'none',
                  borderRadius: 0,
                  resize: 'none',
                  backgroundColor: '#272822',
                  color: '#f8f8f2',
                  lineHeight: '1.5',
                  outline: 'none'
                }}
                spellCheck="false"
                placeholder="// Write your Verilog code here"
              />
            </Box>
            <Box sx={{ p: 2, borderTop: 1, borderColor: 'divider', display: 'flex', gap: 1, bgcolor: '#f5f5f5' }}>
              <Button 
                variant="contained" 
                color="primary" 
                startIcon={<PlayArrow />}
                onClick={handleRunCode}
                disabled={isCompiling}
                size="large"
              >
                {isCompiling ? 'Running...' : 'Run Code'}
              </Button>
              <Button 
                variant="outlined" 
                startIcon={<Save />}
                onClick={handleSaveCode}
                size="large"
              >
                Save
              </Button>
              <Button 
                variant="outlined" 
                color="secondary"
                startIcon={<LightbulbOutlined />}
                onClick={handleShowHint}
                sx={{ ml: 'auto' }}
                size="large"
              >
                Hint
              </Button>
            </Box>
          </Paper>
        </Grid>
        
        {/* Output Section */}
        <Grid item xs={12} md={5}>
          <Paper elevation={3} sx={{ display: 'flex', flexDirection: 'column', height: '100%', borderRadius: 2 }}>
            <Tabs 
              value={tabValue} 
              onChange={handleTabChange}
              sx={{ 
                borderBottom: 1, 
                borderColor: 'divider',
                bgcolor: '#f5f5f5'
              }}
            >
              <Tab label="Simulation" icon={<Assessment fontSize="small" />} iconPosition="start" />
              <Tab label="Console" icon={<Terminal fontSize="small" />} iconPosition="start" />
              <Tab label="Hints" icon={<LightbulbOutlined fontSize="small" />} iconPosition="start" />
              <Tab label="Debug" icon={<BugReport fontSize="small" />} iconPosition="start" />
            </Tabs>
            
            {/* Simulation Output Tab */}
            {tabValue === 0 && (
              <Box 
                sx={{ 
                  flexGrow: 1, 
                  p: 2, 
                  fontFamily: 'Consolas, Monaco, monospace', 
                  fontSize: '14px',
                  bgcolor: '#2d2d2d',
                  color: '#e6e6e6',
                  overflowY: 'auto',
                  whiteSpace: 'pre-wrap',
                  minHeight: '500px'
                }}
              >
                {isCompiling ? (
                  <Box sx={{ p: 2 }}>
                    <LinearProgress color="primary" sx={{ mb: 2 }} />
                    <Typography variant="body2" sx={{ color: '#a6e22e' }}>
                      Compiling and running your code...
                    </Typography>
                  </Box>
                ) : (
                  output ? (
                    <Typography component="pre" sx={{ margin: 0, fontFamily: 'inherit', fontSize: 'inherit' }}>
                      {output}
                    </Typography>
                  ) : (
                    <Typography variant="body2" sx={{ color: '#75715e', fontStyle: 'italic' }}>
                      // Run your code to see the simulation results here.
                      // The simulator will check your implementation against the test cases.
                    </Typography>
                  )
                )}
              </Box>
            )}
            
            {/* Console Tab */}
            {tabValue === 1 && (
              <Box 
                sx={{ 
                  flexGrow: 1, 
                  p: 2, 
                  fontFamily: 'Consolas, Monaco, monospace', 
                  fontSize: '14px',
                  bgcolor: '#2d2d2d',
                  color: '#e6e6e6',
                  overflowY: 'auto',
                  whiteSpace: 'pre-wrap',
                  minHeight: '500px'
                }}
              >
                {consoleOutput ? (
                  <Typography component="pre" sx={{ margin: 0, fontFamily: 'inherit', fontSize: 'inherit' }}>
                    {consoleOutput}
                  </Typography>
                ) : (
                  <Typography variant="body2" sx={{ color: '#75715e', fontStyle: 'italic' }}>
                    // Verilog compiler messages will appear here.
                    // This includes warnings and errors from your code.
                  </Typography>
                )}
              </Box>
            )}
            
            {/* Hints Tab */}
            {tabValue === 2 && exerciseData && (
              <Box 
                sx={{ 
                  flexGrow: 1, 
                  p: 3, 
                  bgcolor: '#fffde7',
                  overflowY: 'auto',
                  minHeight: '500px'
                }}
              >
                <Typography variant="h6" gutterBottom>
                  Helpful Hints
                </Typography>
                <Typography variant="body2" paragraph>
                  If you're stuck, these hints may guide you in the right direction without giving away the full solution.
                </Typography>
                <List>
                  {exerciseData.hints.map((hint, index) => (
                    <ListItem key={index} alignItems="flex-start" sx={{ px: 0 }}>
                      <ListItemIcon sx={{ minWidth: 36, mt: 0.5 }}>
                        <LightbulbOutlined color="warning" />
                      </ListItemIcon>
                      <ListItemText 
                        primary={`Hint ${index + 1}`} 
                        secondary={hint}
                        primaryTypographyProps={{ fontWeight: 'bold' }}
                        secondaryTypographyProps={{ variant: 'body1' }}
                      />
                    </ListItem>
                  ))}
                </List>
                
                {selectedModule === 'full_adder' && (
                  <Box sx={{ mt: 3, p: 2, bgcolor: 'rgba(0, 0, 0, 0.03)', borderRadius: 2 }}>
                    <Typography variant="subtitle1" gutterBottom sx={{ fontWeight: 'bold' }}>
                      Full Adder Logic:
                    </Typography>
                    <Typography variant="body2" paragraph>
                      A full adder adds three bits (a, b, and cin) and produces a sum bit and a carry bit.
                    </Typography>
                    <Box 
                      component="img" 
                      src="https://www.researchgate.net/publication/329466923/figure/fig12/AS:701497183633415@1544299999838/A-full-adder-circuit-and-its-truth-table.png" 
                      alt="Full Adder Logic Diagram" 
                      sx={{ width: '100%', borderRadius: 1, mb: 2 }} 
                    />
                    <Typography variant="body2">
                      The boolean equations for a full adder are:
                    </Typography>
                    <Box sx={{ 
                      bgcolor: 'white', 
                      p: 2, 
                      borderRadius: 1, 
                      fontFamily: 'math', 
                      fontSize: '1rem',
                      textAlign: 'center',
                      mt: 1
                    }}>
                      <Box>sum = a âŠ• b âŠ• cin</Box>
                      <Box>cout = (aÂ·b) + cinÂ·(aâŠ•b)</Box>
                    </Box>
                  </Box>
                )}
              </Box>
            )}
            
            {/* Debug Tab */}
            {tabValue === 3 && (
              <Box 
                sx={{ 
                  flexGrow: 1, 
                  p: 3, 
                  overflowY: 'auto',
                  minHeight: '500px'
                }}
              >
                <Typography variant="h6" gutterBottom>
                  Syntax Verification
                </Typography>
                <Typography variant="body2" paragraph>
                  Let's check your code for common syntax issues and potential problems:
                </Typography>
                
                <Button 
                  variant="outlined" 
                  color="primary" 
                  startIcon={<BugReport />}
                  onClick={() => {
                    const errors = verifySyntax(code);
                    if (errors.length === 0) {
                      setConsoleOutput("Syntax verification passed! No obvious issues found.\n\nThis doesn't guarantee your code is functionally correct, but the basic structure looks good.");
                    } else {
                      setConsoleOutput("Syntax verification failed. Issues found:\n\n" + errors.map(err => `- ${err}`).join('\n'));
                    }
                    setTabValue(1); // Switch to console tab
                  }}
                  sx={{ mb: 3 }}
                >
                  Check Syntax
                </Button>
                
                <Divider sx={{ my: 2 }} />
                
                <Typography variant="h6" gutterBottom>
                  Common Verilog Syntax
                </Typography>
                
                <Typography variant="subtitle1" gutterBottom sx={{ fontWeight: 'bold', mt: 2 }}>
                  Module Declaration:
                </Typography>
                <Box sx={{ 
                  bgcolor: '#f5f5f5', 
                  p: 2, 
                  borderRadius: 1, 
                  fontFamily: 'monospace',
                  mb: 2
                }}>
                  <pre style={{ margin: 0 }}>
{`module module_name(
  input wire signal_a,
  input wire [3:0] signal_b,
  output wire out_1,
  output reg [7:0] out_2
);
  // Module body
endmodule`}
                  </pre>
                </Box>
                
                <Typography variant="subtitle1" gutterBottom sx={{ fontWeight: 'bold', mt: 2 }}>
                  Always Block (Combinational):
                </Typography>
                <Box sx={{ 
                  bgcolor: '#f5f5f5', 
                  p: 2, 
                  borderRadius: 1, 
                  fontFamily: 'monospace',
                  mb: 2
                }}>
                  <pre style={{ margin: 0 }}>
{`always @(*) begin
  // Code executes when any input changes
  result = a + b;
end`}
                  </pre>
                </Box>
                
                <Typography variant="subtitle1" gutterBottom sx={{ fontWeight: 'bold', mt: 2 }}>
                  Always Block (Sequential):
                </Typography>
                <Box sx={{ 
                  bgcolor: '#f5f5f5', 
                  p: 2, 
                  borderRadius: 1, 
                  fontFamily: 'monospace',
                  mb: 2
                }}>
                  <pre style={{ margin: 0 }}>
{`always @(posedge clk or posedge reset) begin
  if (reset)
    counter <= 0;
  else
    counter <= counter + 1;
end`}
                  </pre>
                </Box>
              </Box>
            )}
          </Paper>
        </Grid>
      </Grid>

      {/* Notification */}
      <Snackbar 
        open={notification.open} 
        autoHideDuration={6000} 
        onClose={handleCloseNotification}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
      >
        <Alert 
          onClose={handleCloseNotification} 
          severity={notification.severity} 
          sx={{ width: '100%' }}
        >
          {notification.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default CodeEditor;
