import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { 
  Box, Typography, Button, Grid, Card, CardContent, Breadcrumbs, 
  Tabs, Tab, List, ListItem, ListItemIcon, ListItemText, Divider, 
  Chip, Rating, Avatar, LinearProgress, Paper 
} from '@mui/material';
import { 
  PlayArrow, Assignment, MenuBook, Check, PlayCircleOutline, 
  BarChart, People, Schedule, AutoStories, ArrowBack, BookmarkAdd 
} from '@mui/icons-material';

const ModuleDetail = () => {
  const { id } = useParams();
  const [tabValue, setTabValue] = useState(0);
  const [module, setModule] = useState(null);
  const [loading, setLoading] = useState(true);

  // Fetch module data
  useEffect(() => {
    // Simulating API fetch
    setTimeout(() => {
      // This would normally be an API call
      let moduleData = {};
      
      // Check module ID and load appropriate data
      if (id === '1') {
        moduleData = {
          id: id,
          title: 'Verilog Fundamentals',
          description: 'Master the basics of Verilog HDL and start your journey in digital design. This comprehensive course covers everything from basic syntax to designing combinational and sequential circuits.',
