              <Typography variant="h6">{module.studentsCount.toLocaleString()}</Typography>
            </Box>
          </Paper>
        </Grid>
        <Grid item xs={6} sm={3}>
          <Paper sx={{ p: 2, height: '100%', display: 'flex', alignItems: 'center' }}>
