                        <Box sx={{ 
                          p: 2, 
                          bgcolor: '#272822',
                          color: '#f8f8f2',
                          fontFamily: 'monospace',
                          fontSize: '0.875rem',
                          overflow: 'auto',
                          maxHeight: '300px',
                          whiteSpace: 'pre'
                        }}>
                          {example.code}
                        </Box>
                      </Paper>
                    </Grid>
                  ))}
                </Grid>
              </>
            )}
            
            {/* Additional Resources */}
            {module.resources && (
              <>
                <Typography variant="h5" gutterBottom sx={{ mt: 4, borderLeft: '4px solid #6a0dad', pl: 2 }}>Additional Resources</Typography>
