                          />
                          <Typography variant="body2" paragraph sx={{ mb: 2 }}>
                            {relModule.description}
                          </Typography>
                          <Button 
                            component={Link} 
                            to={`/modules/${relModule.id}`}
                            variant="outlined"
                            color="primary"
                            size="small"
                            fullWidth
                          >
                            View Module
                          </Button>
                        </CardContent>
                      </Card>
                    </Grid>
                  ))}
                </Grid>
              </>
            )}
          </Box>
        )}

        {/* Content Tab */}
        {tabValue === 1 && (
          <Box>
            <Typography variant="h5" gutterBottom sx={{ borderLeft: '4px solid #6a0dad', pl: 2 }}>Module Content</Typography>
