import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Box, Typography, Button, Grid, Paper, Tabs, Tab, Snackbar, Alert, List, ListItem, ListItemIcon, ListItemText, LinearProgress } from '@mui/material';
import { PlayArrow, Save, ArrowBack, LightbulbOutlined, BarChart, Check, MenuBook, Assignment, Code, Terminal, Assessment } from '@mui/icons-material';

const CodeEditor = () => {
  const [code, setCode] = useState(
`module full_adder(
  input wire a, b, cin,
  output wire sum, cout
);
  // Implement full adder logic
  // sum = a XOR b XOR cin
  // cout = (a AND b) OR (cin AND (a XOR b))
  
  // Internal wire for a XOR b
  wire a_xor_b;
  
  // Calculate sum
  assign a_xor_b = a ^ b;
  assign sum = a_xor_b ^ cin;
  
  // Calculate carry out
  assign cout = (a & b) | (cin & a_xor_b);
endmodule

// Testbench (uncomment to simulate)
/*
module full_adder_tb;
  // Testbench signals
  reg a, b, cin;
  wire sum, cout;
  
  // Instantiate the full_adder
  full_adder dut(
    .a(a),
    .b(b),
    .cin(cin),
    .sum(sum),
    .cout(cout)
  );
  
  // Test stimulus
  initial begin
    // Display header
    $display("a b cin | sum cout");
    $display("--------+---------");
    
    // Test all input combinations
    {a, b, cin} = 3'b000; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    {a, b, cin} = 3'b001; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    {a, b, cin} = 3'b010; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    {a, b, cin} = 3'b011; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    {a, b, cin} = 3'b100; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    {a, b, cin} = 3'b101; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    {a, b, cin} = 3'b110; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    {a, b, cin} = 3'b111; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    $finish;
  end
endmodule
*/`);
  const [output, setOutput] = useState('');
  const [isCompiling, setIsCompiling] = useState(false);
  const [tabValue, setTabValue] = useState(0);
  const [notification, setNotification] = useState({ open: false, message: '', severity: 'info' });

  // Mock exercise data
  const exerciseData = {
    id: '103',
    title: 'Implementing a Full Adder',
    description: 'Create a Verilog module for a full adder with inputs a, b, cin and outputs sum and cout. A full adder adds three bits and produces a sum bit and a carry bit.',
    objectives: [
      'Understand the operation of a full adder circuit',
      'Implement combinational logic using Verilog assign statements',
      'Utilize internal wires for intermediate signals',
      'Apply proper port declarations with the wire data type'
    ],
    background: 'A full adder is a fundamental building block in digital circuit design, used to add two binary digits along with a carry input. It produces a sum output and a carry output, which can be cascaded to create multi-bit adders.',
    testCases: [
      { inputs: 'a=0, b=0, cin=0', expectedOutputs: 'sum=0, cout=0', explanation: 'With all inputs 0, both sum and carry are 0' },
      { inputs: 'a=1, b=0, cin=0', expectedOutputs: 'sum=1, cout=0', explanation: 'Adding just a single 1 gives sum=1, with no carry' },
      { inputs: 'a=0, b=1, cin=0', expectedOutputs: 'sum=1, cout=0', explanation: 'Adding just a single 1 gives sum=1, with no carry' },
      { inputs: 'a=0, b=0, cin=1', expectedOutputs: 'sum=1, cout=0', explanation: 'Adding just a single 1 gives sum=1, with no carry' },
      { inputs: 'a=1, b=1, cin=0', expectedOutputs: 'sum=0, cout=1', explanation: 'Adding two 1s results in 0 with a carry of 1 (binary 10)' },
      { inputs: 'a=1, b=0, cin=1', expectedOutputs: 'sum=0, cout=1', explanation: 'Adding two 1s results in 0 with a carry of 1 (binary 10)' },
      { inputs: 'a=0, b=1, cin=1', expectedOutputs: 'sum=0, cout=1', explanation: 'Adding two 1s results in 0 with a carry of 1 (binary 10)' },
      { inputs: 'a=1, b=1, cin=1', expectedOutputs: 'sum=1, cout=1', explanation: 'Adding three 1s results in 1 with a carry of 1 (binary 11)' }
    ],
    hints: [
      'Remember the boolean equations for a full adder: sum = a ⊕ b ⊕ cin and cout = (a·b) + (cin·(a⊕b))',
      'Use intermediate wires to make your code more readable',
      'The XOR operator in Verilog is represented by the caret symbol (^)',
      'Use the & operator for AND and | operator for OR operations'
    ],
    truthTable: [
      { a: 0, b: 0, cin: 0, sum: 0, cout: 0 },
      { a: 0, b: 0, cin: 1, sum: 1, cout: 0 },
      { a: 0, b: 1, cin: 0, sum: 1, cout: 0 },
      { a: 0, b: 1, cin: 1, sum: 0, cout: 1 },
      { a: 1, b: 0, cin: 0, sum: 1, cout: 0 },
      { a: 1, b: 0, cin: 1, sum: 0, cout: 1 },
      { a: 1, b: 1, cin: 0, sum: 0, cout: 1 },
      { a: 1, b: 1, cin: 1, sum: 1, cout: 1 }
    ]
  };

  const handleTabChange = (_, newValue) => setTabValue(newValue);

  const handleRunCode = () => {
    setIsCompiling(true);
    setOutput('');
    
    // Simulate compilation and running
    setTimeout(() => {
      if (code.includes('assign sum') && code.includes('assign cout')) {
        // Format output with color codes for terminal display
        setOutput(`
\x1b[1;32m✓ Compilation successful!\x1b[0m

\x1b[1;36mRunning simulation for full_adder module...\x1b[0m

\x1b[1;33mTruth Table Verification:\x1b[0m
┌─────┬─────┬─────┬─────┬─────┬─────────────────┐
│ \x1b[1mTest\x1b[0m │ \x1b[1ma\x1b[0m   │ \x1b[1mb\x1b[0m   │ \x1b[1mcin\x1b[0m │ \x1b[1msum\x1b[0m │ \x1b[1mcout\x1b[0m │ \x1b[1mStatus\x1b[0m       │
├─────┼─────┼─────┼─────┼─────┼─────────────────┤
│ 1   │ 0   │ 0   │ 0   │ 0   │ 0   │ \x1b[32m✓ PASSED\x1b[0m      │
│ 2   │ 0   │ 0   │ 1   │ 1   │ 0   │ \x1b[32m✓ PASSED\x1b[0m      │
│ 3   │ 0   │ 1   │ 0   │ 1   │ 0   │ \x1b[32m✓ PASSED\x1b[0m      │
│ 4   │ 0   │ 1   │ 1   │ 0   │ 1   │ \x1b[32m✓ PASSED\x1b[0m      │
│ 5   │ 1   │ 0   │ 0   │ 1   │ 0   │ \x1b[32m✓ PASSED\x1b[0m      │
│ 6   │ 1   │ 0   │ 1   │ 0   │ 1   │ \x1b[32m✓ PASSED\x1b[0m      │
│ 7   │ 1   │ 1   │ 0   │ 0   │ 1   │ \x1b[32m✓ PASSED\x1b[0m      │
│ 8   │ 1   │ 1   │ 1   │ 1   │ 1   │ \x1b[32m✓ PASSED\x1b[0m      │
└─────┴─────┴─────┴─────┴─────┴─────────────────┘

\x1b[1;33mWaveform Simulation:\x1b[0m
Time  a b cin | sum cout
----------------+----------
0ns:  0 0 0   |  0   0
10ns: 0 0 1   |  1   0
20ns: 0 1 0   |  1   0
30ns: 0 1 1   |  0   1
40ns: 1 0 0   |  1   0
50ns: 1 0 1   |  0   1
60ns: 1 1 0   |  0   1
70ns: 1 1 1   |  1   1

\x1b[1;32mSummary: 8/8 test cases passed.\x1b[0m
🎉 \x1b[1mCongratulations!\x1b[0m Your full adder implementation works correctly.

\x1b[33mPerformance Analysis:\x1b[0m
• Gate Count: Approximately 5 gates (2 XOR, 2 AND, 1 OR)
• Critical Path: Input to Sum output through 2 XOR gates
• Additional Optimizations: Using intermediate signals improves readability

\x1b[36mDesign Feedback:\x1b[0m
✓ Good use of intermediate signals for readability
✓ Properly declared wire and port types
✓ Clean implementation of the full adder equations
`);
        setNotification({
          open: true,
          message: 'All tests passed! Great job implementing the full adder!',
          severity: 'success'
        });
      } else if (code.includes('module full_adder') && code.includes('endmodule')) {
        setOutput(`
\x1b[1;33mCompilation Warning:\x1b[0m
Your module compiles, but appears incomplete.

\x1b[1;31mRuntime Errors:\x1b[0m
The simulation indicates that your implementation does not correctly produce the expected sum and cout outputs.

\x1b[1;33mVerification Results:\x1b[0m
┌─────┬─────┬─────┬─────┬──────┬──────┬─────────────────┐
│ \x1b[1mTest\x1b[0m │ \x1b[1ma\x1b[0m   │ \x1b[1mb\x1b[0m   │ \x1b[1mcin\x1b[0m │ \x1b[1msum\x1b[0m  │ \x1b[1mcout\x1b[0m │ \x1b[1mStatus\x1b[0m       │
├─────┼─────┼─────┼─────┼──────┼──────┼─────────────────┤
│ 1   │ 0   │ 0   │ 0   │ x    │ x    │ \x1b[31m✗ FAILED\x1b[0m      │
│ 2   │ 0   │ 0   │ 1   │ x    │ x    │ \x1b[31m✗ FAILED\x1b[0m      │
│ 3   │ 0   │ 1   │ 0   │ x    │ x    │ \x1b[31m✗ FAILED\x1b[0m      │
└─────┴─────┴─────┴─────┴──────┴──────┴─────────────────┘
Further tests skipped due to errors.

\x1b[1;36mTroubleshooting Suggestions:\x1b[0m
1. Make sure you've implemented the assign statements for sum and cout
2. Check if you've correctly defined intermediate signals if needed
3. Verify your logic equations: sum = a ^ b ^ cin and cout = (a & b) | (cin & (a ^ b))
4. Make sure all variables are properly connected

\x1b[33mHint:\x1b[0m Review the boolean equations for a full adder and ensure your implementation follows them.
`);
        setNotification({
          open: true,
          message: 'Your code compiles but doesn\'t correctly implement the full adder functionality.',
          severity: 'warning'
        });
      } else {
        setOutput(`
\x1b[1;31mCompilation Error:\x1b[0m
Error: Incomplete or incorrect module implementation.

\x1b[33mExpected structure:\x1b[0m
module full_adder(
  input wire a, b, cin,
  output wire sum, cout
);
  // Implementation here with proper assignments for sum and cout
endmodule

\x1b[36mSpecific Issues:\x1b[0m
- ${!code.includes('module full_adder') ? 'Missing or incorrect module declaration' : 'Module declaration found'} 
- ${!code.includes('input') ? 'Missing input port declarations' : 'Input ports found'}
- ${!code.includes('output') ? 'Missing output port declarations' : 'Output ports found'}
- ${!code.includes('assign') ? 'Missing assign statements for outputs' : 'Assign statements found but may be incomplete'}
- ${!code.includes('endmodule') ? 'Missing endmodule statement' : 'Endmodule statement found'}

\x1b[33mRemember:\x1b[0m
A full adder needs to implement the following logic:
- sum = a XOR b XOR cin
- cout = (a AND b) OR (cin AND (a XOR b))

Please implement the full adder logic before running the code again.
`);
        setNotification({
          open: true,
          message: 'Compilation failed. Please check your code and fix the issues.',
          severity: 'error'
        });
      }
      
      setIsCompiling(false);
    }, 1500);
  };

  const handleSaveCode = () => {
    setNotification({
      open: true,
      message: 'Code saved successfully!',
      severity: 'success'
    });
  };

  const handleCloseNotification = () => {
    setNotification({ ...notification, open: false });
  };

  const handleShowHint = () => {
    setNotification({
      open: true,
      message: `Hint: ${exerciseData.hints[0]}`,
      severity: 'info'
    });
  };

  return (
    <Box className="container page-container">
      {/* Header */}
      <Box sx={{ mb: 3 }}>
        <Button 
          component={Link}
          to="/modules/1"
          startIcon={<ArrowBack />}
          sx={{ mb: 2 }}
        >
          Back to Module
        </Button>
        <Typography variant="h4" component="h1" gutterBottom color="primary" sx={{ fontWeight: 'bold' }}>
          {exerciseData.title}
        </Typography>
        <Typography variant="subtitle1" sx={{ mb: 3, fontSize: '1.1rem' }}>
          {exerciseData.description}
        </Typography>
        
        <Grid container spacing={4} sx={{ mb: 4 }}>
          {/* Objectives */}
          <Grid item xs={12} md={6}>
            <Paper elevation={0} variant="outlined" sx={{ p: 3, height: '100%', borderRadius: 2 }}>
              <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center' }}>
                <BarChart color="primary" sx={{ mr: 1 }} /> Learning Objectives
              </Typography>
              <List dense disablePadding>
                {exerciseData.objectives.map((objective, index) => (
                  <ListItem key={index}>
                    <ListItemIcon sx={{ minWidth: 36 }}>
                      <Check color="primary" />
                    </ListItemIcon>
                    <ListItemText primary={objective} />
                  </ListItem>
                ))}
              </List>
            </Paper>
          </Grid>
          
          {/* Background */}
          <Grid item xs={12} md={6}>
            <Paper elevation={0} variant="outlined" sx={{ p: 3, height: '100%', borderRadius: 2 }}>
              <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center' }}>
                <MenuBook color="primary" sx={{ mr: 1 }} /> Background
              </Typography>
              <Typography>{exerciseData.background}</Typography>
              
              {/* Truth Table */}
              <Typography variant="subtitle1" sx={{ mt: 2, mb: 1, fontWeight: 'bold' }}>
                Truth Table:
              </Typography>
              <Box sx={{ overflowX: 'auto' }}>
                <table style={{ 
                  width: '100%', 
                  borderCollapse: 'collapse', 
                  textAlign: 'center',
                  fontSize: '0.9rem',
                  border: '1px solid #e0e0e0'
                }}>
                  <thead>
                    <tr style={{ backgroundColor: '#f5f5f5' }}>
                      <th style={{ padding: '8px', border: '1px solid #e0e0e0' }}>a</th>
                      <th style={{ padding: '8px', border: '1px solid #e0e0e0' }}>b</th>
                      <th style={{ padding: '8px', border: '1px solid #e0e0e0' }}>cin</th>
                      <th style={{ padding: '8px', border: '1px solid #e0e0e0' }}>sum</th>
                      <th style={{ padding: '8px', border: '1px solid #e0e0e0' }}>cout</th>
                    </tr>
                  </thead>
                  <tbody>
                    {exerciseData.truthTable.map((row, index) => (
                      <tr key={index} style={{ backgroundColor: index % 2 === 0 ? 'white' : '#fafafa' }}>
                        <td style={{ padding: '8px', border: '1px solid #e0e0e0' }}>{row.a}</td>
                        <td style={{ padding: '8px', border: '1px solid #e0e0e0' }}>{row.b}</td>
                        <td style={{ padding: '8px', border: '1px solid #e0e0e0' }}>{row.cin}</td>
                        <td style={{ padding: '8px', border: '1px solid #e0e0e0', backgroundColor: '#e8f5e9' }}>{row.sum}</td>
                        <td style={{ padding: '8px', border: '1px solid #e0e0e0', backgroundColor: '#e8f5e9' }}>{row.cout}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </Box>
            </Paper>
          </Grid>
        </Grid>
      </Box>
      
      {/* Test Cases */}
      <Paper elevation={2} sx={{ p: 3, mb: 4, borderRadius: 2 }}>
        <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center' }}>
          <Assignment color="primary" sx={{ mr: 1 }} /> Test Cases
        </Typography>
        <Typography variant="body2" sx={{ mb: 2 }}>
          Your implementation will be validated against these test cases. Make sure your code handles all these scenarios correctly.
        </Typography>
        <Box sx={{ overflowX: 'auto' }}>
          <table style={{ 
            width: '100%', 
            borderCollapse: 'collapse', 
            textAlign: 'left',
            fontSize: '0.9rem',
            border: '1px solid #e0e0e0'
          }}>
            <thead>
              <tr style={{ backgroundColor: '#f5f5f5' }}>
                <th style={{ padding: '10px', border: '1px solid #e0e0e0' }}>Test Case</th>
                <th style={{ padding: '10px', border: '1px solid #e0e0e0' }}>Inputs</th>
                <th style={{ padding: '10px', border: '1px solid #e0e0e0' }}>Expected Outputs</th>
                <th style={{ padding: '10px', border: '1px solid #e0e0e0' }}>Explanation</th>
              </tr>
            </thead>
            <tbody>
              {exerciseData.testCases.map((testCase, index) => (
                <tr key={index} style={{ backgroundColor: index % 2 === 0 ? 'white' : '#fafafa' }}>
                  <td style={{ padding: '10px', border: '1px solid #e0e0e0', fontWeight: 'bold' }}>Case {index + 1}</td>
                  <td style={{ padding: '10px', border: '1px solid #e0e0e0', fontFamily: 'monospace' }}>{testCase.inputs}</td>
                  <td style={{ padding: '10px', border: '1px solid #e0e0e0', fontFamily: 'monospace' }}>{testCase.expectedOutputs}</td>
                  <td style={{ padding: '10px', border: '1px solid #e0e0e0' }}>{testCase.explanation}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </Box>
      </Paper>
      
      {/* Editor and Output */}
      <Grid container spacing={3}>
        {/* Editor Section */}
        <Grid item xs={12} md={7}>
          <Paper elevation={3} sx={{ height: '100%', display: 'flex', flexDirection: 'column', borderRadius: 2 }}>
            <Box sx={{ p: 2, borderBottom: 1, borderColor: 'divider', bgcolor: '#f5f5f5', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Typography variant="h6" sx={{ display: 'flex', alignItems: 'center' }}>
                <Code sx={{ mr: 1 }} />
                Verilog Editor
              </Typography>
              <Typography variant="caption" color="text.secondary">
                Write your solution here
              </Typography>
            </Box>
            <Box sx={{ flexGrow: 1, p: 0, bgcolor: '#272822', minHeight: '500px' }}>
              <textarea
                value={code}
                onChange={(e) => setCode(e.target.value)}
                style={{
                  width: '100%',
                  height: '100%',
                  minHeight: '500px',
                  fontFamily: 'Consolas, Monaco, "Andale Mono", "Ubuntu Mono", monospace',
                  fontSize: '14px',
                  padding: '16px',
                  border: 'none',
                  borderRadius: 0,
                  resize: 'none',
                  backgroundColor: '#272822',
                  color: '#f8f8f2',
                  lineHeight: '1.5',
                  outline: 'none'
                }}
                spellCheck="false"
                placeholder="// Write your Verilog code here"
              />
            </Box>
            <Box sx={{ p: 2, borderTop: 1, borderColor: 'divider', display: 'flex', gap: 1, bgcolor: '#f5f5f5' }}>
              <Button 
                variant="contained" 
                color="primary" 
                startIcon={<PlayArrow />}
                onClick={handleRunCode}
                disabled={isCompiling}
                size="large"
              >
                {isCompiling ? 'Running...' : 'Run Code'}
              </Button>
              <Button 
                variant="outlined" 
                startIcon={<Save />}
                onClick={handleSaveCode}
                size="large"
              >
                Save
              </Button>
              <Button 
                variant="outlined" 
                color="secondary"
                startIcon={<LightbulbOutlined />}
                onClick={handleShowHint}
                sx={{ ml: 'auto' }}
                size="large"
              >
                Hint
              </Button>
            </Box>
          </Paper>
        </Grid>
        
        {/* Output Section */}
        <Grid item xs={12} md={5}>
          <Paper elevation={3} sx={{ display: 'flex', flexDirection: 'column', height: '100%', borderRadius: 2 }}>
            <Tabs 
              value={tabValue} 
              onChange={handleTabChange}
              sx={{ 
                borderBottom: 1, 
                borderColor: 'divider',
                bgcolor: '#f5f5f5'
              }}
            >
              <Tab label="Output" icon={<Assessment fontSize="small" />} iconPosition="start" />
              <Tab label="Console" icon={<Terminal fontSize="small" />} iconPosition="start" />
              <Tab label="Hints" icon={<LightbulbOutlined fontSize="small" />} iconPosition="start" />
            </Tabs>
            
            {/* Output Tab */}
            {tabValue === 0 && (
              <Box 
                sx={{ 
                  flexGrow: 1, 
                  p: 2, 
                  fontFamily: 'Consolas, Monaco, monospace', 
                  fontSize: '14px',
                  bgcolor: '#2d2d2d',
                  color: '#e6e6e6',
                  overflowY: 'auto',
                  whiteSpace: 'pre-wrap',
                  minHeight: '500px'
                }}
              >
                {isCompiling ? 
                  <Box sx={{ p: 2 }}>
                    <LinearProgress color="primary" sx={{ mb: 2 }} />
                    <Typography variant="body2" sx={{ color: '#a6e22e' }}>
                      Compiling and running your code...
                    </Typography>
                  </Box> : 
                  (output ? 
                    <Typography component="pre" sx={{ margin: 0, fontFamily: 'inherit', fontSize: 'inherit' }}>
                      {output}
                    </Typography> : 
                    <Typography variant="body2" sx={{ color: '#75715e', fontStyle: 'italic' }}>
                      // Run your code to see the output here.
                      // Make sure you've implemented all the required functionality.
                    </Typography>
                  )
                }
              </Box>
            )}
            
            {/* Console Tab */}
            {tabValue === 1 && (
              <Box 
                sx={{ 
                  flexGrow: 1, 
                  p: 2, 
                  fontFamily: 'Consolas, Monaco, monospace', 
                  fontSize: '14px',
                  bgcolor: '#2d2d2d',
                  color: '#e6e6e6',
                  overflowY: 'auto',
                  whiteSpace: 'pre-wrap',
                  minHeight: '500px'
                }}
              >
                <Typography variant="body2" sx={{ color: '#75715e', fontStyle: 'italic' }}>
                  // Verilog compiler messages will appear here.
                  // This includes warnings and errors from your code.
                </Typography>
              </Box>
            )}
            
            {/* Hints Tab */}
            {tabValue === 2 && (
              <Box 
                sx={{ 
                  flexGrow: 1, 
                  p: 3, 
                  bgcolor: '#fffde7',
                  overflowY: 'auto',
                  minHeight: '500px'
                }}
              >
                <Typography variant="h6" gutterBottom>
                  Helpful Hints
                </Typography>
                <Typography variant="body2" paragraph>
                  If you're stuck, these hints may guide you in the right direction without giving away the full solution.
                </Typography>
                <List>
                  {exerciseData.hints.map((hint, index) => (
                    <ListItem key={index} alignItems="flex-start" sx={{ px: 0 }}>
                      <ListItemIcon sx={{ minWidth: 36, mt: 0.5 }}>
                        <LightbulbOutlined color="warning" />
                      </ListItemIcon>
                      <ListItemText 
                        primary={`Hint ${index + 1}`} 
                        secondary={hint}
                        primaryTypographyProps={{ fontWeight: 'bold' }}
                        secondaryTypographyProps={{ variant: 'body1' }}
                      />
                    </ListItem>
                  ))}
                </List>
                
                <Box sx={{ mt: 3, p: 2, bgcolor: 'rgba(0, 0, 0, 0.03)', borderRadius: 2 }}>
                  <Typography variant="subtitle1" gutterBottom sx={{ fontWeight: 'bold' }}>
                    Full Adder Logic:
                  </Typography>
                  <Typography variant="body2" paragraph>
                    A full adder adds three bits (a, b, and cin) and produces a sum bit and a carry bit.
                  </Typography>
                  <Box component="img" src="https://via.placeholder.com/500x200?text=Full+Adder+Logic+Diagram" 
                       alt="Full Adder Logic Diagram" 
                       sx={{ width: '100%', borderRadius: 1, mb: 2 }} />
                  <Typography variant="body2">
                    The boolean equations for a full adder are:
                  </Typography>
                  <Box sx={{ 
                    bgcolor: 'white', 
                    p: 2, 
                    borderRadius: 1, 
                    fontFamily: 'math', 
                    fontSize: '1rem',
                    textAlign: 'center',
                    mt: 1
                  }}>
                    <Box>sum = a ⊕ b ⊕ cin</Box>
                    <Box>cout = (a·b) + cin·(a⊕b)</Box>
                  </Box>
                </Box>
              </Box>
            )}
          </Paper>
        </Grid>
      </Grid>

      {/* Notification */}
      <Snackbar 
        open={notification.open} 
        autoHideDuration={6000} 
        onClose={handleCloseNotification}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
      >
        <Alert 
          onClose={handleCloseNotification} 
          severity={notification.severity} 
          sx={{ width: '100%' }}
        >
          {notification.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default CodeEditor;
