import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  Typography,
  TextField,
  Button,
  List,
  ListItem,
  ListItemText,
  ListItemAvatar,
  Avatar,
  Divider,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Chip,
  Stack,
  Rating,
  Card,
  CardContent,
  CardActions,
  Pagination,
  CircularProgress,
  Alert,
  Tabs,
  Tab,
  TextareaAutosize,
} from '@mui/material';
import {
  Add as AddIcon,
  ThumbUp as ThumbUpIcon,
  Comment as CommentIcon,
  Share as ShareIcon,
  Bookmark as BookmarkIcon,
  Code as CodeIcon,
  Tag as TagIcon,
} from '@mui/icons-material';
import axios from 'axios';

const CommunityForum = () => {
  const [openDialog, setOpenDialog] = useState(false);
  const [question, setQuestion] = useState({
    title: '',
    content: '',
    tags: [],
    currentTag: '',
    codeSnippet: '',
  });
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [selectedTag, setSelectedTag] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [tabValue, setTabValue] = useState(0);
  const [showCodeEditor, setShowCodeEditor] = useState(false);

  useEffect(() => {
    fetchQuestions();
  }, [page, selectedTag, searchQuery]);

  const fetchQuestions = async () => {
    try {
      setLoading(true);
      const params = {
        page,
        limit: 10,
        ...(selectedTag && { tag: selectedTag }),
        ...(searchQuery && { search: searchQuery }),
      };

      // First try to fetch from the API
      try {
        const response = await axios.get('/api/forum', { params });
        if (response.data && response.data.questions) {
          setQuestions(response.data.questions);
          setTotalPages(response.data.totalPages || 1);
          setError(null);
        } else {
          throw new Error('Invalid response format');
        }
      } catch (apiError) {
        console.error('API error:', apiError);
        // If API fails, use sample data for testing UI
        const sampleData = [
          {
            id: '1',
            title: "What's the most efficient way to implement a 4-bit multiplier in Verilog?",
            content: "I'm working on a project that requires a 4-bit multiplier. I've tried a naive implementation using a nested loop, but I'm wondering if there are more efficient techniques specifically for FPGA implementation.",
            tags: ["verilog", "multiplier", "optimization", "fpga"],
            author: { name: 'VerilogNewbie' },
            createdAt: new Date().toISOString(),
            difficulty: 3,
            likes: [],
            comments: []
          },
          {
            id: '2',
            title: "Understanding non-blocking assignments in Verilog",
            content: "I'm confused about when to use blocking (=) versus non-blocking (<=) assignments in Verilog. Could someone explain the difference and provide guidelines on which to use in different situations?",
            tags: ["verilog", "sequential-logic", "best-practices"],
            author: { name: 'LearningVerilog' },
            createdAt: new Date(Date.now() - 86400000).toISOString(),
            difficulty: 2,
            likes: [],
            comments: []
          }
        ];
        
        setQuestions(sampleData);
        setTotalPages(1);
        setError('Could not connect to server. Showing sample data for UI testing.');
      }
    } catch (err) {
      setError('Failed to fetch questions. Please try again later.');
      console.error('Error in fetchQuestions:', err);
      setQuestions([]);
    } finally {
      setLoading(false);
    }
  };

  const handleOpenDialog = () => {
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setQuestion({
      title: '',
      content: '',
      tags: [],
      currentTag: '',
      codeSnippet: '',
    });
    setShowCodeEditor(false);
  };

  const handleAddTag = () => {
    if (question.currentTag.trim()) {
      setQuestion({
        ...question,
        tags: [...question.tags, question.currentTag.trim()],
        currentTag: '',
      });
    }
  };

  const handleRemoveTag = (tagToRemove) => {
    setQuestion({
      ...question,
      tags: question.tags.filter((tag) => tag !== tagToRemove),
    });
  };

  const handleSubmitQuestion = async () => {
    try {
      const response = await axios.post('/api/forum', {
        title: question.title,
        content: question.content,
        tags: question.tags,
        codeSnippet: question.codeSnippet,
      });
      setQuestions([response.data, ...questions]);
      handleCloseDialog();
      setError(null);
    } catch (err) {
      setError('Failed to post question. Please try again later.');
      console.error('Error posting question:', err);
    }
  };

  const handleLike = async (questionId) => {
    try {
      await axios.post(`/api/forum/${questionId}/like`);
      fetchQuestions(); // Refresh questions to get updated likes
    } catch (err) {
      console.error('Error liking question:', err);
    }
  };

  const handlePageChange = (event, value) => {
    setPage(value);
  };

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const renderCodeSnippet = (code) => {
    if (!code) return null;
    return (
      <Paper sx={{ p: 2, bgcolor: '#f5f5f5', mt: 2 }}>
        <Typography variant="subtitle2" gutterBottom>
          <CodeIcon sx={{ mr: 1, verticalAlign: 'middle' }} />
          Code Snippet
        </Typography>
        <pre style={{ margin: 0, whiteSpace: 'pre-wrap' }}>{code}</pre>
      </Paper>
    );
  };

  return (
    <Box sx={{ maxWidth: 800, mx: 'auto', p: 3 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
        <Typography variant="h4" component="h1">
          Verilog Community Forum
        </Typography>
        <Button
          variant="contained"
          color="primary"
          startIcon={<AddIcon />}
          onClick={handleOpenDialog}
        >
          Ask Question
        </Button>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      <TextField
        fullWidth
        label="Search questions"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        sx={{ mb: 2 }}
      />

      <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 2 }}>
        <Tabs value={tabValue} onChange={handleTabChange}>
          <Tab label="Latest Questions" />
          <Tab label="Most Liked" />
          <Tab label="Unanswered" />
        </Tabs>
      </Box>

      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', p: 3 }}>
          <CircularProgress />
        </Box>
      ) : (
        <>
          <List>
            {questions.length > 0 ? (
              questions.map((q) => (
                <React.Fragment key={q._id || q.id}>
                  <ListItem
                    component={Card}
                    sx={{
                      mb: 2,
                      display: 'flex',
                      flexDirection: 'column',
                      alignItems: 'stretch',
                    }}
                  >
                    <CardContent>
                      <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                        <ListItemAvatar>
                          <Avatar>{q.author?.name?.[0] || 'U'}</Avatar>
                        </ListItemAvatar>
                        <Box sx={{ flex: 1 }}>
                          <Typography variant="h6">{q.title}</Typography>
                          <Typography variant="body2" color="text.secondary">
                            Posted by {q.author?.name || 'Unknown'} on{' '}
                            {new Date(q.createdAt || Date.now()).toLocaleDateString()}
                          </Typography>
                        </Box>
                        <Rating value={q.difficulty || 0} readOnly size="small" />
                      </Box>

                      <Typography variant="body1" paragraph>
                        {q.content}
                      </Typography>

                      {renderCodeSnippet(q.codeSnippet)}

                      <Stack direction="row" spacing={1} sx={{ mb: 2 }}>
                        {q.tags && q.tags.map((tag) => (
                          <Chip
                            key={tag}
                            label={tag}
                            size="small"
                            onClick={() => setSelectedTag(tag)}
                            color={selectedTag === tag ? 'primary' : 'default'}
                          />
                        ))}
                      </Stack>
                    </CardContent>

                    <CardActions sx={{ justifyContent: 'space-between' }}>
                      <Box>
                        <IconButton
                          size="small"
                          onClick={() => handleLike(q._id)}
                          color={(q.likes && q.author && q.likes.includes(q.author._id)) ? 'primary' : 'default'}
                        >
                          <ThumbUpIcon />
                          <Typography variant="body2" sx={{ ml: 0.5 }}>
                            {q.likes?.length || 0}
                          </Typography>
                        </IconButton>
                        <IconButton size="small">
                          <CommentIcon />
                          <Typography variant="body2" sx={{ ml: 0.5 }}>
                            {q.comments?.length || 0}
                          </Typography>
                        </IconButton>
                      </Box>
                      <Box>
                        <IconButton size="small">
                          <ShareIcon />
                        </IconButton>
                        <IconButton size="small">
                          <BookmarkIcon />
                        </IconButton>
                      </Box>
                    </CardActions>
                  </ListItem>
                  <Divider />
                </React.Fragment>
              ))
            ) : (
              <Box sx={{ textAlign: 'center', py: 4 }}>
                <Typography variant="h6" gutterBottom>
                  No questions found
                </Typography>
                <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                  Be the first to ask a question!
                </Typography>
                <Button
                  variant="contained"
                  color="primary"
                  startIcon={<AddIcon />}
                  onClick={handleOpenDialog}
                >
                  Ask Question
                </Button>
              </Box>
            )}
          </List>

          <Box sx={{ display: 'flex', justifyContent: 'center', mt: 3 }}>
            <Pagination
              count={totalPages}
              page={page}
              onChange={handlePageChange}
              color="primary"
            />
          </Box>
        </>
      )}

      <Dialog 
        open={openDialog} 
        onClose={handleCloseDialog} 
        maxWidth="md" 
        fullWidth
        PaperProps={{
          sx: { borderRadius: 2 }
        }}
      >
        <DialogTitle 
          sx={{ 
            bgcolor: 'primary.main', 
            color: 'white',
            pt: 2,
            pb: 2
          }}
        >
          Ask a Question
        </DialogTitle>
        <DialogContent sx={{ pt: 3, pb: 1 }}>
          <TextField
            autoFocus
            margin="dense"
            label="Title"
            fullWidth
            value={question.title}
            onChange={(e) => setQuestion({ ...question, title: e.target.value })}
            sx={{ mb: 2 }}
          />
          <TextField
            margin="dense"
            label="Question"
            fullWidth
            multiline
            rows={4}
            value={question.content}
            onChange={(e) => setQuestion({ ...question, content: e.target.value })}
            sx={{ mb: 2 }}
          />
          
          <Box sx={{ mt: 2, mb: 2 }}>
            <Typography variant="subtitle2" gutterBottom>
              <TagIcon sx={{ mr: 1, verticalAlign: 'middle' }} />
              Tags
            </Typography>
            <Box sx={{ display: 'flex', gap: 1, mb: 1 }}>
              <TextField
                size="small"
                label="Add Tag"
                value={question.currentTag}
                onChange={(e) =>
                  setQuestion({ ...question, currentTag: e.target.value })
                }
                onKeyPress={(e) => {
                  if (e.key === 'Enter' && question.currentTag.trim()) {
                    handleAddTag();
                    e.preventDefault();
                  }
                }}
              />
              <Button
                variant="outlined"
                onClick={handleAddTag}
                disabled={!question.currentTag.trim()}
              >
                Add
              </Button>
            </Box>
            <Stack direction="row" spacing={1} flexWrap="wrap" gap={1}>
              {question.tags.map((tag) => (
                <Chip
                  key={tag}
                  label={tag}
                  onDelete={() => handleRemoveTag(tag)}
                />
              ))}
            </Stack>
          </Box>

          <Box sx={{ mt: 2, mb: 2 }}>
            <Button
              startIcon={<CodeIcon />}
              onClick={() => setShowCodeEditor(!showCodeEditor)}
              sx={{ mb: 1 }}
              variant="outlined"
            >
              {showCodeEditor ? 'Remove Code Snippet' : 'Add Code Snippet'}
            </Button>
            {showCodeEditor && (
              <Paper sx={{ p: 1, bgcolor: '#f5f5f5' }}>
                <TextareaAutosize
                  minRows={4}
                  placeholder="Paste your Verilog code here..."
                  value={question.codeSnippet}
                  onChange={(e) =>
                    setQuestion({ ...question, codeSnippet: e.target.value })
                  }
                  style={{ width: '100%', padding: '8px', fontFamily: 'monospace' }}
                />
              </Paper>
            )}
          </Box>
        </DialogContent>
        <DialogActions sx={{ px: 3, py: 2 }}>
          <Button onClick={handleCloseDialog} variant="outlined">Cancel</Button>
          <Button
            onClick={handleSubmitQuestion}
            variant="contained"
            color="primary"
            disabled={!question.title.trim() || !question.content.trim()}
          >
            Post Question
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default CommunityForum;
