sourcesContent:
import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { 
  Box, Typography, Button, Grid, Card, CardContent, Breadcrumbs, 
  Tabs, Tab, List, ListItem, ListItemIcon, ListItemText, Divider, 
  Chip, Rating, Avatar, LinearProgress, Paper 
} from '@mui/material';
import { 
  PlayArrow, Assignment, MenuBook, Check, PlayCircleOutline, 
  BarChart, People, Schedule, AutoStories, ArrowBack, BookmarkAdd 
} from '@mui/icons-material';

const ModuleDetail = () => {
  const { id } = useParams();
  const [tabValue, setTabValue] = useState(0);
  const [module, setModule] = useState(null);
  const [loading, setLoading] = useState(true);

  // Fetch module data
  useEffect(() => {
    // Simulating API fetch
    setTimeout(() => {
      // This would normally be an API call
      const moduleData = {
        id: parseInt(id),
        title: 'Verilog Fundamentals',
        description: 'A comprehensive introduction to Verilog Hardware Description Language (HDL). Master the essential concepts of digital design using Verilog, from basic syntax to advanced modeling techniques.',
        image: 'https://via.placeholder.com/800x400?text=Verilog+Fundamentals',
        level: 'Beginner',
        category: 'Fundamentals',
        duration: '8 hours',
        instructor: {
          name: 'Dr. Jane Smith, Ph.D.',
          bio: 'Professor of Electrical Engineering with 15+ years of experience in digital design and hardware verification. Dr. Smith has worked at Intel and Xilinx, contributing to FPGA design tools and methodologies. She has published over 30 research papers on efficient hardware design and verification techniques.',
          avatar: 'https://via.placeholder.com/150',
          credentials: ['Ph.D. in Computer Engineering', 'Senior Member IEEE', 'Former Principal Engineer at Xilinx']
        },
        rating: 4.9,
        studentsCount: 1520,
        updatedAt: '2023-11-15',
        completion: 15,
        prerequisites: [
          'Basic understanding of digital logic (AND, OR, NOT gates)',
          'Familiarity with binary and hexadecimal number systems',
          'Some programming experience helpful but not required',
          'No prior Verilog experience needed'
        ],
        overview: 'This comprehensive module covers the fundamental concepts of Verilog HDL, equipping you with the skills to design and simulate digital circuits. Starting from the basic syntax and constructs, you will progressively learn different modeling styles in Verilog and how to effectively use them for various design scenarios.\n\nYou\'ll understand the difference between behavioral, dataflow, and structural modeling, and implement basic combinational and sequential circuits using each approach. Throughout the module, you\'ll gain hands-on experience with industry-standard design practices and learn how to write synthesizable Verilog code for real hardware implementation.\n\nBy the end of this module, you\'ll be able to design, implement, and verify basic digital circuits using Verilog, setting a solid foundation for more advanced digital design courses.',
        learningOutcomes: [
          'Write and understand Verilog syntax and language constructs',
          'Implement combinational and sequential logic using Verilog',
          'Apply different modeling styles (behavioral, dataflow, structural)',
          'Create and use modules with proper port declarations',
          'Design basic digital circuits like multiplexers, decoders, and counters',
          'Develop effective testbenches to verify Verilog designs',
          'Understand the synthesis process and write synthesizable code'
        ],
        lessons: [
          {
            id: 1,
            title: 'Introduction to HDLs and Verilog',
            duration: '45 min',
            type: 'video',
            completed: true,
            description: 'Overview of Hardware Description Languages, history of Verilog, comparison with VHDL, and introduction to the design flow using Verilog.',
            topics: ['HDL concepts', 'History of Verilog', 'Design flow', 'Simulation vs. synthesis']
          },
          {
            id: 2,
            title: 'Verilog Syntax and Basic Constructs',
            duration: '60 min',
            type: 'video',
            completed: true,
            description: 'Learn the fundamental syntax elements of Verilog, including modules, port declarations, comments, and basic constructs.',
            topics: ['Modules and ports', 'Comments', 'Identifiers', 'Keywords', 'Case sensitivity']
          },
          {
            id: 3,
            title: 'Data Types and Operators',
            duration: '75 min',
            type: 'video',
            completed: false,
            description: 'Explore Verilog data types including nets, registers, vectors, arrays, parameters, and learn about different operators and their precedence.',
            topics: ['Wire and reg', 'Vectors', 'Arrays', 'Parameters', 'Arithmetic operators', 'Logical operators', 'Relational operators']
          },
          {
            id: 4,
            title: 'Behavioral Modeling',
            duration: '90 min',
            type: 'video',
            completed: false,
            description: 'Master procedural assignments, always blocks, initial blocks, and procedural statements for behavioral modeling in Verilog.',
            topics: ['Always and initial blocks', 'Blocking vs. non-blocking assignments', 'If-else statements', 'Case statements', 'Loops']
          },
          {
            id: 5,
            title: 'Dataflow Modeling',
            duration: '60 min',
            type: 'video',
            completed: false,
            description: 'Learn continuous assignments and expressions for describing combinational logic using dataflow modeling techniques.',
            topics: ['Continuous assignments', 'Expressions', 'Delays', 'Conditional operator', 'Bit-wise operators']
          },
          {
            id: 6,
            title: 'Structural Modeling',
            duration: '60 min',
            type: 'video',
            completed: false,
            description: 'Understand module instantiation, gate-level modeling, and hierarchical design approaches using structural modeling.',
            topics: ['Module instantiation', 'Gate primitives', 'Gate delays', 'User-defined primitives', 'Hierarchical design']
          },
          {
            id: 7,
            title: 'Testbench Development',
            duration: '80 min',
            type: 'video',
            completed: false,
            description: 'Learn to create effective testbenches for verifying Verilog designs, including stimulus generation and result checking.',
            topics: ['Testbench architecture', 'Stimulus generation', 'Result checking', 'Task and functions', 'File I/O']
          },
          {
            id: 8,
            title: 'Sequential Logic Design',
            duration: '90 min',
            type: 'video',
            completed: false,
            description: 'Design flip-flops, latches, and registers in Verilog, and understand clocking and synchronous design principles.',
            topics: ['Flip-flops', 'Latches', 'Registers', 'Counters', 'Synchronous design', 'Reset strategies']
          },
          {
            id: 9,
            title: 'Synthesizable Verilog',
            duration: '60 min',
            type: 'video',
            completed: false,
            description: 'Understand the synthesis process and learn to write synthesizable Verilog code for implementation on real hardware.',
            topics: ['Synthesis guidelines', 'Inferred logic', 'Coding for synthesis', 'Common synthesis issues', 'Timing considerations']
          },
          {
            id: 10,
            title: 'Course Project: 4-bit ALU Design',
            duration: '120 min',
            type: 'project',
            completed: false,
            description: 'Apply everything you\'ve learned to design, implement, and verify a 4-bit Arithmetic Logic Unit (ALU) in Verilog.',
            topics: ['Specification development', 'Module design', 'Implementation', 'Testbench creation', 'Verification', 'Documentation']
          }
        ],
        exercises: [
          {
            id: 101,
            title: 'Basic Logic Gates Implementation',
            difficulty: 'Easy',
            type: 'coding',
            completed: false,
            description: 'Implement basic logic gates (AND, OR, NOT, NAND, NOR, XOR, XNOR) using different modeling styles in Verilog.',
            estimatedTime: '30 min',
            points: 10
          },
          {
            id: 102,
            title: 'Multiplexers and Decoders',
            difficulty: 'Easy',
            type: 'coding',
            completed: false,
            description: 'Design and implement 2-to-1, 4-to-1 multiplexers and 2-to-4, 3-to-8 decoders using Verilog.',
            estimatedTime: '45 min',
            points: 15
          },
          {
            id: 103,
            title: 'Full Adder and Ripple Carry Adder',
            difficulty: 'Medium',
            type: 'coding',
            completed: false,
            description: 'Implement a 1-bit full adder and use it to create a 4-bit ripple carry adder with comprehensive testbench.',
            estimatedTime: '60 min',
            points: 20
          },
          {
            id: 104,
            title: 'Flip-Flops and Counters',
            difficulty: 'Medium',
            type: 'coding',
            completed: false,
            description: 'Design D flip-flop, T flip-flop, and JK flip-flop, then use them to implement various counters.',
            estimatedTime: '75 min',
            points: 25
          },
          {
            id: 105,
            title: 'Finite State Machine',
            difficulty: 'Hard',
            type: 'coding',
            completed: false,
            description: 'Design and implement a sequence detector FSM that identifies a specific bit pattern in an input stream.',
            estimatedTime: '90 min',
            points: 30
          },
          {
            id: 106,
            title: 'Memory Module Design',
            difficulty: 'Hard',
            type: 'coding',
            completed: false,
            description: 'Implement a simple RAM module with read and write capabilities, along with a testbench to verify functionality.',
            estimatedTime: '90 min',
            points: 30
          }
        ],
        resources: [
          {
            title: 'Verilog HDL Quick Reference Guide',
            type: 'PDF',
            url: '#'
          },
          {
            title: 'Common Verilog Coding Mistakes',
            type: 'Article',
            url: '#'
          },
          {
            title: 'Synthesizable vs. Non-synthesizable Constructs',
            type: 'Cheatsheet',
            url: '#'
          },
          {
            title: 'Recommended Verilog Coding Style Guide',
            type: 'PDF',
            url: '#'
          }
        ],
        codeExamples: [
          {
            title: 'Basic Module Structure',
            code: `module example_module(
  input  wire a, b,
  output wire y
);
  // Module implementation
  assign y = a & b;
endmodule`
          },
          {
            title: 'Behavioral Modeling Example',
            code: `module counter(
  input  wire clk, reset,
  output reg [3:0] count
);
  always @(posedge clk or posedge reset) begin
    if (reset)
      count <= 4'b0000;
    else
      count <= count + 1;
  end
endmodule`
          }
        ],
        relatedModules: [
          {
            id: 2,
            title: 'Combinational Circuits',
            level: 'Intermediate',
            description: 'Design complex combinational logic circuits including multiplexers, decoders, and arithmetic circuits.'
          },
          {
            id: 3,
            title: 'Sequential Circuits',
            level: 'Intermediate',
            description: 'Master sequential logic design with flip-flops, counters, shift registers, and state machines.'
          },
          {
            id: 8,
            title: 'System Verilog Basics',
            level: 'Intermediate',
            description: 'Learn SystemVerilog extensions for enhanced hardware design and verification capabilities.'
          }
        ]
      };
      
      setModule(moduleData);
      setLoading(false);
    }, 800);
  }, [id]);

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  if (loading) {
    return (
      <Box className="container page-container" sx={{ textAlign: 'center', py: 8 }}>
        <Typography variant="h5" sx={{ mb: 4 }}>Loading module content...</Typography>
        <LinearProgress color="primary" sx={{ mx: 'auto', width: '50%' }} />
      </Box>
    );
  }

  if (!module) {
    return (
      <Box className="container page-container" sx={{ textAlign: 'center', py: 8 }}>
        <Typography variant="h5">Module not found</Typography>
        <Button component={Link} to="/modules" sx={{ mt: 2 }}>
          Back to Modules
        </Button>
      </Box>
    );
  }

  return (
    <Box className="container page-container">
      {/* Breadcrumb */}
      <Breadcrumbs sx={{ mb: 2 }}>
        <Link to="/" style={{ textDecoration: 'none', color: 'inherit' }}>
          Home
        </Link>
        <Link to="/modules" style={{ textDecoration: 'none', color: 'inherit' }}>
          Modules
        </Link>
        <Typography color="text.primary">{module.title}</Typography>
      </Breadcrumbs>

      <Button 
        component={Link} 
        to="/modules" 
        startIcon={<ArrowBack />} 
        sx={{ mb: 3 }}
      >
        Back to Modules
      </Button>

      {/* Module Header */}
      <Box sx={{ position: 'relative', mb: 4 }}>
        <Box
          component="img"
          src={module.image}
          alt={module.title}
          sx={{ 
            width: '100%', 
            height: '300px', 
            objectFit: 'cover', 
            borderRadius: 2,
            filter: 'brightness(0.7)',
          }}
        />
        <Box 
          sx={{ 
            position: 'absolute', 
            bottom: 0, 
            left: 0, 
            right: 0, 
            p: 3, 
            color: 'white',
            background: 'linear-gradient(to top, rgba(0,0,0,0.7), transparent)'
          }}
        >
          <Box sx={{ display: 'flex', gap: 1, mb: 1 }}>
            <Chip 
              label={module.level} 
              size="small" 
              sx={{ backgroundColor: 'rgba(255, 255, 255, 0.2)', color: 'white' }} 
            />
            <Chip 
              label={module.category} 
              size="small" 
              sx={{ backgroundColor: 'rgba(255, 255, 255, 0.2)', color: 'white' }} 
            />
          </Box>
          <Typography variant="h3" component="h1" gutterBottom fontWeight="bold">
            {module.title}
          </Typography>
          <Typography variant="subtitle1">
            {module.description}
          </Typography>
        </Box>
      </Box>

      {/* Module Stats */}
      <Grid container spacing={2} sx={{ mb: 4 }}>
        <Grid item xs={6} sm={3}>
          <Paper sx={{ p: 2, height: '100%', display: 'flex', alignItems: 'center' }}>
            <People sx={{ color: 'primary.main', mr: 1 }} />
            <Box>
              <Typography variant="body2" color="text.secondary">Students</Typography>
              <Typography variant="h6">{module.studentsCount.toLocaleString()}</Typography>
            </Box>
          </Paper>
        </Grid>
        <Grid item xs={6} sm={3}>
          <Paper sx={{ p: 2, height: '100%', display: 'flex', alignItems: 'center' }}>
            <Schedule sx={{ color: 'primary.main', mr: 1 }} />
            <Box>
              <Typography variant="body2" color="text.secondary">Duration</Typography>
              <Typography variant="h6">{module.duration}</Typography>
            </Box>
          </Paper>
        </Grid>
        <Grid item xs={6} sm={3}>
          <Paper sx={{ p: 2, height: '100%', display: 'flex', alignItems: 'center' }}>
            <AutoStories sx={{ color: 'primary.main', mr: 1 }} />
            <Box>
              <Typography variant="body2" color="text.secondary">Lessons</Typography>
              <Typography variant="h6">{module.lessons.length}</Typography>
            </Box>
          </Paper>
        </Grid>
        <Grid item xs={6} sm={3}>
          <Paper sx={{ p: 2, height: '100%', display: 'flex', alignItems: 'center' }}>
            <Assignment sx={{ color: 'primary.main', mr: 1 }} />
            <Box>
              <Typography variant="body2" color="text.secondary">Exercises</Typography>
              <Typography variant="h6">{module.exercises.length}</Typography>
            </Box>
          </Paper>
        </Grid>
      </Grid>

      {/* Progress and Start buttons */}
      <Grid container spacing={2} sx={{ mb: 4 }}>
        <Grid item xs={12} md={8}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="body2" color="text.secondary" gutterBottom>
              Your Progress
            </Typography>
            <LinearProgress 
              variant="determinate" 
              value={module.completion} 
              sx={{ height: 10, borderRadius: 5, mb: 1 }} 
            />
            <Typography variant="body2">
              {module.completion}% Complete
            </Typography>
          </Paper>
        </Grid>
        <Grid item xs={12} md={4}>
          <Box sx={{ display: 'flex', gap: 2, height: '100%' }}>
            <Button 
              variant="contained" 
              color="primary" 
              fullWidth 
              startIcon={<PlayArrow />}
              component={Link}
              to={`/modules/${id}/lessons/1`}
              sx={{ height: '100%' }}
            >
              {module.completion > 0 ? 'Continue Learning' : 'Start Module'}
            </Button>
            <Button 
              variant="outlined" 
              color="primary"
              startIcon={<BookmarkAdd />}
              sx={{ flexShrink: 0 }}
            >
              Save
            </Button>
          </Box>
        </Grid>
      </Grid>

      {/* Tabs and Content */}
      <Box sx={{ mb: 4 }}>
        <Tabs 
          value={tabValue} 
          onChange={handleTabChange} 
          sx={{ 
            borderBottom: 1, 
            borderColor: 'divider',
            mb: 3,
            '& .MuiTab-root': {
              textTransform: 'none',
              fontSize: '1rem',
              fontWeight: 500
            }
          }}
        >
          <Tab label="Overview" icon={<MenuBook />} iconPosition="start" />
          <Tab label="Content" icon={<PlayCircleOutline />} iconPosition="start" />
          <Tab label="Exercises" icon={<Assignment />} iconPosition="start" />
          <Tab label="Instructor" icon={<People />} iconPosition="start" />
        </Tabs>

        {/* Overview Tab */}
        {tabValue === 0 && (
          <Box>
            <Typography variant="h5" gutterBottom sx={{ borderLeft: '4px solid #6a0dad', pl: 2 }}>About This Module</Typography>
            <Typography paragraph sx={{ fontSize: '1.05rem', lineHeight: 1.6 }}>{module.overview}</Typography>
            
            {/* Learning Outcomes */}
            {module.learningOutcomes && (
              <>
                <Typography variant="h5" gutterBottom sx={{ mt: 4, borderLeft: '4px solid #6a0dad', pl: 2 }}>What You'll Learn</Typography>
                <Paper elevation={0} variant="outlined" sx={{ p: 3, borderRadius: 2, mb: 4 }}>
                  <Grid container spacing={2}>
                    {module.learningOutcomes.map((outcome, index) => (
                      <Grid item xs={12} sm={6} key={index}>
                        <Box sx={{ display: 'flex', alignItems: 'flex-start', mb: 1 }}>
                          <Check sx={{ color: 'success.main', mr: 1, mt: 0.3 }} />
                          <Typography variant="body1">{outcome}</Typography>
                        </Box>
                      </Grid>
                    ))}
                  </Grid>
                </Paper>
              </>
            )}
            
            {/* Prerequisites */}
            <Typography variant="h5" gutterBottom sx={{ mt: 4, borderLeft: '4px solid #6a0dad', pl: 2 }}>Prerequisites</Typography>
            <Paper elevation={0} variant="outlined" sx={{ p: 3, borderRadius: 2, mb: 4 }}>
              <List sx={{ display: 'grid', gridTemplateColumns: { xs: '1fr', sm: '1fr 1fr' }, gap: 1 }}>
                {module.prerequisites.map((prereq, index) => (
                  <ListItem key={index} sx={{ px: 1 }}>
                    <ListItemIcon sx={{ minWidth: 36 }}>
                      <Check sx={{ color: 'primary.main' }} />
                    </ListItemIcon>
                    <ListItemText primary={prereq} />
                  </ListItem>
                ))}
              </List>
            </Paper>

            {/* Skills */}
            <Typography variant="h5" gutterBottom sx={{ mt: 4, borderLeft: '4px solid #6a0dad', pl: 2 }}>Skills You'll Gain</Typography>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mb: 4 }}>
              <Chip label="Verilog Syntax" sx={{ bgcolor: 'rgba(106, 13, 173, 0.08)', fontWeight: 500 }} />
              <Chip label="Module Design" sx={{ bgcolor: 'rgba(106, 13, 173, 0.08)', fontWeight: 500 }} />
              <Chip label="Behavioral Modeling" sx={{ bgcolor: 'rgba(106, 13, 173, 0.08)', fontWeight: 500 }} />
              <Chip label="Dataflow Modeling" sx={{ bgcolor: 'rgba(106, 13, 173, 0.08)', fontWeight: 500 }} />
              <Chip label="Structural Modeling" sx={{ bgcolor: 'rgba(106, 13, 173, 0.08)', fontWeight: 500 }} />
              <Chip label="Testbench Development" sx={{ bgcolor: 'rgba(106, 13, 173, 0.08)', fontWeight: 500 }} />
              <Chip label="Sequential Logic" sx={{ bgcolor: 'rgba(106, 13, 173, 0.08)', fontWeight: 500 }} />
              <Chip label="Combinational Logic" sx={{ bgcolor: 'rgba(106, 13, 173, 0.08)', fontWeight: 500 }} />
            </Box>
            
            {/* Code Examples */}
            {module.codeExamples && (
              <>
                <Typography variant="h5" gutterBottom sx={{ mt: 4, borderLeft: '4px solid #6a0dad', pl: 2 }}>Code Examples</Typography>
                <Grid container spacing={3} sx={{ mb: 4 }}>
                  {module.codeExamples.map((example, index) => (
                    <Grid item xs={12} md={6} key={index}>
                      <Paper elevation={2} sx={{ p: 0, overflow: 'hidden', borderRadius: 2 }}>
                        <Box sx={{ p: 2, bgcolor: '#f5f5f5', borderBottom: '1px solid #e0e0e0' }}>
                          <Typography variant="subtitle1" fontWeight="medium">{example.title}</Typography>
                        </Box>
                        <Box sx={{ 
                          p: 2, 
                          bgcolor: '#272822',
                          color: '#f8f8f2',
                          fontFamily: 'monospace',
                          fontSize: '0.875rem',
                          overflow: 'auto',
                          maxHeight: '300px',
                          whiteSpace: 'pre'
                        }}>
                          {example.code}
                        </Box>
                      </Paper>
                    </Grid>
                  ))}
                </Grid>
              </>
            )}
            
            {/* Additional Resources */}
            {module.resources && (
              <>
                <Typography variant="h5" gutterBottom sx={{ mt: 4, borderLeft: '4px solid #6a0dad', pl: 2 }}>Additional Resources</Typography>
                <Paper elevation={0} variant="outlined" sx={{ p: 3, borderRadius: 2, mb: 4 }}>
                  <List>
                    {module.resources.map((resource, index) => (
                      <React.Fragment key={index}>
                        <ListItem sx={{ px: 1 }}>
                          <ListItemIcon>
                            {resource.type === 'PDF' ? 
                              <MenuBook color="primary" /> : 
                              <Assignment color="primary" />
                            }
                          </ListItemIcon>
                          <ListItemText 
                            primary={resource.title} 
                            secondary={resource.type} 
                          />
                          <Button variant="outlined" size="small" component="a" href={resource.url}>
                            Download
                          </Button>
                        </ListItem>
                        {index < module.resources.length - 1 && <Divider component="li" />}
                      </React.Fragment>
                    ))}
                  </List>
                </Paper>
              </>
            )}

            {/* Related Modules */}
            {module.relatedModules.length > 0 && (
              <>
                <Typography variant="h5" gutterBottom sx={{ mt: 4, borderLeft: '4px solid #6a0dad', pl: 2 }}>Related Modules</Typography>
                <Grid container spacing={3}>
                  {module.relatedModules.map(relModule => (
                    <Grid item xs={12} sm={6} md={4} key={relModule.id}>
                      <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column', transition: 'transform 0.3s', '&:hover': { transform: 'translateY(-8px)' } }}>
                        <CardContent sx={{ flexGrow: 1 }}>
                          <Typography variant="h6" component="h3" gutterBottom>
                            {relModule.title}
                          </Typography>
                          <Chip 
                            label={relModule.level} 
                            size="small" 
                            sx={{ 
                              mb: 2,
                              backgroundColor: relModule.level === 'Beginner' ? '#e3f2fd' : 
                                            relModule.level === 'Intermediate' ? '#fff8e1' : '#fbe9e7',
                              color: relModule.level === 'Beginner' ? '#0277bd' : 
                                  relModule.level === 'Intermediate' ? '#ff8f00' : '#e64a19'
                            }} 
                          />
                          <Typography variant="body2" paragraph sx={{ mb: 2 }}>
                            {relModule.description}
                          </Typography>
                          <Button 
                            component={Link} 
                            to={`/modules/${relModule.id}`}
                            variant="outlined"
                            color="primary"
                            size="small"
                            fullWidth
                          >
                            View Module
                          </Button>
                        </CardContent>
                      </Card>
                    </Grid>
                  ))}
                </Grid>
              </>
            )}
          </Box>
        )}

        {/* Content Tab */}
        {tabValue === 1 && (
          <Box>
            <Typography variant="h5" gutterBottom>Module Content</Typography>
            <Typography paragraph>
              This module contains {module.lessons.length} lessons with a total duration of {module.duration}.
            </Typography>
            
            <List sx={{ bgcolor: 'background.paper' }}>
              {module.lessons.map((lesson, index) => (
                <React.Fragment key={lesson.id}>
                  <ListItem 
                    component={Link} 
                    to={`/modules/${id}/lessons/${lesson.id}`}
                    sx={{ 
                      textDecoration: 'none', 
                      color: 'inherit',
                      py: 2,
                      borderRadius: 1,
                      '&:hover': {
                        backgroundColor: 'rgba(0, 0, 0, 0.04)'
                      }
                    }}
                  >
                    <ListItemIcon>
                      {lesson.type === 'video' ? 
                        <PlayCircleOutline color="primary" /> : 
                        <Assignment color="primary" />
                      }
                    </ListItemIcon>
                    <ListItemText 
                      primary={
                        <Box sx={{ display: 'flex', alignItems: 'center' }}>
                          <Typography component="span" sx={{ mr: 1 }}>
                            {index + 1}. {lesson.title}
                          </Typography>
                          {lesson.completed && <Check fontSize="small" color="success" />}
                        </Box>
                      } 
                      secondary={
                        <Box sx={{ display: 'flex', alignItems: 'center', mt: 0.5 }}>
                          <Chip 
                            label={lesson.type} 
                            size="small"
                            sx={{ 
                              mr: 1,
                              textTransform: 'capitalize',
                              bgcolor: lesson.type === 'video' ? '#e3f2fd' : '#fff8e1'
                            }} 
                          />
                          <Typography variant="body2" color="text.secondary">
                            {lesson.duration}
                          </Typography>
                        </Box>
                      }
                    />
                  </ListItem>
                  {index < module.lessons.length - 1 && <Divider component="li" />}
                </React.Fragment>
              ))}
            </List>
          </Box>
        )}

        {/* Exercises Tab */}
        {tabValue === 2 && (
          <Box>
            <Typography variant="h5" gutterBottom>Practical Exercises</Typography>
            <Typography paragraph>
              Test your knowledge with these hands-on exercises. Complete them to reinforce your learning.
            </Typography>
            
            <List sx={{ bgcolor: 'background.paper' }}>
              {module.exercises.map((exercise, index) => (
                <React.Fragment key={exercise.id}>
                  <ListItem 
                    component={Link} 
                    to={`/modules/${id}/exercises/${exercise.id}`}
                    sx={{ 
                      textDecoration: 'none', 
                      color: 'inherit',
                      py: 2,
                      borderRadius: 1,
                      '&:hover': {
                        backgroundColor: 'rgba(0, 0, 0, 0.04)'
                      }
                    }}
                  >
                    <ListItemIcon>
                      <Assignment color="primary" />
                    </ListItemIcon>
                    <ListItemText 
                      primary={
                        <Box sx={{ display: 'flex', alignItems: 'center' }}>
                          <Typography component="span" sx={{ mr: 1 }}>
                            {exercise.title}
                          </Typography>
                          {exercise.completed && <Check fontSize="small" color="success" />}
                        </Box>
                      } 
                      secondary={
                        <Box sx={{ display: 'flex', alignItems: 'center', mt: 0.5 }}>
                          <Chip 
                            label={exercise.difficulty} 
                            size="small"
                            sx={{ 
                              mr: 1,
                              bgcolor: 
                                exercise.difficulty === 'Easy' ? '#e8f5e9' : 
                                exercise.difficulty === 'Medium' ? '#fff8e1' : '#ffebee'
                            }} 
                          />
                          <Chip 
                            label={exercise.type} 
                            size="small"
                            sx={{ 
                              textTransform: 'capitalize'
                            }} 
                          />
                        </Box>
                      }
                    />
                  </ListItem>
                  {index < module.exercises.length - 1 && <Divider component="li" />}
                </React.Fragment>
              ))}
            </List>
          </Box>
        )}

        {/* Instructor Tab */}
        {tabValue === 3 && (
          <Box>
            <Grid container spacing={3}>
              <Grid item xs={12} md={4} sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                <Avatar 
                  src={module.instructor.avatar} 
                  alt={module.instructor.name}
                  sx={{ width: 150, height: 150, mb: 2 }}
                />
                <Typography variant="h5" gutterBottom>
                  {module.instructor.name}
                </Typography>
                <Rating value={module.rating} precision={0.1} readOnly sx={{ mb: 1 }} />
                <Typography variant="body2" color="text.secondary">
                  Module rating: {module.rating}/5
                </Typography>
              </Grid>
              <Grid item xs={12} md={8}>
                <Typography variant="h6" gutterBottom>About the Instructor</Typography>
                <Typography paragraph>
                  {module.instructor.bio}
                </Typography>
                <Button variant="outlined" color="primary">
                  View All Courses by This Instructor
                </Button>
              </Grid>
            </Grid>
          </Box>
        )}
      </Box>
    </Box>
  );
};

export default ModuleDetail;
