import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { 
  Box, Typography, Button, Grid, Card, CardContent, Chip, TextField,
  InputAdornment, MenuItem, Select, FormControl, InputLabel,
  Avatar, Divider, Tab, Tabs, Badge, Pagination, Paper
} from '@mui/material';
import { 
  Search, FilterList, Add, ThumbUp, Comment, Visibility,
  CheckCircle, Sort, FiberNew
} from '@mui/icons-material';

const Forum = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('all');
  const [sortOrder, setSortOrder] = useState('recent');
  const [tabValue, setTabValue] = useState(0);
  const [page, setPage] = useState(1);

  // Sample forum posts
  const posts = [
    {
      id: 1,
      title: 'How to implement an efficient 4-bit multiplier?',
      content: 'I\'m trying to create a 4-bit multiplier that is efficient in terms of gates used. Has anyone implemented this before? I\'m particularly interested in understanding how to optimize the partial products calculation.',
      tags: ['multiplier', 'optimization', 'combinational-logic'],
      user: {
        id: 101,
        name: 'John Doe',
        avatar: 'https://via.placeholder.com/40'
      },
      category: 'Design Optimization',
      created: '2023-07-15T10:30:00',
      upvotes: 12,
      views: 56,
      comments: 4,
      isResolved: false,
      isNew: false
    },
    {
      id: 2,
      title: 'Understanding non-blocking assignments',
      content: 'I\'m confused about when to use blocking vs non-blocking assignments in Verilog. Could someone explain the difference and provide examples of when each should be used?',
      tags: ['syntax', 'basics', 'assignments'],
      user: {
        id: 102,
        name: 'Jane Smith',
        avatar: 'https://via.placeholder.com/40'
      },
      category: 'Language Basics',
      created: '2023-07-20T14:45:00',
      upvotes: 24,
      views: 128,
      comments: 11,
      isResolved: true,
      isNew: false
    },
    {
      id: 3,
      title: 'How to simulate a UART transmitter?',
      content: 'I\'ve implemented a UART transmitter in Verilog but I\'m not sure how to properly simulate it. What test bench strategy should I use?',
      tags: ['uart', 'testbench', 'simulation'],
      user: {
        id: 103,
        name: 'Alex Chen',
        avatar: 'https://via.placeholder.com/40'
      },
      category: 'Verification',
      created: '2023-08-05T09:15:00',
      upvotes: 8,
      views: 42,
      comments: 3,
      isResolved: false,
      isNew: true
    },
    {
      id: 4,
      title: 'Timing issues with my FSM implementation',
      content: 'I\'m encountering timing issues with my Finite State Machine implementation. The state transitions aren\'t happening as expected. How can I debug this?',
      tags: ['fsm', 'timing', 'debugging'],
      user: {
        id: 104,
        name: 'Emily Johnson',
        avatar: 'https://via.placeholder.com/40'
      },
      category: 'Debugging',
      created: '2023-08-10T16:20:00',
      upvotes: 5,
      views: 31,
      comments: 2,
      isResolved: false,
      isNew: true
    },
    {
      id: 5,
      title: 'Best practices for clock domain crossing',
      content: 'What are the recommended techniques for handling clock domain crossing in a complex FPGA design? I\'m working on a project with multiple clock domains.',
      tags: ['cdc', 'fpga', 'synchronization'],
      user: {
        id: 105,
        name: 'Robert Kim',
        avatar: 'https://via.placeholder.com/40'
      },
      category: 'Advanced Design',
      created: '2023-08-02T11:50:00',
      upvotes: 19,
      views: 85,
      comments: 7,
      isResolved: true,
      isNew: false
    }
  ];

  // Filter and sort posts
  const filteredPosts = posts.filter(post => {
    const matchesSearch = post.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                        post.content.toLowerCase().includes(searchQuery.toLowerCase()) ||
                        post.tags.some(tag => tag.toLowerCase().includes(searchQuery.toLowerCase()));
    const matchesCategory = categoryFilter === 'all' || post.category === categoryFilter;
    const matchesTab = (tabValue === 0) ||
                     (tabValue === 1 && post.isResolved) ||
                     (tabValue === 2 && !post.isResolved) ||
                     (tabValue === 3 && post.isNew);
    
    return matchesSearch && matchesCategory && matchesTab;
  });

  const sortedPosts = [...filteredPosts].sort((a, b) => {
    if (sortOrder === 'recent') {
      return new Date(b.created) - new Date(a.created);
    } else if (sortOrder === 'popular') {
      return b.upvotes - a.upvotes;
    } else if (sortOrder === 'commented') {
      return b.comments - a.comments;
    }
    return 0;
  });

  // Pagination
  const postsPerPage = 5;
  const pagesCount = Math.ceil(sortedPosts.length / postsPerPage);
  const displayedPosts = sortedPosts.slice(
    (page - 1) * postsPerPage,
    page * postsPerPage
  );

  // Get unique categories for filter
  const categories = [...new Set(posts.map(post => post.category))];

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
    setPage(1);
  };

  // Format date from ISO string to a readable format
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    
    // Calculate difference in days
    const diffTime = Math.abs(now - date);
    const diffDays = Math.floor(diffTime / (1000 * 60 * 60 * 24));
    
    if (diffDays === 0) {
      return 'Today';
    } else if (diffDays === 1) {
      return 'Yesterday';
    } else if (diffDays < 7) {
      return `${diffDays} days ago`;
    } else {
      return date.toLocaleDateString('en-US', { 
        year: 'numeric', 
        month: 'short', 
        day: 'numeric' 
      });
    }
  };

  return (
    <Box className="container page-container">
      <Typography variant="h3" component="h1" gutterBottom color="primary">
        Verilog Community Forum
      </Typography>
      <Typography variant="subtitle1" paragraph sx={{ mb: 4 }}>
        Connect with other learners, ask questions, and share your Verilog knowledge
      </Typography>

      {/* Actions Bar */}
      <Grid container spacing={2} sx={{ mb: 4 }}>
        <Grid item xs={12} md={8}>
          <TextField
            fullWidth
            placeholder="Search posts by keyword, title, or tag"
            variant="outlined"
            value={searchQuery}
            onChange={(e) => {
              setSearchQuery(e.target.value);
              setPage(1);
            }}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <Search />
                </InputAdornment>
              ),
            }}
          />
        </Grid>
        <Grid item xs={12} md={4}>
          <Button
            component={Link}
            to="/forum/new"
            variant="contained"
            color="primary"
            fullWidth
            startIcon={<Add />}
            size="large"
            sx={{ height: '100%' }}
          >
            Ask a Question
          </Button>
        </Grid>
      </Grid>

      {/* Filters Bar */}
      <Grid container spacing={2} sx={{ mb: 3 }}>
        <Grid item xs={12} md={5}>
          <FormControl fullWidth variant="outlined" size="small">
            <InputLabel id="category-filter-label">Filter by Category</InputLabel>
            <Select
              labelId="category-filter-label"
              id="category-filter"
              value={categoryFilter}
              onChange={(e) => {
                setCategoryFilter(e.target.value);
                setPage(1);
              }}
              label="Filter by Category"
              startAdornment={
                <InputAdornment position="start">
                  <FilterList fontSize="small" />
                </InputAdornment>
              }
            >
              <MenuItem value="all">All Categories</MenuItem>
              {categories.map(category => (
                <MenuItem key={category} value={category}>{category}</MenuItem>
              ))}
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs={12} md={4}>
          <FormControl fullWidth variant="outlined" size="small">
            <InputLabel id="sort-order-label">Sort By</InputLabel>
            <Select
              labelId="sort-order-label"
              id="sort-order"
              value={sortOrder}
              onChange={(e) => setSortOrder(e.target.value)}
              label="Sort By"
              startAdornment={
                <InputAdornment position="start">
                  <Sort fontSize="small" />
                </InputAdornment>
              }
            >
              <MenuItem value="recent">Most Recent</MenuItem>
              <MenuItem value="popular">Most Upvoted</MenuItem>
              <MenuItem value="commented">Most Commented</MenuItem>
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs={12} md={3}>
          <Paper elevation={0} sx={{ p: 1, border: '1px solid #e0e0e0', borderRadius: 1 }}>
            <Typography variant="body2" color="text.secondary" align="center">
              {filteredPosts.length} {filteredPosts.length === 1 ? 'post' : 'posts'} found
            </Typography>
          </Paper>
        </Grid>
      </Grid>

      {/* Tabs */}
      <Box sx={{ mb: 3, borderBottom: 1, borderColor: 'divider' }}>
        <Tabs value={tabValue} onChange={handleTabChange} aria-label="forum tabs">
          <Tab label="All Posts" />
          <Tab 
            label="Resolved" 
            icon={<CheckCircle color="success" fontSize="small" />} 
            iconPosition="start"
          />
          <Tab 
            label="Unanswered" 
            icon={
              <Badge badgeContent={posts.filter(p => !p.isResolved).length} color="error">
                <Box sx={{ mr: 1 }} />
              </Badge>
            } 
          />
          <Tab 
            label="New" 
            icon={<FiberNew color="primary" fontSize="small" />} 
            iconPosition="start"
          />
        </Tabs>
      </Box>

      {/* Posts List */}
      {displayedPosts.length > 0 ? (
        <Box sx={{ mb: 4 }}>
          {displayedPosts.map(post => (
            <Card key={post.id} sx={{ mb: 2, borderRadius: 2 }}>
              <CardContent>
                <Grid container spacing={2}>
                  <Grid item xs={12}>
                    <Box sx={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'space-between' }}>
                      <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                        <Avatar 
                          src={post.user.avatar} 
                          alt={post.user.name}
                          sx={{ width: 32, height: 32, mr: 1 }}
                        />
                        <Typography variant="body2" color="text.secondary">
                          {post.user.name} • {formatDate(post.created)}
                        </Typography>
                        {post.isNew && (
                          <Chip 
                            label="New" 
                            size="small" 
                            color="primary" 
                            sx={{ ml: 1, height: 20, fontSize: '0.75rem' }}
                          />
                        )}
                      </Box>
                      <Box>
                        <Chip 
                          label={post.category} 
                          size="small" 
                          variant="outlined"
                          sx={{ borderRadius: '4px' }}
                        />
                        {post.isResolved && (
                          <Chip 
                            label="Resolved" 
                            size="small" 
                            color="success" 
                            icon={<CheckCircle fontSize="small" />}
                            sx={{ ml: 1, borderRadius: '4px' }}
                          />
                        )}
                      </Box>
                    </Box>
                    
                    <Link 
                      to={`/forum/${post.id}`} 
                      style={{ textDecoration: 'none', color: 'inherit' }}
                    >
                      <Typography variant="h6" component="h2" gutterBottom>
                        {post.title}
                      </Typography>
                    </Link>
                    
                    <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                      {post.content.length > 200 
                        ? `${post.content.substring(0, 200)}...` 
                        : post.content}
                    </Typography>
                    
                    <Box sx={{ mb: 2 }}>
                      {post.tags.map(tag => (
                        <Chip 
                          key={tag} 
                          label={tag} 
                          size="small" 
                          sx={{ mr: 1, mb: 1, backgroundColor: '#f0f0f0' }}
                        />
                      ))}
                    </Box>
                  </Grid>
                </Grid>
                
                <Divider sx={{ my: 1 }} />
                
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <Box sx={{ display: 'flex', alignItems: 'center', mr: 3 }}>
                      <ThumbUp fontSize="small" sx={{ mr: 0.5, color: 'text.secondary' }} />
                      <Typography variant="body2" color="text.secondary">
                        {post.upvotes}
                      </Typography>
                    </Box>
                    <Box sx={{ display: 'flex', alignItems: 'center', mr: 3 }}>
                      <Comment fontSize="small" sx={{ mr: 0.5, color: 'text.secondary' }} />
                      <Typography variant="body2" color="text.secondary">
                        {post.comments}
                      </Typography>
                    </Box>
                    <Box sx={{ display: 'flex', alignItems: 'center' }}>
                      <Visibility fontSize="small" sx={{ mr: 0.5, color: 'text.secondary' }} />
                      <Typography variant="body2" color="text.secondary">
                        {post.views}
                      </Typography>
                    </Box>
                  </Box>
                  
                  <Button 
                    component={Link}
                    to={`/forum/${post.id}`}
                    size="small" 
                    color="primary"
                  >
                    Read More
                  </Button>
                </Box>
              </CardContent>
            </Card>
          ))}
        </Box>
      ) : (
        <Box sx={{ textAlign: 'center', py: 5 }}>
          <Typography variant="h6" gutterBottom>
            No posts found matching your criteria
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Try adjusting your search filters or create a new post
          </Typography>
          <Button 
            variant="contained" 
            color="primary" 
            component={Link}
            to="/forum/new"
            startIcon={<Add />}
            sx={{ mt: 2 }}
          >
            Ask a Question
          </Button>
        </Box>
      )}

      {/* Pagination */}
      {pagesCount > 1 && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4, mb: 2 }}>
          <Pagination 
            count={pagesCount} 
            page={page} 
            onChange={(event, value) => setPage(value)}
            color="primary"
            showFirstButton
            showLastButton
          />
        </Box>
      )}
    </Box>
  );
};

export default Forum;
