import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { 
  Box, Typography, Grid, Card, CardContent, CardMedia, CardActionArea, 
  Chip, TextField, InputAdornment, FormControl, InputLabel, Select, MenuItem,
  Pagination, Divider
} from '@mui/material';
import { Search, FilterList } from '@mui/icons-material';

const Modules = () => {
  // State for filters and pagination
  const [searchQuery, setSearchQuery] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('all');
  const [levelFilter, setLevelFilter] = useState('all');
  const [page, setPage] = useState(1);
  
  // Enhanced modules data with realistic Verilog content
  const modules = [
    {
      id: 1,
      title: 'Verilog Fundamentals',
      description: 'Master the basics of Verilog HDL including syntax, data types, operators, and module structure. Learn how to create your first Verilog module with inputs and outputs.',
      image: 'https://via.placeholder.com/300x160?text=Verilog+Fundamentals',
      level: 'Beginner',
      category: 'Fundamentals',
      lessons: 10,
      exercises: 8,
      rating: 4.9,
      students: 1520,
      topics: ['Syntax Basics', 'Data Types', 'Operators', 'Module Structure', 'Simulation Basics']
    },
    {
      id: 2,
      title: 'Combinational Circuits',
      description: 'Design and implement combinational logic circuits using Verilog, including decoders, multiplexers, adders, and comparators. Learn efficient coding techniques for synthesis.',
      image: 'https://via.placeholder.com/300x160?text=Combinational+Circuits',
      level: 'Intermediate',
      category: 'Digital Design',
      lessons: 12,
      exercises: 10,
      rating: 4.7,
      students: 1182,
      topics: ['Boolean Logic', 'Multiplexers', 'Decoders', 'Adders', 'Comparators']
    },
    {
      id: 3,
      title: 'Sequential Circuits',
      description: 'Master sequential logic design with flip-flops, latches, registers, and counters. Understand the differences between blocking and non-blocking assignments.',
      image: 'https://via.placeholder.com/300x160?text=Sequential+Circuits',
      level: 'Intermediate',
      category: 'Digital Design',
      lessons: 14,
      exercises: 12,
      rating: 4.8,
      students: 956,
      topics: ['Flip-Flops', 'Latches', 'Counters', 'Shift Registers', 'Memory Elements']
    },
    {
      id: 4,
