import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { 
  Box, Typography, Button, Grid, Card, CardContent, Breadcrumbs, 
  Tabs, Tab, List, ListItem, ListItemIcon, ListItemText, Divider, 
  Chip, Rating, Avatar, LinearProgress, Paper 
} from '@mui/material';
import { 
  PlayArrow, Assignment, MenuBook, Check, PlayCircleOutline, 
  BarChart, People, Schedule, AutoStories, ArrowBack, BookmarkAdd 
} from '@mui/icons-material';

const ModuleDetail = () => {
  const { id } = useParams();
  const [tabValue, setTabValue] = useState(0);
  const [module, setModule] = useState(null);
  const [loading, setLoading] = useState(true);

  // Fetch module data
  useEffect(() => {
    // Simulating API fetch
    setTimeout(() => {
      // This would normally be an API call
      const moduleData = {
        id: parseInt(id),
        title: 'Introduction to Verilog',
        description: 'A comprehensive introduction to Verilog hardware description language. Learn the fundamentals of digital design using Verilog, from basic syntax to advanced concepts.',
        image: 'https://via.placeholder.com/800x400?text=Verilog+Introduction',
        level: 'Beginner',
        category: 'Fundamentals',
        duration: '6 hours',
        instructor: {
          name: 'Dr. Jane Smith',
          bio: 'Professor of Electrical Engineering with 15 years of experience in digital design',
          avatar: 'https://via.placeholder.com/150'
        },
        rating: 4.8,
        studentsCount: 1250,
        updatedAt: '2023-09-15',
        completion: 0,
        prerequisites: [
          'Basic understanding of digital logic',
          'Familiarity with programming concepts',
          'No prior Verilog experience required'
        ],
        overview: 'This module covers the fundamental concepts of Verilog, starting from the basic syntax and moving toward more complex design concepts. You will learn how to describe digital circuits using Verilog constructs, understand the difference between behavioral, dataflow, and structural modeling, and implement basic combinational and sequential circuits.',
        lessons: [
          {
            id: 1,
            title: 'Introduction to HDLs and Verilog',
            duration: '30 min',
            type: 'video',
            completed: false
          },
          {
            id: 2,
            title: 'Verilog Syntax and Basic Constructs',
            duration: '45 min',
            type: 'video',
            completed: false
          },
          {
            id: 3,
            title: 'Data Types and Operators',
            duration: '40 min',
            type: 'video',
            completed: false
          },
          {
            id: 4,
            title: 'Behavioral Modeling',
            duration: '50 min',
            type: 'video',
            completed: false
          },
          {
            id: 5,
            title: 'Dataflow Modeling',
            duration: '45 min',
            type: 'video',
            completed: false
          },
          {
            id: 6,
            title: 'Structural Modeling',
            duration: '55 min',
            type: 'video',
            completed: false
          },
          {
            id: 7,
            title: 'Module and Port Declarations',
            duration: '40 min',
            type: 'video',
            completed: false
          },
          {
            id: 8,
            title: 'Course Project: Basic Calculator',
            duration: '60 min',
            type: 'project',
            completed: false
          }
        ],
        exercises: [
          {
            id: 101,
            title: 'Basic Logic Gates Implementation',
            difficulty: 'Easy',
            type: 'coding',
            completed: false
          },
          {
            id: 102,
            title: 'Creating a 2-to-1 Multiplexer',
            difficulty: 'Easy',
            type: 'coding',
            completed: false
          },
          {
            id: 103,
            title: 'Implementing a Full Adder',
            difficulty: 'Medium',
            type: 'coding',
            completed: false
          },
          {
            id: 104,
            title: '4-bit Ripple Carry Adder',
            difficulty: 'Medium',
            type: 'coding',
            completed: false
          },
          {
            id: 105,
            title: 'Simple D Flip-Flop',
            difficulty: 'Hard',
            type: 'coding',
            completed: false
          }
        ],
        relatedModules: [
          {
            id: 2,
            title: 'Combinational Circuits',
            level: 'Intermediate'
          },
          {
            id: 3,
            title: 'Sequential Circuits',
            level: 'Intermediate'
          }
        ]
      };
      
      setModule(moduleData);
      setLoading(false);
    }, 800);
  }, [id]);

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  if (loading) {
    return (
      <Box className="container page-container" sx={{ textAlign: 'center', py: 8 }}>
        <Typography variant="h5" sx={{ mb: 4 }}>Loading module content...</Typography>
        <LinearProgress color="primary" sx={{ mx: 'auto', width: '50%' }} />
      </Box>
    );
  }

  if (!module) {
    return (
      <Box className="container page-container" sx={{ textAlign: 'center', py: 8 }}>
        <Typography variant="h5">Module not found</Typography>
        <Button component={Link} to="/modules" sx={{ mt: 2 }}>
          Back to Modules
        </Button>
      </Box>
    );
  }

  return (
    <Box className="container page-container">
      {/* Breadcrumb */}
      <Breadcrumbs sx={{ mb: 2 }}>
        <Link to="/" style={{ textDecoration: 'none', color: 'inherit' }}>
          Home
        </Link>
        <Link to="/modules" style={{ textDecoration: 'none', color: 'inherit' }}>
          Modules
        </Link>
        <Typography color="text.primary">{module.title}</Typography>
      </Breadcrumbs>

      <Button 
        component={Link} 
        to="/modules" 
        startIcon={<ArrowBack />} 
        sx={{ mb: 3 }}
      >
        Back to Modules
      </Button>

      {/* Module Header */}
      <Box sx={{ position: 'relative', mb: 4 }}>
        <Box
          component="img"
          src={module.image}
          alt={module.title}
          sx={{ 
            width: '100%', 
            height: '300px', 
            objectFit: 'cover', 
            borderRadius: 2,
            filter: 'brightness(0.7)',
          }}
        />
        <Box 
          sx={{ 
            position: 'absolute', 
            bottom: 0, 
            left: 0, 
            right: 0, 
            p: 3, 
            color: 'white',
            background: 'linear-gradient(to top, rgba(0,0,0,0.7), transparent)'
          }}
        >
          <Box sx={{ display: 'flex', gap: 1, mb: 1 }}>
            <Chip 
              label={module.level} 
              size="small" 
              sx={{ backgroundColor: 'rgba(255, 255, 255, 0.2)', color: 'white' }} 
            />
            <Chip 
              label={module.category} 
              size="small" 
              sx={{ backgroundColor: 'rgba(255, 255, 255, 0.2)', color: 'white' }} 
            />
          </Box>
          <Typography variant="h3" component="h1" gutterBottom fontWeight="bold">
            {module.title}
          </Typography>
          <Typography variant="subtitle1">
            {module.description}
          </Typography>
        </Box>
      </Box>

      {/* Module Stats */}
      <Grid container spacing={2} sx={{ mb: 4 }}>
        <Grid item xs={6} sm={3}>
          <Paper sx={{ p: 2, height: '100%', display: 'flex', alignItems: 'center' }}>
            <People sx={{ color: 'primary.main', mr: 1 }} />
            <Box>
              <Typography variant="body2" color="text.secondary">Students</Typography>
              <Typography variant="h6">{module.studentsCount.toLocaleString()}</Typography>
            </Box>
          </Paper>
        </Grid>
        <Grid item xs={6} sm={3}>
          <Paper sx={{ p: 2, height: '100%', display: 'flex', alignItems: 'center' }}>
            <Schedule sx={{ color: 'primary.main', mr: 1 }} />
            <Box>
              <Typography variant="body2" color="text.secondary">Duration</Typography>
              <Typography variant="h6">{module.duration}</Typography>
            </Box>
          </Paper>
        </Grid>
        <Grid item xs={6} sm={3}>
          <Paper sx={{ p: 2, height: '100%', display: 'flex', alignItems: 'center' }}>
            <AutoStories sx={{ color: 'primary.main', mr: 1 }} />
            <Box>
              <Typography variant="body2" color="text.secondary">Lessons</Typography>
              <Typography variant="h6">{module.lessons.length}</Typography>
            </Box>
          </Paper>
        </Grid>
        <Grid item xs={6} sm={3}>
          <Paper sx={{ p: 2, height: '100%', display: 'flex', alignItems: 'center' }}>
            <Assignment sx={{ color: 'primary.main', mr: 1 }} />
            <Box>
              <Typography variant="body2" color="text.secondary">Exercises</Typography>
              <Typography variant="h6">{module.exercises.length}</Typography>
            </Box>
          </Paper>
        </Grid>
      </Grid>

      {/* Progress and Start buttons */}
      <Grid container spacing={2} sx={{ mb: 4 }}>
        <Grid item xs={12} md={8}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="body2" color="text.secondary" gutterBottom>
              Your Progress
            </Typography>
            <LinearProgress 
              variant="determinate" 
              value={module.completion} 
              sx={{ height: 10, borderRadius: 5, mb: 1 }} 
            />
            <Typography variant="body2">
              {module.completion}% Complete
            </Typography>
          </Paper>
        </Grid>
        <Grid item xs={12} md={4}>
          <Box sx={{ display: 'flex', gap: 2, height: '100%' }}>
            <Button 
              variant="contained" 
              color="primary" 
              fullWidth 
              startIcon={<PlayArrow />}
              component={Link}
              to={`/modules/${id}/lessons/1`}
              sx={{ height: '100%' }}
            >
              {module.completion > 0 ? 'Continue Learning' : 'Start Module'}
            </Button>
            <Button 
              variant="outlined" 
              color="primary"
              startIcon={<BookmarkAdd />}
              sx={{ flexShrink: 0 }}
            >
              Save
            </Button>
          </Box>
        </Grid>
      </Grid>

      {/* Tabs and Content */}
      <Box sx={{ mb: 4 }}>
        <Tabs 
          value={tabValue} 
          onChange={handleTabChange} 
          sx={{ 
            borderBottom: 1, 
            borderColor: 'divider',
            mb: 3,
            '& .MuiTab-root': {
              textTransform: 'none',
              fontSize: '1rem',
              fontWeight: 500
            }
          }}
        >
          <Tab label="Overview" icon={<MenuBook />} iconPosition="start" />
          <Tab label="Content" icon={<PlayCircleOutline />} iconPosition="start" />
          <Tab label="Exercises" icon={<Assignment />} iconPosition="start" />
          <Tab label="Instructor" icon={<People />} iconPosition="start" />
        </Tabs>

        {/* Overview Tab */}
        {tabValue === 0 && (
          <Box>
            <Typography variant="h5" gutterBottom>About This Module</Typography>
            <Typography paragraph>{module.overview}</Typography>
            
            <Typography variant="h6" gutterBottom sx={{ mt: 3 }}>Prerequisites</Typography>
            <List>
              {module.prerequisites.map((prereq, index) => (
                <ListItem key={index}>
                  <ListItemIcon sx={{ minWidth: 36 }}>
                    <Check color="primary" />
                  </ListItemIcon>
                  <ListItemText primary={prereq} />
                </ListItem>
              ))}
            </List>

            <Typography variant="h6" gutterBottom sx={{ mt: 3 }}>Skills You'll Learn</Typography>
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mb: 3 }}>
              <Chip label="Verilog Syntax" />
              <Chip label="Digital Design" />
              <Chip label="Hardware Description" />
              <Chip label="Module Creation" />
              <Chip label="Logic Gates" />
              <Chip label="Combinational Circuits" />
              <Chip label="Sequential Logic" />
            </Box>

            {module.relatedModules.length > 0 && (
              <>
                <Typography variant="h6" gutterBottom sx={{ mt: 3 }}>Related Modules</Typography>
                <Grid container spacing={2}>
                  {module.relatedModules.map(relModule => (
                    <Grid item xs={12} sm={6} md={4} key={relModule.id}>
                      <Card variant="outlined">
                        <CardContent>
                          <Typography variant="subtitle1" component="h3">
                            {relModule.title}
                          </Typography>
                          <Typography variant="caption" color="text.secondary">
                            {relModule.level}
                          </Typography>
                          <Button 
                            component={Link} 
                            to={`/modules/${relModule.id}`}
                            size="small" 
                            sx={{ mt: 1 }}
                          >
                            View Module
                          </Button>
                        </CardContent>
                      </Card>
                    </Grid>
                  ))}
                </Grid>
              </>
            )}
          </Box>
        )}

        {/* Content Tab */}
        {tabValue === 1 && (
          <Box>
            <Typography variant="h5" gutterBottom>Module Content</Typography>
            <Typography paragraph>
              This module contains {module.lessons.length} lessons with a total duration of {module.duration}.
            </Typography>
            
            <List sx={{ bgcolor: 'background.paper' }}>
              {module.lessons.map((lesson, index) => (
                <React.Fragment key={lesson.id}>
                  <ListItem 
                    component={Link} 
                    to={`/modules/${id}/lessons/${lesson.id}`}
                    sx={{ 
                      textDecoration: 'none', 
                      color: 'inherit',
                      py: 2,
                      borderRadius: 1,
                      '&:hover': {
                        backgroundColor: 'rgba(0, 0, 0, 0.04)'
                      }
                    }}
                  >
                    <ListItemIcon>
                      {lesson.type === 'video' ? 
                        <PlayCircleOutline color="primary" /> : 
                        <Assignment color="primary" />
                      }
                    </ListItemIcon>
                    <ListItemText 
                      primary={
                        <Box sx={{ display: 'flex', alignItems: 'center' }}>
                          <Typography component="span" sx={{ mr: 1 }}>
                            {index + 1}. {lesson.title}
                          </Typography>
                          {lesson.completed && <Check fontSize="small" color="success" />}
                        </Box>
                      } 
                      secondary={
                        <Box sx={{ display: 'flex', alignItems: 'center', mt: 0.5 }}>
                          <Chip 
                            label={lesson.type} 
                            size="small"
                            sx={{ 
                              mr: 1,
                              textTransform: 'capitalize',
                              bgcolor: lesson.type === 'video' ? '#e3f2fd' : '#fff8e1'
                            }} 
                          />
                          <Typography variant="body2" color="text.secondary">
                            {lesson.duration}
                          </Typography>
                        </Box>
                      }
                    />
                  </ListItem>
                  {index < module.lessons.length - 1 && <Divider component="li" />}
                </React.Fragment>
              ))}
            </List>
          </Box>
        )}

        {/* Exercises Tab */}
        {tabValue === 2 && (
          <Box>
            <Typography variant="h5" gutterBottom>Practical Exercises</Typography>
            <Typography paragraph>
              Test your knowledge with these hands-on exercises. Complete them to reinforce your learning.
            </Typography>
            
            <List sx={{ bgcolor: 'background.paper' }}>
              {module.exercises.map((exercise, index) => (
                <React.Fragment key={exercise.id}>
                  <ListItem 
                    component={Link} 
                    to={`/modules/${id}/exercises/${exercise.id}`}
                    sx={{ 
                      textDecoration: 'none', 
                      color: 'inherit',
                      py: 2,
                      borderRadius: 1,
                      '&:hover': {
                        backgroundColor: 'rgba(0, 0, 0, 0.04)'
                      }
                    }}
                  >
                    <ListItemIcon>
                      <Assignment color="primary" />
                    </ListItemIcon>
                    <ListItemText 
                      primary={
                        <Box sx={{ display: 'flex', alignItems: 'center' }}>
                          <Typography component="span" sx={{ mr: 1 }}>
                            {exercise.title}
                          </Typography>
                          {exercise.completed && <Check fontSize="small" color="success" />}
                        </Box>
                      } 
                      secondary={
                        <Box sx={{ display: 'flex', alignItems: 'center', mt: 0.5 }}>
                          <Chip 
                            label={exercise.difficulty} 
                            size="small"
                            sx={{ 
                              mr: 1,
                              bgcolor: 
                                exercise.difficulty === 'Easy' ? '#e8f5e9' : 
                                exercise.difficulty === 'Medium' ? '#fff8e1' : '#ffebee'
                            }} 
                          />
                          <Chip 
                            label={exercise.type} 
                            size="small"
                            sx={{ 
                              textTransform: 'capitalize'
                            }} 
                          />
                        </Box>
                      }
                    />
                  </ListItem>
                  {index < module.exercises.length - 1 && <Divider component="li" />}
                </React.Fragment>
              ))}
            </List>
          </Box>
        )}

        {/* Instructor Tab */}
        {tabValue === 3 && (
          <Box>
            <Grid container spacing={3}>
              <Grid item xs={12} md={4} sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                <Avatar 
                  src={module.instructor.avatar} 
                  alt={module.instructor.name}
                  sx={{ width: 150, height: 150, mb: 2 }}
                />
                <Typography variant="h5" gutterBottom>
                  {module.instructor.name}
                </Typography>
                <Rating value={module.rating} precision={0.1} readOnly sx={{ mb: 1 }} />
                <Typography variant="body2" color="text.secondary">
                  Module rating: {module.rating}/5
                </Typography>
              </Grid>
              <Grid item xs={12} md={8}>
                <Typography variant="h6" gutterBottom>About the Instructor</Typography>
                <Typography paragraph>
                  {module.instructor.bio}
                </Typography>
                <Button variant="outlined" color="primary">
                  View All Courses by This Instructor
                </Button>
              </Grid>
            </Grid>
          </Box>
        )}
      </Box>
    </Box>
  );
};

export default ModuleDetail;
