import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Box, Typography, Button, Grid, Paper, Tabs, Tab, Snackbar, Alert } from '@mui/material';
import { PlayArrow, Save, ArrowBack, LightbulbOutlined } from '@mui/icons-material';

const CodeEditor = () => {
  const [code, setCode] = useState(
`module full_adder(
  input a, b, cin,
  output sum, cout
);
  // Your implementation here
  
endmodule`);
  const [output, setOutput] = useState('');
  const [isCompiling, setIsCompiling] = useState(false);
  const [tabValue, setTabValue] = useState(0);
  const [notification, setNotification] = useState({ open: false, message: '', severity: 'info' });

  // Mock exercise data
  const exerciseData = {
    id: '103',
    title: 'Implementing a Full Adder',
    description: 'Create a Verilog module for a full adder with inputs a, b, cin and outputs sum and cout.',
    testCases: [
      { inputs: 'a=0, b=0, cin=0', expectedOutputs: 'sum=0, cout=0' },
      { inputs: 'a=1, b=1, cin=1', expectedOutputs: 'sum=1, cout=1' }
    ],
    hints: ['Try using XOR gates for the sum and AND/OR gates for the carry.']
  };

  const handleTabChange = (_, newValue) => setTabValue(newValue);

  const handleRunCode = () => {
    setIsCompiling(true);
    setOutput('');
    
    // Simulate compilation and running
    setTimeout(() => {
      if (code.includes('assign') || code.includes('wire')) {
        setOutput(`
Compilation successful!

Running test cases:
Test Case 1: a=0, b=0, cin=0
  Expected: sum=0, cout=0
  Actual:   sum=0, cout=0
  Result:   âœ… PASSED

Test Case 2: a=1, b=1, cin=1
  Expected: sum=1, cout=1
  Actual:   sum=1, cout=1
  Result:   âœ… PASSED

Summary: 2/2 test cases passed.
ðŸŽ‰ Congratulations! All tests passed.
`);
        setNotification({
          open: true,
          message: 'All tests passed! Great job!',
          severity: 'success'
        });
      } else {
        setOutput(`
Compilation Error:
Error: No implementation found.
Please implement the full adder logic before running the code.
`);
        setNotification({
          open: true,
          message: 'Compilation failed. Please check your code.',
          severity: 'error'
        });
      }
      
      setIsCompiling(false);
    }, 1000);
  };

  const handleSaveCode = () => {
    setNotification({
      open: true,
      message: 'Code saved successfully!',
      severity: 'success'
    });
  };

  const handleCloseNotification = () => {
    setNotification({ ...notification, open: false });
  };

  const handleShowHint = () => {
    setNotification({
      open: true,
      message: `Hint: ${exerciseData.hints[0]}`,
      severity: 'info'
    });
  };

  return (
    <Box className="container page-container">
      {/* Header */}
      <Box sx={{ mb: 3 }}>
        <Button 
          component={Link}
          to="/modules/1"
          startIcon={<ArrowBack />}
          sx={{ mb: 2 }}
        >
          Back to Module
        </Button>
        <Typography variant="h4" component="h1" gutterBottom color="primary">
          {exerciseData.title}
        </Typography>
        <Typography variant="subtitle1" sx={{ mb: 2 }}>
          {exerciseData.description}
        </Typography>
      </Box>

      {/* Test Cases */}
      <Paper elevation={2} sx={{ p: 2, mb: 3 }}>
        <Typography variant="h6" gutterBottom>Test Cases</Typography>
        <Grid container spacing={2}>
          {exerciseData.testCases.map((testCase, index) => (
            <Grid item xs={12} sm={6} key={index}>
              <Paper variant="outlined" sx={{ p: 1, typography: 'body2', fontFamily: 'monospace' }}>
                <strong>Test {index + 1}:</strong><br />
                {testCase.inputs}<br />
                Expected: {testCase.expectedOutputs}
              </Paper>
            </Grid>
          ))}
        </Grid>
      </Paper>
      
      {/* Editor and Output */}
      <Grid container spacing={2}>
        {/* Editor Section */}
        <Grid item xs={12} md={7}>
          <Paper elevation={3} sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
            <Box sx={{ p: 2, borderBottom: 1, borderColor: 'divider' }}>
              <Typography variant="h6">Verilog Editor</Typography>
            </Box>
            <Box sx={{ flexGrow: 1, p: 2, backgroundColor: '#f5f5f5', minHeight: '400px' }}>
              <textarea
                value={code}
                onChange={(e) => setCode(e.target.value)}
                style={{
                  width: '100%',
                  height: '380px',
                  fontFamily: 'monospace',
                  fontSize: '14px',
                  padding: '8px',
                  border: '1px solid #ccc',
                  borderRadius: '4px',
                  resize: 'none',
                  backgroundColor: 'white'
                }}
              />
            </Box>
            <Box sx={{ p: 2, borderTop: 1, borderColor: 'divider', display: 'flex', gap: 1 }}>
              <Button 
                variant="contained" 
                color="primary" 
                startIcon={<PlayArrow />}
                onClick={handleRunCode}
                disabled={isCompiling}
              >
                {isCompiling ? 'Running...' : 'Run Code'}
              </Button>
              <Button 
                variant="outlined" 
                startIcon={<Save />}
                onClick={handleSaveCode}
              >
                Save
              </Button>
              <Button 
                variant="outlined" 
                color="secondary"
                startIcon={<LightbulbOutlined />}
                onClick={handleShowHint}
                sx={{ ml: 'auto' }}
              >
                Hint
              </Button>
            </Box>
          </Paper>
        </Grid>
        
        {/* Output Section */}
        <Grid item xs={12} md={5}>
          <Paper elevation={3} sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
            <Tabs 
              value={tabValue} 
              onChange={handleTabChange}
              sx={{ borderBottom: 1, borderColor: 'divider' }}
            >
              <Tab label="Output" />
              <Tab label="Console" />
            </Tabs>
            <Box 
              sx={{ 
                flexGrow: 1, 
                p: 2, 
                fontFamily: 'monospace', 
                fontSize: '14px',
                backgroundColor: '#f8f8f8',
                overflowY: 'auto',
                whiteSpace: 'pre-wrap',
                minHeight: '400px'
              }}
            >
              {isCompiling ? 
                'Compiling and running your code...' : 
                (tabValue === 0 ? 
                  (output || 'Run your code to see the output here.') : 
                  'Console output will appear here when you run your code.'
                )
              }
            </Box>
          </Paper>
        </Grid>
      </Grid>

      {/* Notification */}
      <Snackbar 
        open={notification.open} 
        autoHideDuration={6000} 
        onClose={handleCloseNotification}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
      >
        <Alert 
          onClose={handleCloseNotification} 
          severity={notification.severity} 
          sx={{ width: '100%' }}
        >
          {notification.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default CodeEditor;
