import React from 'react';
import { Routes, Route } from 'react-router-dom';
import { Box, CssBaseline } from '@mui/material';
import './styles/index.css';

// Import components here when you create them

function App() {
  return (
    <>
      <CssBaseline />
      <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
        {/* Header will go here */}
        <Box component="main" sx={{ flexGrow: 1 }}>
          <Routes>
            <Route path="/" element={<div className="container page-container">
              <h1 style={{ color: '#6a0dad' }}>Verilog Learning Platform</h1>
              <p>Welcome to the Verilog Learning Platform! This platform is currently under development.</p>
              <p>This will be a comprehensive resource for learning Verilog with interactive exercises.</p>
              <div className="code-editor" style={{ padding: '20px', backgroundColor: '#f8f8f8', margin: '20px 0' }}>
                <pre>{`module example(
  input a, b,
  output y
);
  assign y = a & b;
endmodule`}</pre>
              </div>
            </div>} />
            {/* Add more routes here as you develop */}
          </Routes>
        </Box>
        {/* Footer will go here */}
      </Box>
    </>
  );
}

export default App;
