import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Box, Typography, Button, Grid, Paper, Tabs, Tab, Snackbar, Alert, List, ListItem, ListItemIcon, ListItemText } from '@mui/material';
import { PlayArrow, Save, ArrowBack, LightbulbOutlined, BarChart, Check, MenuBook, Assignment } from '@mui/icons-material';

const CodeEditor = () => {
  const [code, setCode] = useState(
`module full_adder(
  input wire a, b, cin,
  output wire sum, cout
);
  // Implement full adder logic
  // sum = a XOR b XOR cin
  // cout = (a AND b) OR (cin AND (a XOR b))
  
  // Internal wire for a XOR b
  wire a_xor_b;
  
  // Calculate sum
  assign a_xor_b = a ^ b;
  assign sum = a_xor_b ^ cin;
  
  // Calculate carry out
  assign cout = (a & b) | (cin & a_xor_b);
endmodule

// Testbench (uncomment to simulate)
/*
module full_adder_tb;
  // Testbench signals
  reg a, b, cin;
  wire sum, cout;
  
  // Instantiate the full_adder
  full_adder dut(
    .a(a),
    .b(b),
    .cin(cin),
    .sum(sum),
    .cout(cout)
  );
  
  // Test stimulus
  initial begin
    // Display header
    $display("a b cin | sum cout");
    $display("--------+---------");
    
    // Test all input combinations
    {a, b, cin} = 3'b000; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    {a, b, cin} = 3'b001; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    {a, b, cin} = 3'b010; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    {a, b, cin} = 3'b011; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    {a, b, cin} = 3'b100; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    {a, b, cin} = 3'b101; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    {a, b, cin} = 3'b110; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    {a, b, cin} = 3'b111; #10;
    $display("%b %b %b  |  %b   %b", a, b, cin, sum, cout);
    
    $finish;
  end
endmodule
*/`);
  const [output, setOutput] = useState('');
  const [isCompiling, setIsCompiling] = useState(false);
  const [tabValue, setTabValue] = useState(0);
  const [notification, setNotification] = useState({ open: false, message: '', severity: 'info' });

  // Mock exercise data
  const exerciseData = {
    id: '103',
    title: 'Implementing a Full Adder',
    description: 'Create a Verilog module for a full adder with inputs a, b, cin and outputs sum and cout. A full adder adds three bits and produces a sum bit and a carry bit.',
    objectives: [
      'Understand the operation of a full adder circuit',
      'Implement combinational logic using Verilog assign statements',
      'Utilize internal wires for intermediate signals',
      'Apply proper port declarations with the wire data type'
    ],
    background: 'A full adder is a fundamental building block in digital circuit design, used to add two binary digits along with a carry input. It produces a sum output and a carry output, which can be cascaded to create multi-bit adders.',
    testCases: [
      { inputs: 'a=0, b=0, cin=0', expectedOutputs: 'sum=0, cout=0', explanation: 'With all inputs 0, both sum and carry are 0' },
      { inputs: 'a=1, b=0, cin=0', expectedOutputs: 'sum=1, cout=0', explanation: 'Adding just a single 1 gives sum=1, with no carry' },
      { inputs: 'a=0, b=1, cin=0', expectedOutputs: 'sum=1, cout=0', explanation: 'Adding just a single 1 gives sum=1, with no carry' },
      { inputs: 'a=0, b=0, cin=1', expectedOutputs: 'sum=1, cout=0', explanation: 'Adding just a single 1 gives sum=1, with no carry' },
      { inputs: 'a=1, b=1, cin=0', expectedOutputs: 'sum=0, cout=1', explanation: 'Adding two 1s results in 0 with a carry of 1 (binary 10)' },
      { inputs: 'a=1, b=0, cin=1', expectedOutputs: 'sum=0, cout=1', explanation: 'Adding two 1s results in 0 with a carry of 1 (binary 10)' },
      { inputs: 'a=0, b=1, cin=1', expectedOutputs: 'sum=0, cout=1', explanation: 'Adding two 1s results in 0 with a carry of 1 (binary 10)' },
      { inputs: 'a=1, b=1, cin=1', expectedOutputs: 'sum=1, cout=1', explanation: 'Adding three 1s results in 1 with a carry of 1 (binary 11)' }
    ],
    hints: [
      'Remember the boolean equations for a full adder: sum = a âŠ• b âŠ• cin and cout = (aÂ·b) + (cinÂ·(aâŠ•b))',
      'Use intermediate wires to make your code more readable',
      'The XOR operator in Verilog is represented by the caret symbol (^)',
      'Use the & operator for AND and | operator for OR operations'
    ],
    truthTable: [
      { a: 0, b: 0, cin: 0, sum: 0, cout: 0 },
      { a: 0, b: 0, cin: 1, sum: 1, cout: 0 },
      { a: 0, b: 1, cin: 0, sum: 1, cout: 0 },
      { a: 0, b: 1, cin: 1, sum: 0, cout: 1 },
      { a: 1, b: 0, cin: 0, sum: 1, cout: 0 },
      { a: 1, b: 0, cin: 1, sum: 0, cout: 1 },
      { a: 1, b: 1, cin: 0, sum: 0, cout: 1 },
      { a: 1, b: 1, cin: 1, sum: 1, cout: 1 }
    ]
  };

  const handleTabChange = (_, newValue) => setTabValue(newValue);

  const handleRunCode = () => {
    setIsCompiling(true);
    setOutput('');
    
    // Simulate compilation and running
    setTimeout(() => {
      if (code.includes('assign') || code.includes('wire')) {
        setOutput(`
Compilation successful!

Running test cases:
Test Case 1: a=0, b=0, cin=0
  Expected: sum=0, cout=0
  Actual:   sum=0, cout=0
  Result:   âœ… PASSED

Test Case 2: a=1, b=1, cin=1
  Expected: sum=1, cout=1
  Actual:   sum=1, cout=1
  Result:   âœ… PASSED

Summary: 2/2 test cases passed.
ðŸŽ‰ Congratulations! All tests passed.
`);
        setNotification({
          open: true,
          message: 'All tests passed! Great job!',
          severity: 'success'
        });
      } else {
        setOutput(`
Compilation Error:
Error: No implementation found.
Please implement the full adder logic before running the code.
`);
        setNotification({
          open: true,
          message: 'Compilation failed. Please check your code.',
          severity: 'error'
        });
      }
      
      setIsCompiling(false);
    }, 1000);
  };

  const handleSaveCode = () => {
    setNotification({
      open: true,
      message: 'Code saved successfully!',
      severity: 'success'
    });
  };

  const handleCloseNotification = () => {
    setNotification({ ...notification, open: false });
  };

  const handleShowHint = () => {
    setNotification({
      open: true,
      message: `Hint: ${exerciseData.hints[0]}`,
      severity: 'info'
    });
  };

  return (
    <Box className="container page-container">
      {/* Header */}
      <Box sx={{ mb: 3 }}>
        <Button 
          component={Link}
          to="/modules/1"
          startIcon={<ArrowBack />}
          sx={{ mb: 2 }}
        >
          Back to Module
        </Button>
        <Typography variant="h4" component="h1" gutterBottom color="primary" sx={{ fontWeight: 'bold' }}>
          {exerciseData.title}
        </Typography>
        <Typography variant="subtitle1" sx={{ mb: 3, fontSize: '1.1rem' }}>
          {exerciseData.description}
        </Typography>
        
        <Grid container spacing={4} sx={{ mb: 4 }}>
          {/* Objectives */}
          <Grid item xs={12} md={6}>
            <Paper elevation={0} variant="outlined" sx={{ p: 3, height: '100%', borderRadius: 2 }}>
              <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center' }}>
                <BarChart color="primary" sx={{ mr: 1 }} /> Learning Objectives
              </Typography>
              <List dense disablePadding>
                {exerciseData.objectives.map((objective, index) => (
                  <ListItem key={index}>
                    <ListItemIcon sx={{ minWidth: 36 }}>
                      <Check color="primary" />
                    </ListItemIcon>
                    <ListItemText primary={objective} />
                  </ListItem>
                ))}
              </List>
            </Paper>
          </Grid>
          
          {/* Background */}
          <Grid item xs={12} md={6}>
            <Paper elevation={0} variant="outlined" sx={{ p: 3, height: '100%', borderRadius: 2 }}>
              <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center' }}>
                <MenuBook color="primary" sx={{ mr: 1 }} /> Background
              </Typography>
              <Typography>{exerciseData.background}</Typography>
              
              {/* Truth Table */}
              <Typography variant="subtitle1" sx={{ mt: 2, mb: 1, fontWeight: 'bold' }}>
                Truth Table:
              </Typography>
              <Box sx={{ overflowX: 'auto' }}>
                <table style={{ 
                  width: '100%', 
                  borderCollapse: 'collapse', 
                  textAlign: 'center',
                  fontSize: '0.9rem',
                  border: '1px solid #e0e0e0'
                }}>
                  <thead>
                    <tr style={{ backgroundColor: '#f5f5f5' }}>
                      <th style={{ padding: '8px', border: '1px solid #e0e0e0' }}>a</th>
                      <th style={{ padding: '8px', border: '1px solid #e0e0e0' }}>b</th>
                      <th style={{ padding: '8px', border: '1px solid #e0e0e0' }}>cin</th>
                      <th style={{ padding: '8px', border: '1px solid #e0e0e0' }}>sum</th>
                      <th style={{ padding: '8px', border: '1px solid #e0e0e0' }}>cout</th>
                    </tr>
                  </thead>
                  <tbody>
                    {exerciseData.truthTable.map((row, index) => (
                      <tr key={index} style={{ backgroundColor: index % 2 === 0 ? 'white' : '#fafafa' }}>
                        <td style={{ padding: '8px', border: '1px solid #e0e0e0' }}>{row.a}</td>
                        <td style={{ padding: '8px', border: '1px solid #e0e0e0' }}>{row.b}</td>
                        <td style={{ padding: '8px', border: '1px solid #e0e0e0' }}>{row.cin}</td>
                        <td style={{ padding: '8px', border: '1px solid #e0e0e0', backgroundColor: '#e8f5e9' }}>{row.sum}</td>
                        <td style={{ padding: '8px', border: '1px solid #e0e0e0', backgroundColor: '#e8f5e9' }}>{row.cout}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </Box>
            </Paper>
          </Grid>
        </Grid>
      </Box>
      
      {/* Test Cases */}
      <Paper elevation={2} sx={{ p: 3, mb: 4, borderRadius: 2 }}>
        <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center' }}>
          <Assignment color="primary" sx={{ mr: 1 }} /> Test Cases
        </Typography>
        <Typography variant="body2" sx={{ mb: 2 }}>
          Your implementation will be validated against these test cases. Make sure your code handles all these scenarios correctly.
        </Typography>
        <Box sx={{ overflowX: 'auto' }}>
          <table style={{ 
            width: '100%', 
            borderCollapse: 'collapse', 
            textAlign: 'left',
            fontSize: '0.9rem',
            border: '1px solid #e0e0e0'
          }}>
            <thead>
              <tr style={{ backgroundColor: '#f5f5f5' }}>
                <th style={{ padding: '10px', border: '1px solid #e0e0e0' }}>Test Case</th>
                <th style={{ padding: '10px', border: '1px solid #e0e0e0' }}>Inputs</th>
                <th style={{ padding: '10px', border: '1px solid #e0e0e0' }}>Expected Outputs</th>
                <th style={{ padding: '10px', border: '1px solid #e0e0e0' }}>Explanation</th>
              </tr>
            </thead>
            <tbody>
              {exerciseData.testCases.map((testCase, index) => (
                <tr key={index} style={{ backgroundColor: index % 2 === 0 ? 'white' : '#fafafa' }}>
                  <td style={{ padding: '10px', border: '1px solid #e0e0e0', fontWeight: 'bold' }}>Case {index + 1}</td>
                  <td style={{ padding: '10px', border: '1px solid #e0e0e0', fontFamily: 'monospace' }}>{testCase.inputs}</td>
                  <td style={{ padding: '10px', border: '1px solid #e0e0e0', fontFamily: 'monospace' }}>{testCase.expectedOutputs}</td>
                  <td style={{ padding: '10px', border: '1px solid #e0e0e0' }}>{testCase.explanation}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </Box>
      </Paper>
      
      {/* Editor and Output */}
      <Grid container spacing={2}>
        {/* Editor Section */}
        <Grid item xs={12} md={7}>
          <Paper elevation={3} sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
            <Box sx={{ p: 2, borderBottom: 1, borderColor: 'divider' }}>
              <Typography variant="h6">Verilog Editor</Typography>
            </Box>
            <Box sx={{ flexGrow: 1, p: 2, backgroundColor: '#f5f5f5', minHeight: '400px' }}>
              <textarea
                value={code}
                onChange={(e) => setCode(e.target.value)}
                style={{
                  width: '100%',
                  height: '380px',
                  fontFamily: 'monospace',
                  fontSize: '14px',
                  padding: '8px',
                  border: '1px solid #ccc',
                  borderRadius: '4px',
                  resize: 'none',
                  backgroundColor: 'white'
                }}
              />
            </Box>
            <Box sx={{ p: 2, borderTop: 1, borderColor: 'divider', display: 'flex', gap: 1 }}>
              <Button 
                variant="contained" 
                color="primary" 
                startIcon={<PlayArrow />}
                onClick={handleRunCode}
                disabled={isCompiling}
              >
                {isCompiling ? 'Running...' : 'Run Code'}
              </Button>
              <Button 
                variant="outlined" 
                startIcon={<Save />}
                onClick={handleSaveCode}
              >
                Save
              </Button>
              <Button 
                variant="outlined" 
                color="secondary"
                startIcon={<LightbulbOutlined />}
                onClick={handleShowHint}
                sx={{ ml: 'auto' }}
              >
                Hint
              </Button>
            </Box>
          </Paper>
        </Grid>
        
        {/* Output Section */}
        <Grid item xs={12} md={5}>
          <Paper elevation={3} sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
            <Tabs 
              value={tabValue} 
              onChange={handleTabChange}
              sx={{ borderBottom: 1, borderColor: 'divider' }}
            >
              <Tab label="Output" />
              <Tab label="Console" />
            </Tabs>
            <Box 
              sx={{ 
                flexGrow: 1, 
                p: 2, 
                fontFamily: 'monospace', 
                fontSize: '14px',
                backgroundColor: '#f8f8f8',
                overflowY: 'auto',
                whiteSpace: 'pre-wrap',
                minHeight: '400px'
              }}
            >
              {isCompiling ? 
                'Compiling and running your code...' : 
                (tabValue === 0 ? 
                  (output || 'Run your code to see the output here.') : 
                  'Console output will appear here when you run your code.'
                )
              }
            </Box>
          </Paper>
        </Grid>
      </Grid>

      {/* Notification */}
      <Snackbar 
        open={notification.open} 
        autoHideDuration={6000} 
        onClose={handleCloseNotification}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
      >
        <Alert 
          onClose={handleCloseNotification} 
          severity={notification.severity} 
          sx={{ width: '100%' }}
        >
          {notification.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default CodeEditor;
