          title: 'RISC-V Register File Implementation',
          description: 'A parameterized register file with two read ports and one write port.',
          code: `module register_file #(
  parameter DATA_WIDTH = 32,
  parameter ADDR_WIDTH = 5
)(
  input wire clk,
  input wire reset,
  input wire write_enable,
  input wire [ADDR_WIDTH-1:0] write_addr,
  input wire [DATA_WIDTH-1:0] write_data,
  input wire [ADDR_WIDTH-1:0] read_addr1,
  input wire [ADDR_WIDTH-1:0] read_addr2,
  output wire [DATA_WIDTH-1:0] read_data1,
  output wire [DATA_WIDTH-1:0] read_data2
);

  // Register file (R0 is hardwired to 0)
  reg [DATA_WIDTH-1:0] registers[0:(2**ADDR_WIDTH)-1];

  integer i;

  // Initialize registers
  initial begin
    for (i = 0; i < 2**ADDR_WIDTH; i = i + 1) begin
      registers[i] = 0;
    end
  end

  // Write operation (synchronous)
  always @(posedge clk) begin
    if (reset) begin
      for (i = 0; i < 2**ADDR_WIDTH; i = i + 1) begin
        registers[i] <= 0;
      end
    end else if (write_enable && write_addr != 0) begin
      // R0 is hardwired to 0, cannot be written
      registers[write_addr] <= write_data;
    end
  end

  // Read operations (asynchronous)
  assign read_data1 = (read_addr1 == 0) ? 0 : registers[read_addr1];
  assign read_data2 = (read_addr2 == 0) ? 0 : registers[read_addr2];

endmodule`
