  input wire [4:0] id_ex_rd,
  input wire id_ex_mem_read,
  input wire [4:0] if_id_rs1,
  input wire [4:0] if_id_rs2,
  output reg stall_pipeline
);

  // Detect load-use hazard
  // If a load instruction is in the EX stage and its destination register
  // is used by the instruction in the ID stage, we need to stall
  always @(*) begin
    if (id_ex_mem_read && 
