import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { 
  Box, Typography, Button, Grid, Card, CardContent, Breadcrumbs, 
  Tabs, Tab, List, ListItem, ListItemIcon, ListItemText, Divider, 
  Chip, Rating, Avatar, LinearProgress, Paper 
} from '@mui/material';
import { 
  PlayArrow, Assignment, MenuBook, Check, PlayCircleOutline, 
  BarChart, People, Schedule, AutoStories, ArrowBack, BookmarkAdd 
} from '@mui/icons-material';

const ModuleDetail = () => {
  const { id } = useParams();
  const [tabValue, setTabValue] = useState(0);
  const [module, setModule] = useState(null);
  const [loading, setLoading] = useState(true);

  // Fetch module data
  useEffect(() => {
    // Simulating API fetch
    setTimeout(() => {
      // This would normally be an API call
      const moduleData = {
        id: id,
        title: 'RISC-V Processor Design',
        description: 'Design a complete RISC-V processor from scratch with pipelining, hazard resolution, and advanced architectural features. Master the implementation of the RV32I instruction set and optimize your design for performance.',
        image: 'https://www.cnx-software.com/wp-content/uploads/2020/10/RISC-V-VegaFEmicro.jpg',
        level: 'Expert',
        duration: '10 weeks',
        prerequisites: [
          'Strong foundation in digital logic design',
          'Proficiency in Verilog HDL',
          'Understanding of combinational and sequential circuits',
          'Familiarity with basic computer architecture concepts',
          'Experience with FSM design and implementation'
        ],
        skills: [
          'RISC-V Architecture',
          'Processor Design',
          'Pipelining Techniques',
          'Memory Hierarchy',
          'Performance Optimization',
          'Hazard Resolution',
          'Branch Prediction',
          'Verilog HDL',
          'RTL Design',
          'Hardware Verification'
        ],
        rating: 4.9,
        studentsCount: 542,
        completed: 0,
        createdAt: '2023-04-15T00:00:00.000Z',
        updatedAt: '2024-02-22T00:00:00.000Z',
        instructor: {
          name: 'Dr. Brian Miller',
          title: 'Professor of Computer Engineering',
          bio: 'Dr. Miller has over 15 years of experience in computer architecture and VLSI design. He has worked at Intel and ARM before joining academia, where he leads research in efficient processor design for embedded systems.',
          avatar: 'https://randomuser.me/api/portraits/men/32.jpg'
        },
        overview: `<p>This comprehensive module takes you through the entire process of designing a RISC-V processor from basic concepts to a fully functional pipelined implementation. RISC-V is an open-source instruction set architecture (ISA) that has gained significant traction in both academia and industry due to its clean, modular design.</p>
        
        <p>You'll start by understanding the RISC-V instruction formats and addressing modes, then progressively build your implementation starting with a single-cycle datapath and control unit. As the module advances, you'll enhance your design with pipelining, hazard detection and resolution, branch prediction, and memory hierarchy considerations.</p>
        
        <p>Throughout the module, you'll implement your designs in Verilog HDL and verify them using comprehensive testbenches. The module combines theoretical concepts with practical implementation, ensuring you gain both deep understanding and valuable hands-on experience.</p>
        
        <p>By the end of this module, you'll have designed a fully functional RISC-V processor capable of executing the RV32I base integer instruction set, with optimization for performance and efficiency.</p>`,
        
        syllabus: [
          {
            week: 1,
            title: 'RISC-V Architecture Fundamentals',
            lessons: [
              {
                id: 1,
                title: 'Introduction to the RISC-V ISA',
                duration: '45 min',
                completed: true
              },
              {
                id: 2,
                title: 'RISC-V Instruction Formats and Encoding',
                duration: '55 min',
                completed: true
              },
              {
                id: 3,
                title: 'RV32I Base Integer Instruction Set',
                duration: '60 min',
                completed: false
              },
              {
                id: 4,
                title: 'Lab: Instruction Decoding in Verilog',
                duration: '90 min',
                completed: false
              }
            ]
          },
          {
            week: 2,
            title: 'Single-Cycle Processor Design',
            lessons: [
              {
                id: 5,
                title: 'Components of a RISC-V Processor',
                duration: '50 min',
                completed: false
              },
              {
                id: 6,
                title: 'Register File Implementation',
                duration: '45 min',
                completed: false
              },
              {
                id: 7,
                title: 'ALU Design for RISC-V Operations',
                duration: '60 min',
                completed: false
              },
              {
                id: 8,
                title: 'Lab: Building a Single-Cycle Datapath',
                duration: '120 min',
                completed: false
              }
            ]
          },
          {
            week: 3,
            title: 'Control Unit and Instruction Execution',
            lessons: [
              {
                id: 9,
                title: 'Control Signal Generation',
                duration: '50 min',
                completed: false
              },
              {
                id: 10,
                title: 'Implementing R-Type Instructions',
                duration: '60 min',
                completed: false
              },
              {
                id: 11,
                title: 'Implementing I-Type Instructions',
                duration: '60 min',
                completed: false
              },
              {
                id: 12,
                title: 'Lab: Control Unit Implementation',
                duration: '90 min',
                completed: false
              }
            ]
          },
          {
            week: 4,
            title: 'Memory Operations and Control Flow',
            lessons: [
              {
                id: 13,
                title: 'Memory Organization and Addressing',
                duration: '45 min',
                completed: false
              },
              {
                id: 14,
                title: 'Load and Store Operations',
                duration: '50 min',
                completed: false
              },
              {
                id: 15,
                title: 'Implementing Branches and Jumps',
                duration: '60 min',
                completed: false
              },
              {
                id: 16,
                title: 'Lab: Memory Interface and Control Flow',
                duration: '120 min',
                completed: false
              }
            ]
          },
          {
            week: 5,
            title: 'Introduction to Pipelining',
            lessons: [
              {
                id: 17,
                title: 'Pipelining Concepts and Benefits',
                duration: '45 min',
                completed: false
              },
              {
                id: 18,
                title: 'The Five-Stage RISC Pipeline',
                duration: '60 min',
                completed: false
              },
              {
                id: 19,
                title: 'Pipeline Registers and Datapath',
                duration: '50 min',
                completed: false
              },
              {
                id: 20,
                title: 'Lab: Basic Pipeline Implementation',
                duration: '120 min',
                completed: false
              }
            ]
          },
          {
            week: 6,
            title: 'Pipeline Hazards and Resolution',
            lessons: [
              {
                id: 21,
                title: 'Data Hazards and Forwarding',
                duration: '60 min',
                completed: false
              },
              {
                id: 22,
                title: 'Control Hazards',
                duration: '45 min',
                completed: false
              },
              {
                id: 23,
                title: 'Implementing Forwarding Units',
                duration: '60 min',
                completed: false
              },
              {
                id: 24,
                title: 'Lab: Hazard Detection and Resolution',
                duration: '120 min',
                completed: false
              }
            ]
          },
          {
            week: 7,
            title: 'Branch Prediction',
            lessons: [
              {
                id: 25,
                title: 'Static Branch Prediction',
                duration: '45 min',
                completed: false
              },
              {
                id: 26,
                title: 'Dynamic Branch Prediction',
                duration: '60 min',
                completed: false
              },
              {
                id: 27,
                title: 'Branch Target Buffer',
                duration: '50 min',
                completed: false
              },
              {
                id: 28,
                title: 'Lab: Implementing Branch Prediction',
                duration: '120 min',
                completed: false
              }
            ]
          },
          {
            week: 8,
            title: 'Memory Hierarchy and Cache',
            lessons: [
              {
                id: 29,
                title: 'Memory Hierarchy Overview',
                duration: '45 min',
                completed: false
              },
              {
                id: 30,
                title: 'Cache Organization and Operation',
                duration: '60 min',
                completed: false
              },
              {
                id: 31,
                title: 'Cache Controller Design',
                duration: '50 min',
                completed: false
              },
              {
                id: 32,
                title: 'Lab: Integrating Cache with Processor',
                duration: '120 min',
                completed: false
              }
            ]
          },
          {
            week: 9,
            title: 'Performance Optimization',
            lessons: [
              {
                id: 33,
                title: 'Performance Metrics and Analysis',
                duration: '50 min',
                completed: false
              },
              {
                id: 34,
                title: 'Optimizing the Critical Path',
                duration: '60 min',
                completed: false
              },
              {
                id: 35,
                title: 'Superscalar Concepts',
                duration: '45 min',
                completed: false
              },
              {
                id: 36,
                title: 'Lab: Performance Evaluation',
                duration: '90 min',
                completed: false
              }
            ]
          },
          {
            week: 10,
            title: 'Final Implementation and Extensions',
            lessons: [
              {
                id: 37,
                title: 'System Integration',
                duration: '50 min',
                completed: false
              },
              {
                id: 38,
                title: 'RISC-V Standard Extensions (M, F)',
                duration: '60 min',
                completed: false
              },
              {
                id: 39,
                title: 'Debugging Techniques',
                duration: '45 min',
                completed: false
              },
              {
                id: 40,
                title: 'Lab: Final Project - Complete Processor',
                duration: '180 min',
                completed: false
              }
            ]
          }
        ],
        
        students: [
          {
            id: 1,
            name: 'Alex Johnson',
            avatar: 'https://randomuser.me/api/portraits/men/41.jpg',
            comment: 'This module provided a thorough understanding of RISC-V processor design. The hands-on labs were challenging but incredibly rewarding.',
            rating: 5,
            date: '2023-12-15'
          },
          {
            id: 2,
            name: 'Sophia Chen',
            avatar: 'https://randomuser.me/api/portraits/women/33.jpg',
            comment: 'Dr. Miller explains complex concepts in an accessible way. The pipelining section was particularly well-structured and helped me understand hazard resolution thoroughly.',
            rating: 5,
            date: '2024-01-22'
          },
          {
            id: 3,
            name: 'Marcus Williams',
            avatar: 'https://randomuser.me/api/portraits/men/22.jpg',
            comment: 'Challenging but extremely educational. The progressive building of the processor from single-cycle to pipelined design made the learning journey logical and engaging.',
            rating: 4,
            date: '2023-11-05'
          }
        ],
        
        resources: [
          {
            title: 'The RISC-V Instruction Set Manual',
            description: 'Official specification of the RISC-V instruction set architecture (Volume 1: User-Level ISA).',
            type: 'Document',
            url: 'https://riscv.org/technical/specifications/'
          },
          {
            title: 'Digital Design and Computer Architecture: RISC-V Edition',
            description: 'Comprehensive textbook covering digital design principles and RISC-V implementation by Sarah Harris and David Harris.',
            type: 'Book',
            author: 'Harris & Harris'
          },
          {
            title: 'Processor Verification with SystemVerilog',
            description: 'Guidelines and examples for verifying your RISC-V processor design using SystemVerilog.',
            type: 'Tutorial',
            url: '#'
          },
          {
            title: 'Branch Prediction Techniques: Survey and Taxonomy',
            description: 'Research paper surveying various branch prediction techniques and their performance characteristics.',
            type: 'Paper',
            author: 'Chen et al., IEEE Transactions on Computers'
          },
          {
            title: 'Industrial Processor Design Case Studies',
            description: 'Collection of case studies from commercial processor designs, highlighting real-world design decisions and trade-offs.',
            type: 'Document',
            url: '#'
          }
        ],
        
        codeExamples: [
          {
            title: 'RISC-V Register File Implementation',
            description: 'A parameterized register file with two read ports and one write port.',
            code: `module register_file #(
  parameter DATA_WIDTH = 32,
  parameter ADDR_WIDTH = 5
)(
  input wire clk,
  input wire reset,
  input wire write_enable,
  input wire [ADDR_WIDTH-1:0] write_addr,
  input wire [DATA_WIDTH-1:0] write_data,
  input wire [ADDR_WIDTH-1:0] read_addr1,
  input wire [ADDR_WIDTH-1:0] read_addr2,
  output wire [DATA_WIDTH-1:0] read_data1,
  output wire [DATA_WIDTH-1:0] read_data2
);

  // Register file (R0 is hardwired to 0)
  reg [DATA_WIDTH-1:0] registers[0:(2**ADDR_WIDTH)-1];
  
  integer i;
  
  // Initialize registers
  initial begin
    for (i = 0; i < 2**ADDR_WIDTH; i = i + 1) begin
      registers[i] = 0;
    end
  end
  
  // Write operation (synchronous)
  always @(posedge clk) begin
    if (reset) begin
      for (i = 0; i < 2**ADDR_WIDTH; i = i + 1) begin
        registers[i] <= 0;
      end
    end else if (write_enable && write_addr != 0) begin
      // R0 is hardwired to 0, cannot be written
      registers[write_addr] <= write_data;
    end
  end
  
  // Read operations (asynchronous)
  assign read_data1 = (read_addr1 == 0) ? 0 : registers[read_addr1];
  assign read_data2 = (read_addr2 == 0) ? 0 : registers[read_addr2];

endmodule`
          },
          {
            title: 'RISC-V ALU Implementation',
            description: 'Arithmetic Logic Unit for the RISC-V processor supporting all RV32I arithmetic and logical operations.',
            code: `module riscv_alu #(
  parameter WIDTH = 32
)(
  input wire [WIDTH-1:0] operand_a,
  input wire [WIDTH-1:0] operand_b,
  input wire [3:0] alu_control,
  output reg [WIDTH-1:0] result,
  output wire zero_flag
);

  // ALU control signals
  localparam ALU_ADD  = 4'b0000;
  localparam ALU_SUB  = 4'b0001;
  localparam ALU_SLL  = 4'b0010; // Shift left logical
  localparam ALU_SLT  = 4'b0011; // Set less than
  localparam ALU_SLTU = 4'b0100; // Set less than unsigned
  localparam ALU_XOR  = 4'b0101;
  localparam ALU_SRL  = 4'b0110; // Shift right logical
  localparam ALU_SRA  = 4'b0111; // Shift right arithmetic
  localparam ALU_OR   = 4'b1000;
  localparam ALU_AND  = 4'b1001;

  // ALU operation
  always @(*) begin
    case (alu_control)
      ALU_ADD:  result = operand_a + operand_b;
      ALU_SUB:  result = operand_a - operand_b;
      ALU_SLL:  result = operand_a << operand_b[4:0];
      ALU_SLT:  result = ($signed(operand_a) < $signed(operand_b)) ? 1 : 0;
      ALU_SLTU: result = (operand_a < operand_b) ? 1 : 0;
      ALU_XOR:  result = operand_a ^ operand_b;
      ALU_SRL:  result = operand_a >> operand_b[4:0];
      ALU_SRA:  result = $signed(operand_a) >>> operand_b[4:0];
      ALU_OR:   result = operand_a | operand_b;
      ALU_AND:  result = operand_a & operand_b;
      default:  result = 0;
    endcase
  end

  // Zero flag generation
  assign zero_flag = (result == 0);

endmodule`
          },
          {
            title: 'Pipeline Hazard Detection Unit',
            description: 'Detects data hazards in the pipeline and controls stalling when necessary.',
            code: `module hazard_detection_unit(
  input wire [4:0] id_ex_rd,
  input wire id_ex_mem_read,
  input wire [4:0] if_id_rs1,
  input wire [4:0] if_id_rs2,
  output reg stall_pipeline
);

  // Detect load-use hazard
  // If a load instruction is in the EX stage and its destination register
  // is used by the instruction in the ID stage, we need to stall
  always @(*) begin
    if (id_ex_mem_read && 
        ((id_ex_rd == if_id_rs1) || (id_ex_rd == if_id_rs2)) &&
        (id_ex_rd != 0)) begin
      stall_pipeline = 1'b1;
    end else begin
      stall_pipeline = 1'b0;
    end
  end

endmodule`
          },
          {
            title: 'Forwarding Unit',
            description: 'Handles data forwarding to resolve data hazards in the pipeline.',
            code: `module forwarding_unit(
  input wire [4:0] ex_mem_rd,
  input wire ex_mem_reg_write,
  input wire [4:0] mem_wb_rd,
  input wire mem_wb_reg_write,
  input wire [4:0] id_ex_rs1,
  input wire [4:0] id_ex_rs2,
  output reg [1:0] forward_a,
  output reg [1:0] forward_b
);

  // Forward A logic
  always @(*) begin
    // Forward from EX/MEM pipeline register
    if (ex_mem_reg_write && (ex_mem_rd != 0) && (ex_mem_rd == id_ex_rs1)) begin
      forward_a = 2'b10;
    // Forward from MEM/WB pipeline register
    end else if (mem_wb_reg_write && (mem_wb_rd != 0) && 
                (mem_wb_rd == id_ex_rs1) && 
                !(ex_mem_reg_write && (ex_mem_rd != 0) && (ex_mem_rd == id_ex_rs1))) begin
      forward_a = 2'b01;
    // No forwarding
    end else begin
      forward_a = 2'b00;
    end
  end

  // Forward B logic
  always @(*) begin
    // Forward from EX/MEM pipeline register
    if (ex_mem_reg_write && (ex_mem_rd != 0) && (ex_mem_rd == id_ex_rs2)) begin
      forward_b = 2'b10;
    // Forward from MEM/WB pipeline register
    end else if (mem_wb_reg_write && (mem_wb_rd != 0) && 
                (mem_wb_rd == id_ex_rs2) && 
                !(ex_mem_reg_write && (ex_mem_rd != 0) && (ex_mem_rd == id_ex_rs2))) begin
      forward_b = 2'b01;
    // No forwarding
    end else begin
      forward_b = 2'b00;
    end
  end

endmodule`
          }
        ],
        
        faqs: [
          {
            question: 'What prior knowledge is required for this module?',
            answer: 'You should have a strong foundation in digital logic design, good understanding of Verilog HDL, and experience with combinational and sequential circuit design. Familiarity with computer architecture fundamentals is also recommended.'
          },
          {
            question: 'Will this module cover advanced RISC-V extensions like Vector instructions?',
            answer: 'This module focuses on the RV32I base integer instruction set. Advanced extensions are introduced conceptually but not implemented in the practical exercises. We offer a separate advanced module for Vector extension implementation.'
          },
          {
            question: 'What hardware/software tools are needed for this module?',
            answer: 'You will need a Verilog simulator (like ModelSim, VCS, or the open-source Icarus Verilog) for development and testing. For the FPGA implementation sections, you\'ll need Xilinx Vivado or Intel Quartus software and compatible FPGA hardware.'
          },
          {
            question: 'How much time should I allocate per week for this module?',
            answer: 'We recommend allocating at least 8-10 hours per week. Each week includes about 3-4 hours of instructional content and 4-6 hours for lab work and assignments.'
          },
          {
            question: 'Can I implement the processor on an actual FPGA?',
            answer: 'Yes, the final project includes instructions for implementing your processor on an FPGA. We provide guides for both Xilinx and Intel FPGAs. The final design is optimized to fit on mid-range FPGA development boards.'
          }
        ],
        
        relatedModules: [
          {
            id: 8,
            title: 'Memory Controller Design',
            level: 'Advanced',
            description: 'Learn to design efficient memory controllers for different memory technologies.'
          },
          {
            id: 14,
            title: 'Advanced SystemVerilog Features',
            level: 'Expert',
            description: 'Explore advanced language features and design patterns in SystemVerilog.'
          },
          {
            id: 18,
            title: 'Microprocessor Design',
            level: 'Expert',
            description: 'Design a complete RISC microprocessor from scratch.'
          }
        ]
      };
      
      setModule(moduleData);
      setLoading(false);
    }, 800);
  }, [id]);

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  if (loading) {
    return (
      <Box className="container page-container" sx={{ textAlign: 'center', py: 8 }}>
        <Typography variant="h5" sx={{ mb: 4 }}>Loading module content...</Typography>
