              startIcon={<PlayArrow />}
              component={Link}
              to={`/modules/${id}/lessons/1`}
              sx={{ height: '100%' }}
            >
              {module.completed > 0 ? 'Continue Learning' : 'Start Module'}
            </Button>
            <Button 
              variant="outlined" 
              color="primary"
              startIcon={<BookmarkAdd />}
