            {/* Additional Resources */}
            {module.resources && (
              <>
                <Typography variant="h5" gutterBottom sx={{ mt: 4, borderLeft: '4px solid #6a0dad', pl: 2 }}>Additional Resources</Typography>
                <Paper elevation={0} variant="outlined" sx={{ p: 3, borderRadius: 2, mb: 4 }}>
                  <List>
                    {module.resources.map((resource, index) => (
                      <React.Fragment key={index}>
                        <ListItem sx={{ px: 1 }}>
                          <ListItemIcon>
                            {resource.type === 'PDF' ? 
                              <MenuBook color="primary" /> : 
                              <Assignment color="primary" />
                            }
                          </ListItemIcon>
                          <ListItemText 
                            primary={resource.title} 
                            secondary={resource.type} 
                          />
                          <Button variant="outlined" size="small" component="a" href={resource.url}>
                            Download
                          </Button>
                        </ListItem>
                        {index < module.resources.length - 1 && <Divider component="li" />}
                      </React.Fragment>
                    ))}
                  </List>
                </Paper>
              </>
            )}

            {/* Related Modules */}
            {module.relatedModules.length > 0 && (
              <>
                <Typography variant="h5" gutterBottom sx={{ mt: 4, borderLeft: '4px solid #6a0dad', pl: 2 }}>Related Modules</Typography>
                <Grid container spacing={3}>
                  {module.relatedModules.map(relModule => (
                    <Grid item xs={12} sm={6} md={4} key={relModule.id}>
                      <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column', transition: 'transform 0.3s', '&:hover': { transform: 'translateY(-8px)' } }}>
                        <CardContent sx={{ flexGrow: 1 }}>
                          <Typography variant="h6" component="h3" gutterBottom>
                            {relModule.title}
                          </Typography>
                          <Chip 
                            label={relModule.level} 
                            size="small" 
                            sx={{ 
                              mb: 2,
                              backgroundColor: relModule.level === 'Beginner' ? '#e3f2fd' : 
                                            relModule.level === 'Intermediate' ? '#fff8e1' : '#fbe9e7',
                              color: relModule.level === 'Beginner' ? '#0277bd' : 
                                  relModule.level === 'Intermediate' ? '#ff8f00' : '#e64a19'
                            }} 
                          />
                          <Typography variant="body2" paragraph sx={{ mb: 2 }}>
                            {relModule.description}
                          </Typography>
                          <Button 
                            component={Link} 
                            to={`/modules/${relModule.id}`}
                            variant="outlined"
                            color="primary"
                            size="small"
                            fullWidth
                          >
                            View Module
                          </Button>
                        </CardContent>
                      </Card>
                    </Grid>
                  ))}
                </Grid>
              </>
            )}
          </Box>
        )}

        {/* Content Tab */}
        {tabValue === 1 && (
          <Box>
            <Typography variant="h5" gutterBottom sx={{ borderLeft: '4px solid #6a0dad', pl: 2 }}>Module Content</Typography>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 3, gap: 2 }}>
              <Typography variant="body1">
                This module contains <strong>{module.syllabus.length} lessons</strong> with a total duration of <strong>{module.duration}</strong>.
              </Typography>
              <Button 
                variant="contained" 
                color="primary" 
                startIcon={<PlayArrow />}
                component={Link}
                to={`/modules/${id}/lessons/1`}
                size="small"
              >
                Start Learning
              </Button>
            </Box>
            
            <Paper elevation={0} variant="outlined" sx={{ borderRadius: 2, overflow: 'hidden' }}>
              {module.syllabus.map((week, index) => (
                <Box 
                  key={week.week}
                  sx={{ 
                    borderBottom: index < module.syllabus.length - 1 ? '1px solid #e0e0e0' : 'none',
                  }}
                >
                  <Box 
                    sx={{ 
                      p: 2,
                      display: 'flex',
                      alignItems: 'flex-start',
                      cursor: 'pointer',
                      transition: 'background-color 0.2s',
                      '&:hover': {
                        backgroundColor: 'rgba(0, 0, 0, 0.02)'
                      },
                      bgcolor: week.completed ? 'rgba(76, 175, 80, 0.05)' : 'transparent'
                    }}
                  >
                    <Box sx={{ mr: 2, mt: 0.5 }}>
                      {week.completed ? (
                        <Box sx={{ 
                          width: 32, 
                          height: 32, 
                          borderRadius: '50%', 
                          bgcolor: 'success.main',
                          color: 'white',
                          display: 'flex',
                          alignItems: 'center',
                          justifyContent: 'center'
                        }}>
                          <Check fontSize="small" />
                        </Box>
                      ) : (
                        <Box sx={{ 
                          width: 32, 
                          height: 32, 
                          borderRadius: '50%', 
                          border: '2px solid #e0e0e0',
