                    </Box>
                    
                    <Box sx={{ flexGrow: 1 }}>
                      <Box sx={{ display: 'flex', alignItems: 'center', mb: 1, flexWrap: 'wrap' }}>
                        <Typography variant="h6" component="h3" sx={{ mr: 2 }}>
                          {week.title}
                        </Typography>
                        <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap' }}>
                          <Chip 
                            icon={week.type === 'video' ? <PlayCircleOutline fontSize="small" /> : <Assignment fontSize="small" />}
                            label={week.type} 
                            size="small"
                            sx={{ 
                              textTransform: 'capitalize',
                              bgcolor: week.type === 'video' ? '#e3f2fd' : '#fff8e1',
                              color: week.type === 'video' ? '#0277bd' : '#ff8f00',
                            }} 
                          />
                          <Chip 
                            label={week.duration} 
                            size="small"
                            sx={{ bgcolor: 'rgba(0, 0, 0, 0.05)' }} 
                          />
                        </Box>
                      </Box>
                      
                      <Typography variant="body2" color="text.secondary" paragraph>
                        {week.description}
                      </Typography>
                      
                      {week.lessons && (
                        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                          {week.lessons.map((lesson, lessonIndex) => (
                            <Chip 
                              key={lessonIndex}
                              label={lesson.title} 
                              size="small"
                              sx={{ 
                                fontSize: '0.7rem',
                                height: 24,
                                bgcolor: 'rgba(106, 13, 173, 0.08)'
                              }} 
                            />
                          ))}
                        </Box>
                      )}
                    </Box>
                    
                    <Button 
                      component={Link} 
                      to={`/modules/${id}/lessons/${week.week}`}
                      variant="outlined"
                      color="primary"
                      size="small"
                      sx={{ ml: 2, alignSelf: 'center', flexShrink: 0 }}
                    >
                      {week.completed ? 'Revisit' : 'Start'}
                    </Button>
                  </Box>
                </Box>
              ))}
            </Paper>
          </Box>
        )}

        {/* Exercises Tab */}
        {tabValue === 2 && (
          <Box>
            <Typography variant="h5" gutterBottom sx={{ borderLeft: '4px solid #6a0dad', pl: 2 }}>Practical Exercises</Typography>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
              <Typography variant="body1">
                Master your skills with these <strong>{module.exercises.length} hands-on exercises</strong>. Complete them to reinforce your learning.
              </Typography>
              <Chip 
                label={`${module.exercises.filter(ex => ex.completed).length}/${module.exercises.length} Completed`}
                color={module.exercises.filter(ex => ex.completed).length === module.exercises.length ? "success" : "default"}
                variant="outlined"
              />
            </Box>
            
            <Grid container spacing={3}>
              {module.exercises.map((exercise, index) => (
                <Grid item xs={12} md={6} key={exercise.id}>
                  <Paper 
                    elevation={0} 
                    variant="outlined" 
                    sx={{ 
                      p: 0, 
                      borderRadius: 2, 
                      overflow: 'hidden',
                      transition: 'transform 0.2s, box-shadow 0.2s',
                      '&:hover': {
                        transform: 'translateY(-5px)',
                        boxShadow: 3
                      },
                      height: '100%',
                      display: 'flex',
                      flexDirection: 'column'
                    }}
                  >
                    <Box sx={{ 
                      p: 2,
                      borderBottom: '1px solid #e0e0e0',
                      bgcolor: exercise.difficulty === 'Easy' ? '#e8f5e9' : 
                             exercise.difficulty === 'Medium' ? '#fff8e1' : '#ffebee',
                    }}>
                      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <Typography variant="h6" component="h3">
                          {exercise.title}
                        </Typography>
                        {exercise.completed && <Check color="success" />}
                      </Box>
                      <Box sx={{ display: 'flex', gap: 1, mt: 1 }}>
                        <Chip 
                          label={exercise.difficulty} 
                          size="small"
                          sx={{ 
                            bgcolor: 'white',
                            color: exercise.difficulty === 'Easy' ? '#2e7d32' : 
                                  exercise.difficulty === 'Medium' ? '#ff8f00' : '#d32f2f',
                            fontWeight: 'bold'
                          }} 
                        />
                        <Chip 
                          label={exercise.type} 
                          size="small"
                          sx={{ 
                            bgcolor: 'white',
                            textTransform: 'capitalize'
                          }} 
                        />
                        <Chip 
                          label={`${exercise.points} pts`}
                          size="small"
                          sx={{ bgcolor: 'white' }}
                        />
                      </Box>
                    </Box>
                    
                    <Box sx={{ p: 2, flexGrow: 1, display: 'flex', flexDirection: 'column' }}>
                      <Typography variant="body2" paragraph>
                        {exercise.description}
                      </Typography>
                      
                      <Box sx={{ 
                        display: 'flex', 
                        justifyContent: 'space-between', 
                        mt: 'auto', 
                        borderTop: '1px solid #e0e0e0',
                        pt: 2
                      }}>
                        <Typography variant="body2" color="text.secondary" sx={{ display: 'flex', alignItems: 'center' }}>
                          <Schedule fontSize="small" sx={{ mr: 0.5 }} />
                          Est. time: {exercise.estimatedTime}
                        </Typography>
                        
                        <Button 
                          component={Link} 
                          to={`/modules/${id}/exercises/${exercise.id}`}
                          variant="contained"
                          color="primary"
                          size="small"
                        >
                          {exercise.completed ? 'Review' : 'Start Exercise'}
                        </Button>
                      </Box>
                    </Box>
                  </Paper>
                </Grid>
              ))}
            </Grid>
          </Box>
        )}

        {/* Instructor Tab */}
        {tabValue === 3 && (
          <Box>
            <Paper elevation={0} variant="outlined" sx={{ p: 0, borderRadius: 2, overflow: 'hidden' }}>
              <Box sx={{ 
                p: 3, 
                bgcolor: 'rgba(106, 13, 173, 0.05)', 
                borderBottom: '1px solid #e0e0e0',
                display: 'flex',
                flexDirection: { xs: 'column', md: 'row' },
                alignItems: { xs: 'center', md: 'flex-start' },
                gap: 4
              }}>
                <Box sx={{ 
                  display: 'flex', 
                  flexDirection: 'column', 
                  alignItems: 'center',
                  width: { xs: '100%', md: '220px' } 
                }}>
                  <Avatar 
                    src={module.instructor.avatar} 
                    alt={module.instructor.name}
                    sx={{ 
                      width: 160, 
                      height: 160, 
                      mb: 2,
                      border: '4px solid white',
                      boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
                    }}
                  />
                  <Typography variant="h5" gutterBottom sx={{ textAlign: 'center' }}>
                    {module.instructor.name}
                  </Typography>
                  <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                    <Rating value={module.rating} precision={0.1} readOnly sx={{ mr: 1 }} />
                    <Typography variant="body2" color="text.secondary">
                      {module.rating}/5 ({module.studentsCount.toLocaleString()} students)
                    </Typography>
                  </Box>
                  <Button 
                    variant="outlined" 
                    color="primary"
                    fullWidth
                    sx={{ mb: 1 }}
                  >
                    View Profile
                  </Button>
                  <Button 
                    variant="text" 
                    color="primary"
                    fullWidth
                  >
                    All Courses
                  </Button>
                </Box>
                
                <Box sx={{ flexGrow: 1 }}>
                  <Typography variant="h5" gutterBottom>About the Instructor</Typography>
                  <Typography paragraph sx={{ fontSize: '1.05rem', lineHeight: 1.6, mb: 3 }}>
                    {module.instructor.bio}
                  </Typography>
                  
                  {module.instructor.credentials && (
                    <>
                      <Typography variant="h6" gutterBottom>Credentials</Typography>
                      <List disablePadding>
                        {module.instructor.credentials.map((credential, index) => (
                          <ListItem key={index} sx={{ px: 0, py: 0.5 }}>
                            <ListItemIcon sx={{ minWidth: 36 }}>
                              <Check sx={{ color: 'success.main' }} />
                            </ListItemIcon>
                            <ListItemText primary={credential} />
                          </ListItem>
                        ))}
                      </List>
                    </>
                  )}
                </Box>
              </Box>
              
              <Box sx={{ p: 3 }}>
                <Typography variant="h6" gutterBottom>Other Modules by This Instructor</Typography>
                <Grid container spacing={2}>
                  <Grid item xs={12} sm={6} md={4}>
                    <Paper variant="outlined" sx={{ p: 2, borderRadius: 2 }}>
                      <Typography variant="subtitle1" gutterBottom>Advanced Verilog Techniques</Typography>
                      <Typography variant="body2" color="text.secondary" paragraph>
                        Master advanced Verilog concepts for complex digital systems design.
                      </Typography>
                      <Button size="small" color="primary">View Module</Button>
                    </Paper>
                  </Grid>
                  <Grid item xs={12} sm={6} md={4}>
                    <Paper variant="outlined" sx={{ p: 2, borderRadius: 2 }}>
                      <Typography variant="subtitle1" gutterBottom>SystemVerilog for Verification</Typography>
                      <Typography variant="body2" color="text.secondary" paragraph>
                        Learn hardware verification using advanced SystemVerilog features.
                      </Typography>
                      <Button size="small" color="primary">View Module</Button>
                    </Paper>
                  </Grid>
                  <Grid item xs={12} sm={6} md={4}>
                    <Paper variant="outlined" sx={{ p: 2, borderRadius: 2 }}>
                      <Typography variant="subtitle1" gutterBottom>FPGA Design with Verilog</Typography>
                      <Typography variant="body2" color="text.secondary" paragraph>
                        Implement your Verilog designs on real FPGA hardware.
                      </Typography>
                      <Button size="small" color="primary">View Module</Button>
                    </Paper>
                  </Grid>
                </Grid>
              </Box>
            </Paper>
          </Box>
        )}
      </Box>
    </Box>
  );
};

export default ModuleDetail;
