import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { 
  Box, Typography, Grid, Card, CardContent, CardMedia, CardActionArea, 
  Chip, TextField, InputAdornment, FormControl, InputLabel, Select, MenuItem,
  Pagination, Divider
} from '@mui/material';
import { Search, FilterList } from '@mui/icons-material';

const Modules = () => {
  // State for filters and pagination
  const [searchQuery, setSearchQuery] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('all');
  const [levelFilter, setLevelFilter] = useState('all');
  const [page, setPage] = useState(1);
  
  // Sample modules data
  const modules = [
    {
      id: 1,
      title: 'Introduction to Verilog',
      description: 'Learn the basics of Verilog HDL and get started with digital design',
      image: 'https://via.placeholder.com/300x160?text=Verilog+Basics',
      level: 'Beginner',
      category: 'Fundamentals',
      lessons: 8,
      exercises: 5,
      rating: 4.8,
      students: 1250
    },
    {
      id: 2,
      title: 'Combinational Circuits',
      description: 'Design and implement combinational logic circuits using Verilog',
      image: 'https://via.placeholder.com/300x160?text=Combinational+Circuits',
      level: 'Intermediate',
      category: 'Digital Design',
      lessons: 12,
      exercises: 8,
      rating: 4.6,
      students: 982
    },
    {
      id: 3,
      title: 'Sequential Circuits',
      description: 'Learn about flip-flops, registers, and sequential logic design',
      image: 'https://via.placeholder.com/300x160?text=Sequential+Circuits',
      level: 'Intermediate',
      category: 'Digital Design',
      lessons: 10,
      exercises: 7,
      rating: 4.7,
      students: 856
    },
    {
      id: 4,
      title: 'Finite State Machines',
      description: 'Design and implement FSMs using Verilog for complex sequential circuits',
      image: 'https://via.placeholder.com/300x160?text=Finite+State+Machines',
      level: 'Advanced',
      category: 'Digital Design',
      lessons: 15,
      exercises: 10,
      rating: 4.9,
      students: 723
    },
    {
      id: 5,
      title: 'Testbench Development',
      description: 'Learn to create effective testbenches for verifying Verilog designs',
      image: 'https://via.placeholder.com/300x160?text=Testbench+Development',
      level: 'Intermediate',
      category: 'Verification',
      lessons: 8,
      exercises: 6,
      rating: 4.5,
      students: 689
    },
    {
      id: 6,
      title: 'Memory Modules',
      description: 'Implement different types of memory modules in Verilog',
      image: 'https://via.placeholder.com/300x160?text=Memory+Modules',
      level: 'Advanced',
      category: 'Digital Design',
      lessons: 9,
      exercises: 7,
      rating: 4.7,
      students: 542
    }
  ];

  // Filter modules based on search and filters
  const filteredModules = modules.filter(module => {
    const matchesSearch = module.title.toLowerCase().includes(searchQuery.toLowerCase()) || 
                         module.description.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = categoryFilter === 'all' || module.category === categoryFilter;
    const matchesLevel = levelFilter === 'all' || module.level === levelFilter;
    
    return matchesSearch && matchesCategory && matchesLevel;
  });

  // Get unique categories and levels for filter options
  const categories = [...new Set(modules.map(module => module.category))];
  const levels = [...new Set(modules.map(module => module.level))];

  // Pagination logic
  const modulesPerPage = 6;
  const pagesCount = Math.ceil(filteredModules.length / modulesPerPage);
  const displayedModules = filteredModules.slice(
    (page - 1) * modulesPerPage,
    page * modulesPerPage
  );

  return (
    <Box className="container page-container">
      <Typography variant="h3" component="h1" gutterBottom color="primary">
        Verilog Modules
      </Typography>
      <Typography variant="subtitle1" paragraph sx={{ mb: 4 }}>
        Browse our comprehensive collection of Verilog modules designed to take you from beginner to expert
      </Typography>

      {/* Search and Filters */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} md={6}>
          <TextField
            fullWidth
            label="Search Modules"
            variant="outlined"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <Search />
                </InputAdornment>
              ),
            }}
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <FormControl fullWidth variant="outlined">
            <InputLabel id="category-filter-label">Category</InputLabel>
            <Select
              labelId="category-filter-label"
              id="category-filter"
              value={categoryFilter}
              onChange={(e) => setCategoryFilter(e.target.value)}
              label="Category"
              startAdornment={
                <InputAdornment position="start">
                  <FilterList />
                </InputAdornment>
              }
            >
              <MenuItem value="all">All Categories</MenuItem>
              {categories.map(category => (
                <MenuItem key={category} value={category}>{category}</MenuItem>
              ))}
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <FormControl fullWidth variant="outlined">
            <InputLabel id="level-filter-label">Level</InputLabel>
            <Select
              labelId="level-filter-label"
              id="level-filter"
              value={levelFilter}
              onChange={(e) => setLevelFilter(e.target.value)}
              label="Level"
              startAdornment={
                <InputAdornment position="start">
                  <FilterList />
                </InputAdornment>
              }
            >
              <MenuItem value="all">All Levels</MenuItem>
              {levels.map(level => (
                <MenuItem key={level} value={level}>{level}</MenuItem>
              ))}
            </Select>
          </FormControl>
        </Grid>
      </Grid>

      {/* Results info */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="body2" color="text.secondary">
          Showing {displayedModules.length} of {filteredModules.length} modules
        </Typography>
      </Box>

      {/* Modules Grid */}
      {displayedModules.length > 0 ? (
        <Grid container spacing={4} sx={{ mb: 4 }}>
          {displayedModules.map(module => (
            <Grid item xs={12} sm={6} md={4} key={module.id}>
              <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                <CardActionArea component={Link} to={`/modules/${module.id}`}>
                  <CardMedia
                    component="img"
                    height="160"
                    image={module.image}
                    alt={module.title}
                  />
                  <CardContent sx={{ flexGrow: 1 }}>
                    <Typography variant="h6" component="h3" gutterBottom noWrap>
                      {module.title}
                    </Typography>
                    <Box sx={{ display: 'flex', gap: 1, mb: 1 }}>
                      <Chip 
                        label={module.level} 
                        size="small" 
                        sx={{ 
                          backgroundColor: module.level === 'Beginner' ? '#e3f2fd' : 
                                          module.level === 'Intermediate' ? '#fff8e1' : '#fbe9e7',
                          borderRadius: '4px'
                        }} 
                      />
                      <Chip 
                        label={module.category} 
                        size="small" 
                        sx={{ backgroundColor: '#f1f8e9', borderRadius: '4px' }} 
                      />
                    </Box>
                    <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                      {module.description}
                    </Typography>
                    <Divider sx={{ my: 1 }} />
                    <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 1 }}>
                      <Typography variant="body2" color="text.secondary">
                        ‚≠ê {module.rating} ({module.students})
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {module.lessons} lessons
                      </Typography>
                    </Box>
                  </CardContent>
                </CardActionArea>
              </Card>
            </Grid>
          ))}
        </Grid>
      ) : (
        <Box sx={{ textAlign: 'center', my: 8 }}>
          <Typography variant="h6">
            No modules found matching your criteria
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Try adjusting your search or filters
          </Typography>
        </Box>
      )}

      {/* Pagination */}
      {pagesCount > 1 && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
          <Pagination 
            count={pagesCount} 
            page={page} 
            onChange={(event, value) => setPage(value)}
            color="primary"
            size="large"
            showFirstButton
            showLastButton
          />
        </Box>
      )}
    </Box>
  );
};

export default Modules;
